css:
@-moz-document url-prefix("https://ethermine.org") {
::-webkit-scrollbar-thumb{background:#221f1b!important;border-radius:0!important}
::-webkit-scrollbar-thumb:hover{background:#1B1713!important}
::-webkit-scrollbar-corner,::-webkit-scrollbar-track{background:#13100D!important}
:not(div)::-webkit-scrollbar{width:10px!important;height:10px!important}
:not(div)::-webkit-scrollbar-thumb{background:#333!important}
:not(div)::-webkit-scrollbar-thumb:hover{background:#444!important}
:not(div)::-webkit-scrollbar-corner,:not(div)::-webkit-scrollbar-track{background:#090909!important}
:not(div)::-webkit-scrollbar-track-piece{background:#171717!important}

:not(div).resize-grip {background:linear-gradient(-45deg,transparent 2px,rgba(255,255,255,0.75) 2px, transparent 3px,transparent 4.5px,rgba(255,255,255,0.63) 5px,transparent 5.5px);background-color:#222;width:10px;height:10px}

.card-deck.mt-4.text-center{margin-left:10px!important;margin-right:10px!important}
.close:not(:disabled):not(.disabled){position:relative;left:-12px;z-index:1}

body{background-color:#13110f}
h2.miner-header.text-center,h3.text-center.charts-title,h3.m-auto{font-size:1.55rem;color:#695f52}
.navbar{padding: .25rem 1rem 0}
#navbar li > a:not(.selected){border-bottom: 4px solid transparent}
.panel{background-color:#221f1b}
table{background-color:#161e25}
.table-bordered{border:1px solid #3a3020}
.table-bordered td,.table-bordered th{border:1px solid #2b2723!important}
.table-responsive .bg-danger {background-color:#501c1b!important}
.table thead th{border-bottom:1px solid #312d26}
.table td,.table th{border-top:1px solid #312d26}
.text-right[data-v-70766c76]{border-right:1px solid #312d26}
.form-control,input{color:#f9f9f9!important;background-color:#1d1d1d;transition:background-color .5s;border:none}
.form-control:focus,input:focus{background-color:#201f1c;border-color:#2f2c2a;box-shadow:0 0 0 1px #5f554e40;}
.bg-dark {background-color: #1d1d1d!important}
.btn-outline-default {color: #615342;border-color:#4a4033}
.btn-outline-default.focus,.btn-outline-default:focus{box-shadow: 0 0 0 1px #4a4033}
.btn-outline-default:hover,.btn-outline-default:not([disabled]):not(.disabled).active,.btn-outline-default:not([disabled]):not(.disabled):active,.show>.btn-outline-default.dropdown-toggle{background-color: #73695c;border-color: #312d26}

.btn-light,.btn-primary,.btn-secondary{background-color:#2b2723;border-color:#3d3229}
.btn-light:hover,.btn-primary:hover,.btn-secondary:hover{background-color:#4a4033;border-color:#73695c}
.btn-primary.focus,.btn-primary:focus {box-shadow:0 0 0 1px #4E2A11}

#apiTab .active, #myTab .router-link-active {background-color: #312d26;border: 1px solid #4a4033;border-top-left-radius:4px;border-top-right-radius:4px}
.nav-tabs{border-bottom:1px solid #4a4033}
.nav-tabs .nav-item{line-height:.5em}
.nav-tabs .nav-link:focus,.nav-tabs .nav-link:hover{border-color:#4a4033}

.card-body {padding:0.75rem 1.25rem;min-height:66px}
.card{background-color:#1d1d1d;border-radius:4px}
.progress{background-color:#4a4033}
.border-info {border-color: #73695c!important}
.bg-info {background-color: #73695c!important}

.bg-primary{background-color:#312d26!important;color:#d0c4b3!important}
.border-primary{border-color:#312d26!important}

.border-success{border-color:#35540c!important}
.bg-success{background:#3b501f!important;color:#c2d4ab}

.border-danger{border-color: #773a39!important}
.bg-danger{background-color: #5f2a28!important;color:#e4c9c8}

.bg-warning{background-color:#826031!important}
.border-warning{border-color:#8c5b16!important;color:#dde4c8}

.card.border-default{border:1px solid #4e4e4e}

.card-header {padding:1px 0;border-bottom: 0}
.card-title {margin-bottom: 0}
.card-text.text-center{color:#e5d9c6;transition:color .5s}
.card-body:hover .card-text.text-center{color:#f4f1eb}

.card.border-info .card-text.text-center[style="text-decoration: underline dotted;"]{text-decoration:none!important}
.card.border-info .card-text.text-center span{border-bottom:2px solid #4a4033;padding-bottom:1px;color:#e5d9c6;transition:color .5s, border-color .5s}
.card.border-info:hover .card-text.text-center span{border-bottom:2px solid #625443;color:#ece6dc}


.chart-dashboard[data-v-3331255e]{margin-bottom:-2.5rem}
#miner-hashrate-chart,#miner-shares-chart{top:-24px;position:relative}
.btn.btn-primary.mx-5{margin-bottom:24px;top:-4px;position:relative}
.row.mt-4.mb-2 {margin-top: 1rem!important}

g[transform^="translate"] g[style="pointer-events: none;"] rect,g[transform^="translate"] g[style="pointer-events: none;"] path,a[href="http://www.amcharts.com"]{display:none!important}
.col-lg-9 {max-width:100%;flex:0 0 100%}

.modal-content {background-color:#211d1c}
.modal-footer, .modal-header {background-color:#191615}

.amcharts-balloon-div th{background-color:#221f1b;color:#999}
.amcharts-balloon-div td{background-color:#191919aa;border-left:1px dotted #313131;color:#FFF;padding:2px 10px}
.amcharts-balloon-div tr{border:1px solid #444;color:#FFF}
.amcharts-balloon-div > div{max-width:300px!important}
.amcharts-balloon-div > div > table {font-size:14px!important}
#miner-hashrate-chart > div > .amcharts-chart-div > svg > g:nth-child(3) > g > g path[cs="100,100"],#miner-hashrate-chart > div > div.amcharts-chart-div > svg > g:nth-child(10) > g:nth-child(1) > path,#miner-hashrate-chart > div > div.amcharts-chart-div > svg > g:nth-child(10) > g:nth-child(2) > path,
#miner-shares-chart > div > div.amcharts-chart-div > svg > g:nth-child(3) > g > g > path[cs="100,100"],#miner-shares-chart > div > div.amcharts-chart-div > svg > g:nth-child(10) > path,#miner-shares-chart > div > div.amcharts-chart-div > svg > g:nth-child(10) > g:nth-child(3) > path{stroke:#444}

#miner-shares-chart{width:100%!important}

#miner-hashrate-chart > div > div.amcharts-chart-div > svg > g:nth-child(11) > g > path:nth-child(1)[cs="100,100"]{stroke:#777!important}
#miner-payouts-chart > div > div.amcharts-chart-div > div > div,#miner-rounds-chart > div > div.amcharts-chart-div > div > div{color:#fff!important}
#miner-payouts-chart > div > div.amcharts-chart-div > svg > g:nth-child(8) > g > g:nth-child(2) > path:nth-child(1){fill:#f60!important}
#miner-payouts-chart > div > div.amcharts-chart-div > svg > g:nth-child(8) > g > g:nth-child(2) > path:nth-child(2){stroke:#f60!important}
#miner-payouts-chart > div > div.amcharts-chart-div > svg > g:nth-child(13) > g > circle,#miner-payouts-chart > div > div.amChartsLegend.amcharts-legend-div > svg > g > g > g > path,#miner-rounds-chart > div > div.amChartsLegend.amcharts-legend-div > svg > g > g > g > path{stroke:#f60!important;fill:#f60!important}
#miner-rounds-chart > div > div.amcharts-chart-div > svg > g:nth-child(8) > g > g > g > path{stroke:#994005!important;fill:#ec6408!important}
#miner-rounds-chart > div > div.amChartsLegend.amcharts-legend-div > svg > g > g > g > path[fill="#AAB3B3"]{stroke:#403127!important;fill:#1B1917!important}


/* tweets menu */
.twitter-column{background:#13110f;margin-left:0px;position:absolute;top:46px;right:-324px;width:420px;max-width:420px;overflow:hidden;max-height:37px;opacity:.15;transition:max-height .7s,right .7s,opacity .7s .5s}
.twitter-column:hover{box-shadow:0px 1px 1px 0px #312d26;max-height:640px;height:90%;overflow-y:scroll;opacity:.95;right:0px;transition:max-height .7s,right .7s,opacity 1s}
body{overflow-x:hidden}

}
