css:
@-moz-document domain(app.hubspot.com) {
	

/* Developed for Safari using Cascadea for Mac. */
#hs-nav-v4 #navbar {
width: 100%;
border-top: 3px solid #000000;
height: 60px;
background-color: #1d0e1f!important;}
#hs-nav-v4 #navbar .primary-links>li.currentPage>a {
background-color: #3d2242;
}
#hs-nav-v4 #navbar .primary-links>li>a:hover {
background-color: #7e3e83;
}
.private-button--primary {
background-color: #8f3a8d;
border-color: #b85bb9;
color: #fff;}

.private-button--primary:hover {
background-color: #9f419d;
    border-color: #b85bb9;
}

.private-button--secondary:hover {
background-color: #fff8f6;
color: #000;
}
.private-button--secondary {
background-color: #282828;
border-color: #9f419d;
color: #9f419d;}
#hs-nav-v4 #navbar .navSearch-input {
background-color: #50244c;}
#hs-nav-v4 #navbar .navSearch-input:hover {
background-color: #50244c;}
#hs-nav-v4 #navbar .navSearch.active .navSearch-input {
width: 360px;
background-color: #3f2542;}
 #hs-nav-v4 #navbar .settings>a:hover {
background-color: #7e3e83;
}
#hs-nav-v4 #navbar #account-menu-container>a:hover {background-color: #7e3e83;}
.private-link:hover {
color: #8c2c7f;
text-decoration: underline;
}
.private-link {
color: #8c2c7f;
}
#hs-nav-v4~.app {
background-color: rgb(39, 41, 45);
}
.private-nav--expandable .private-nav-item:hover {
background-color: #0f1010;
}
.private-nav-item.private-nav-item--active {
background-color: #0f1010;}
body {color: #c0bfc5;}
.board-column {
background: #222323;
}
.board-column-item-column {
border: 1px solid #363739;
border-right: none;
}
.board-container {
border-left: 1px solid #363739;
}
.board-column .board-total {
background: #181718;
border-top: 1px solid #606265;
}
.private-card {
border-radius: .1875rem;
background-color: #161616;}
.private-button--tertiary-light {
background-color: #28292a;
border-color: #484b50;
color: #b9c0ca;
}
.private-button--tertiary-light.private-button--active {
background-color: #202123;
border-color: #646577;
color: #bfc6e7;
}
.draft-extend-editor {
background: #161616;}
.private-form__set--floating .private-form__control__overlay {
background: #000;
    box-shadow: -.5rem 0 .375rem -.25rem #000;
}
.private-expandable-text__fade-out {
background: linear-gradient(hsla(0, 0%, 15%, 0), #131313 80.25%);}
.private-form__label--floating {
color: #b4b6d2;}
.private-template {
background-color: #161616;}
.private-template__section--divided {
border-top: 1px solid #cbd6e2;
background-color: #1c1c1c!important;
}
.forecast-bar-chart .forecast-bar .forecast-quota {
background-color: #1b1b1b;
}
.forecast-bar-chart .forecast-bar .forecast-stage .forecast-stage-amount span {
background-color: #161616;
color: #c3bdcc;}
.private-scroll__wrapper--tables {
background-color: #1e1e1e;
}
.iMAEt {background-image:none!important;}
.funnel-bar-contents .funnel-bar .funnel-bar-stage {
background: #222222;
}
.funnel-bar-contents .funnel-bar .funnel-bar-percent {
position: relative;
background: #222222;}
.private-tile {
border-radius: .1875rem;
background-color: #050505;
border: 1px solid #737579;}

}
