/* ==UserStyle==
@name           Yammer Dark
@namespace      FizzBuzz791
@version        0.1.0
@description    A dark theme for Yammer, heavily based on github.com/StylishThemes/Github-Dark
@author         FizzBuzz791
@license        GPL-3.0-only
@homepageURL    https://github.com/FizzBuzz791/Yammer-Dark
@updateURL      https://raw.githubusercontent.com/FizzBuzz791/Yammer-Dark/master/yammer-dark.user.css
@preprocessor   less
==/UserStyle== */
@-moz-document domain("yammer.com") {
  /* rgb(192, 192, 192) */
  /* rgb(24, 24, 24) */
  /* rgb(36, 36, 36) */
  /* rgb(32, 32, 32) */
  /* rgb(56, 56, 56) */
  /* rgb(56, 108, 187) */
  body {
    background-image: url("data:image/png;base64,");
    background-repeat: repeat;
  }
  .yj-nav-menu .yj-global-sidebar--fixed-content,
  .column-container,
  .main-content,
  #column-two,
  .y-navigationLink__unstyled,
  .y-navigationLink__unstyled:hover,
  .yj-global-sidebar--item--link {
    background-color: transparent;
    color: #c0c0c0;
  }
  #column-one.main-nav {
    background-color: #181818;
  }
  #search {
    background-color: #202020;
  }
  .yj-global-sidebar--nav-list-item-navigation-link:hover,
  .yj-global-sidebar--action-list-item-clickable:hover,
  .yj-global-sidebar--nav-list-item--selected.yj-global-sidebar--nav-list-item-navigation-link,
  .yj-global-sidebar--nav-list-item--selected.yj-global-sidebar--nav-list-item-navigation-link:hover,
  .yj-global-sidebar--nav-list-item--selected.yj-global-sidebar--nav-list-item-navigation-link:active,
  .yj-global-sidebar--nav-list-item--selected.yj-global-sidebar--nav-list-item-navigation-link:focus,
  .yj-nav-menu--primary-actions-item.is-active .yj-nav-menu--primary-actions-link,
  .yj-nav-menu--primary-actions-home .yj-nav-menu--primary-actions-link:hover,
  .yj-nav-menu--primary-actions-inbox .yj-nav-menu--primary-actions-link:hover,
  .yj-nav-menu--primary-actions-notifications .yj-nav-menu--primary-actions-link:hover,
  .yj-global-sidebar--item--link:hover,
  .yj-global-sidebar--item--link:hover .yj-global-sidebar--item--text-wrapper {
    background-color: #242424;
    color: #c0c0c0;
  }
  .y-icon {
    color: #c0c0c0;
  }
  .yj-nav-menu--primary-actions-item,
  .yj-nav-menu--primary-actions-item.is-active {
    border-color: #383838;
  }
  .yj-selected .yj-global-sidebar--item--link {
    background-color: #242424;
    color: #c0c0c0;
  }
  .yj-selected .yj-global-sidebar--item--link:hover {
    background-color: #242424;
  }
  .yj-selected .yj-global-sidebar--item--link:hover .yj-global-sidebar--item--text-wrapper {
    color: #c0c0c0;
  }
  .page-header {
    border-bottom-color: #383838;
  }
  .page-header .header-content,
  .page-header .yj-inbox-header--tab-link,
  .page-header .yj-inbox-header--search-link,
  .page-header .yj-feed-name {
    color: #c0c0c0;
  }
  /* 
  		END: General content & left sidebar 
  		###################################
  	   	START: Main content 
  	*/
  .unstyle-button-as-link.publisher-placeholder {
    background-color: #202020;
    border-color: #383838;
    color: #666666;
  }
  .yj-global-publisher-switcher .option-arrow::before,
  .yj-global-publisher-switcher .option-arrow::after {
    border-color: #383838 transparent;
  }
  .switcher-option .unstyle-button-as-span,
  .yj-tabs .yj-filter-tab-link,
  .yj-message-list-item--body-byline-user-link .yj-hovercard-link,
  .yj-message-list-item--inline-attributes span,
  .yj-message-list-item--inline-attributes a,
  .yj-message-list-item--in-reply-to,
  .yj-message-list-item--in-reply-to-user,
  .yj-likes-text,
  .y-text,
  .yj-message-list-item--action-list-icon--alt,
  .yj-media-card--location-name,
  .yj-media-card-menu--trigger,
  .yj-icon-banner--header,
  .yj-thread-share-sender-name .yj-hovercard-link {
    color: #c0c0c0;
  }
  .yj-group-unread-header--unread-header {
    color: #c0c0c0;
    border-bottom-color: #383838;
  }
  .switcher-option.yj-selected .unstyle-button-as-span {
    color: #386cbb;
  }
  .yj-thread-replies-link--container,
  .yj-thread-reply-list-item,
  .yj-global-reply-publisher-with-avatar,
  .yj-media-card--content,
  .yj-media-card--footer,
  .yj-recipient-list-item {
    background-color: #181818;
    border-color: #383838;
  }
  .yj-btn-alt {
    background-color: #181818;
    border-color: #383838;
  }
  .yj-btn-alt:hover {
    background-color: #242424;
    border-color: #383838;
  }
  .yj-new-messages-notice--button {
    background-color: #181818;
    border-color: #383838;
  }
  .yj-new-messages-notice--button:hover {
    background-color: #242424;
    border-color: #383838;
  }
  .yj-message-body--byline-follow,
  .yj-groupcallout-join-link--link {
    background-color: #181818;
    border-color: #383838;
    color: #c0c0c0;
  }
  .yj-message-body--byline-follow:hover,
  .yj-groupcallout-join-link--link:hover,
  .yj-message-body--byline-follow:active,
  .yj-groupcallout-join-link--link:active,
  .yj-message-body--byline-follow:focus,
  .yj-groupcallout-join-link--link:focus {
    background-color: #242424;
    border-color: #383838;
  }
  .DraftEditor-editorContainer,
  .DraftEditor-editorContainer span,
  .yj-publisher-toolbar,
  .yj-autogrow-textarea {
    background-color: #202020;
    color: #c0c0c0;
  }
  .yj-global-publisher .publisher-main,
  .yj-global-publisher .publisher-recipients,
  .yj-global-publisher input,
  .yj-global-publisher .publisher-body {
    background-color: #202020;
    border-color: #383838;
    color: #c0c0c0;
  }
  .yj-global-publisher .publisher-main ::placeholder,
  .yj-global-publisher .publisher-recipients ::placeholder,
  .yj-global-publisher input ::placeholder,
  .yj-global-publisher .publisher-body ::placeholder {
    color: #c0c0c0;
  }
  .yj-global-publisher .yj-global-topic-menu.yj-open {
    background-color: #202020;
  }
  .yj-global-publisher .yj-global-topic-menu.yj-open .yj-global-tag-selector.yj-global-topic-selector {
    border-color: #383838;
  }
  .yj-card,
  .yj-card--thumbnail {
    border-color: #383838;
  }
  .yj-metabar,
  .yj-metabar .yj-hovercard-link,
  .yj-metabar .yj-message-others {
    color: #666666;
  }
  .yj-thread-list-item--shares-topics-container {
    background-color: #181818;
    border-bottom-color: #383838;
  }
  .yj-thread-list-item .yj-thread-share-excerpt {
    background-color: #242424;
    border-color: #383838;
    color: #c0c0c0;
  }
  .yj-groupcallout-join-link--link {
    background-color: #181818;
    border-color: #383838;
    color: #c0c0c0;
  }
  .yj-normal-text-input,
  .poll-question .yj-autogrow-textarea textarea,
  .yj-global-poll-answer .yj-normal-text-input {
    color: #c0c0c0;
  }
  .yj-ta-option {
    background-color: #181818;
  }
  .yj-ta-option .yj-ta-name {
    color: #c0c0c0;
  }
  .yj-ta-option .yj-ta-byline {
    color: #666666;
  }
  .yj-ta-results.yj-menu {
    background-color: #181818;
  }
  .yj-ta-results .yj-ta-additional {
    background-color: #181818;
  }
  .yj-ta .yj-selected {
    background-color: #242424;
  }
  .yj-ta .yj-ta-hint {
    background-color: #202020;
    border-top-color: #383838;
    color: #666666;
  }
  .yj-ta-withheaders .yj-ta-option {
    border-left-color: #383838;
  }
  .yj-tabs {
    border-bottom-color: #383838;
  }
  .yj-message-body--praise-poll {
    background-color: #181818;
  }
  .yj-message-body--praise-poll .yj-praise-attachment {
    border-color: #383838;
  }
  .yj-message-body--praise-poll .yj-praise-attachment span {
    color: #386cbb;
  }
  .yj-message-body--praise-poll .yj-praise-attachment .yj-praise-attachment--comment {
    color: #c0c0c0;
  }
  /* 
  		END: Main content
  		###################################
  	   	START: Right sidebar 
  	*/
  #sidebar h2,
  #group-details h2,
  #sidebar p,
  #group-details p,
  #sidebar .yj-pinned-content--empty-text,
  #group-details .yj-pinned-content--empty-text {
    color: #c0c0c0;
    border-bottom-color: #383838;
  }
  #sidebar h2 a,
  #group-details h2 a,
  #sidebar p a,
  #group-details p a,
  #sidebar .yj-pinned-content--empty-text a,
  #group-details .yj-pinned-content--empty-text a {
    color: #c0c0c0;
  }
  #sidebar h2.yj-user-getting-started--header,
  #group-details h2.yj-user-getting-started--header,
  #sidebar p.yj-user-getting-started--header,
  #group-details p.yj-user-getting-started--header,
  #sidebar .yj-pinned-content--empty-text.yj-user-getting-started--header,
  #group-details .yj-pinned-content--empty-text.yj-user-getting-started--header {
    color: #181818;
  }
  #sidebar input,
  #group-details input {
    background-color: #202020;
    border-color: #383838;
  }
  #sidebar .yj-group-options--settings-option-label,
  #group-details .yj-group-options--settings-option-label,
  #sidebar .yj-group-options--settings-option-link,
  #group-details .yj-group-options--settings-option-link {
    color: #386cbb;
  }
  #sidebar .yj-suggestion-link,
  #group-details .yj-suggestion-link,
  #sidebar .position,
  #group-details .position,
  #sidebar .yamicon,
  #group-details .yamicon {
    color: #c0c0c0;
  }
  .yj-header-extra-link {
    border-bottom-color: #383838;
  }
  .yj-user-getting-started {
    background-color: #4183c4;
  }
  .yj-user-getting-started .yj-user-getting-started--pm-label {
    color: #181818;
  }
  .yj-user-getting-started .yj-user-getting-started--link {
    color: #ffffff;
  }
  .ms-Button {
    background-color: #181818;
    border-color: #383838;
    color: #c0c0c0;
  }
  .ms-Button:hover {
    background-color: #242424;
    border-color: #383838;
  }
  /*
  		END: Right sidebar
  		###################################
  	   	START: Inbox
  	*/
  .yj-inbox-list-item {
    background-color: #242424;
    border-color: #383838;
  }
  .yj-inbox-list-item:hover {
    background-color: #383838;
    border-color: #666666;
  }
  .yj-inbox-list-item.yj-read-item {
    background-color: #181818;
    border-color: #383838;
  }
  .yj-inbox-list-item.yj-read-item:hover {
    background-color: #242424;
    border-color: #383838;
  }
  .yj-inbox-list-item .yj-inbox-read-state-button {
    border-color: #383838;
  }
  .yj-inbox-list-item .yj-inbox-read-state-button button {
    background-color: #181818;
  }
  .yj-inbox-list-item .yj-inbox-list-item--message-header,
  .yj-inbox-list-item .yj-inbox-list-item--message-sender-user,
  .yj-inbox-list-item .yj-inbox-list-item--message-text,
  .yj-inbox-list-item .yj-reply-blurb-text {
    color: #c0c0c0;
  }
  .yj-conversation-participants--header {
    color: #c0c0c0;
    border-bottom-color: #383838;
  }
  .yj-conversation-participants input {
    background-color: #202020;
    border-color: #383838;
  }
  .yj-conversation-participants input::placeholder {
    color: #666666;
  }
  .yj-search-controls--input-container {
    border-color: #383838;
  }
  .yj-search-controls--input-container input {
    background-color: #202020;
  }
  .yj-search-controls--input-container input::placeholder {
    color: #666666;
  }
  .yj-simple-lightbox--content {
    background-color: #181818;
    color: #c0c0c0;
    /* This only affects the 'X' */
  
  }
  .yj-simple-lightbox--content #yj-simple-lightbox--title {
    color: #c0c0c0;
  }
  .yj-global-split-publisher .yj-global-publisher-switcher .switcher-option {
    background-color: #181818;
    border-color: #383838;
  }
  .yj-global-split-publisher .yj-global-publisher-switcher .switcher-option.yj-selected {
    background-color: #242424;
    border-color: #383838;
  }
  .yj-global-split-publisher .yj-global-publisher-switcher .switcher-option.yj-selected .option-arrow::before,
  .yj-global-split-publisher .yj-global-publisher-switcher .switcher-option.yj-selected .option-arrow::after {
    border-color: #383838 transparent;
  }
  .yj-global-split-publisher .yj-global-publisher-switcher .switcher-option .option-label {
    color: #c0c0c0;
  }
  .yj-global-split-publisher .yj-global-publisher-switcher .switcher-option:first-child {
    border-color: #383838;
  }
  .yj-global-split-publisher .yj-publisher-lightbox--group,
  .yj-global-split-publisher .yj-publisher-lightbox--private {
    border-color: #383838;
  }
  .yj-global-split-publisher .publisher-recipients {
    border-color: #383838 !important;
    /* This exact selector is used by Yammer CSS with !important, so there's no way to avoid this. */
  
  }
  .yj-global-split-publisher .publisher-main {
    background-color: #181818;
  }
  .yj-global-group-publisher .publisher-group {
    border-color: #383838;
  }
  .yj-global-tag-selector .selector-arrow {
    background-color: #181818;
    border-color: #383838;
  }
  /*
  		END: Inbox
  		###################################
  	   	START: Notifications
  	*/
  .yj-nav-menu--primary-actions-link.yj-open {
    background-color: #242424;
  }
  .yj-nav-menu--primary-actions-link.yj-open::after {
    background-color: #242424;
  }
  .yj-notifications-menu-contents {
    background-color: #242424;
    color: #c0c0c0;
  }
  .yj-notifications-menu-contents .yj-notification-menu-heading {
    background-color: #242424;
    border-bottom-color: #383838;
    color: #c0c0c0;
  }
  .yj-notifications-menu-see-all {
    border-top-color: #383838 !important;
    /* This exact selector is used by Yammer CSS with !important, so there's no way to avoid this. */
  
  }
  .yj-notifications-menu-see-all.yj-btn {
    background-color: #242424;
  }
  .yj-feed-notifications .yj-notification-item > .yamicon {
    color: #c0c0c0;
  }
  .yj-notification-item-thread-link {
    background-color: #181818;
  }
  .yj-notification-item-thread-link .yj-notification-attributes {
    color: #c0c0c0;
  }
  .yj-notification-item {
    border-bottom-color: #383838;
  }
  /*
  		END: Notifications
  		###################################
  	*/
}
