@-moz-document domain("pansci.asia"), domain("panx.asia") {
.wpbcap.wpbcap-147166.wpbcap-,
#wpbcap img,
#wpbcap,
#ad-sidebar-main{
    animation-name:ad;
    animation-duration:2s;
    animation-fill-mode:forwards;
    animation-delay:1s;
    animation-iteration-count:1;
}

@keyframes ad{
    from{
      opacity:1;
    }
    30%{
        opacity:.8;
    }
    70%{
        height:107px;
        opacity:.7;
    }
    to{
       opacity:0;
        height:0.1px;
    }
}


.ad_box,
.col-md-12 .wrapper-sticky, 
.col-md-12 .sticky_bar{
   display:none !important;
}


}

