css:
/* i really want this to be global */

@-moz-document url-prefix("https://www.infobierzo.com/") {

body {
    background-color: #20202C !important;
}

nav, .elementor-element-14cdcbd {
    background-color: #0C0C0C !important;
}

#menu-1-5b74a4b9 {
    padding-top: 0.3em;
}
#menu-1-9b56af9 {
    padding-bottom: 0.3em;
}

/* Widgets cabecera */
.elementor-element-883c1c4, .elementor-element-6324f89 {
    display: none;
}

.elementor-element-db4ab0f {
    left: 1rem !important;
    top: -0.4rem;
}

.elementor-element-8542fa8 {
    filter: brightness(0) invert(100%);
    left: 20%;
}

nav>ul>li>a {
    
    color: white !important;
    background: #269;
    border-radius: 12px;
    margin: 2px;
    line-height: 60%;
}

#menu-1-5b74a4b9>li>a {
    line-height: 60% !important;
}

* {
    color: white !important;
}

a {
    color: #269 !important;
    transition: color 0.15s ease;
}

a:hover {
    color: #3AF !important;
    transition: color 0.15s ease;
}

.barrantetitulo, .antetitulo {
    background: #269;
}

.fa-level-up {
    color: #269 !important;
}

/* Semana */
.elementor-element-ef10dc9>div>div>.elementor-widget-divider:not(.elementor-widget-divider--view-line_text)
:not(.elementor-widget-divider--view-line_icon) .elementor-divider-separator {
    border-top: var(--divider-border-width) var(--divider-border-style) #922;
}

#cabecerasemana>div, .elementor-element-7a70512>div {
    background: #922 !important;
}

/* Destacamos */
.elementor-element-8016eb7>div>div>div>div>div>.elementor-widget-divider:not(.elementor-widget-divider--view-line_text)
:not(.elementor-widget-divider--view-line_icon) .elementor-divider-separator {
    border-top: var(--divider-border-width) var(--divider-border-style) #3AF;
}
.elementor-element-01ade1b>div {
    background: #3AF !important;
}

/* CyL */.elementor-element-f49b1c1>div>div>div>div>div>.elementor-widget-divider:not(.elementor-widget-divider--view-line_text)
:not(.elementor-widget-divider--view-line_icon) .elementor-divider-separator {
    border-top: var(--divider-border-width) var(--divider-border-style) #C90;
}
.elementor-element-7a70512>div {
    background: #C90 !important;
}

/* Deportes */
.elementor-element-aaf2c28>div>div>.elementor-widget-divider:not(.elementor-widget-divider--view-line_text)
:not(.elementor-widget-divider--view-line_icon) .elementor-divider-separator {
    border-top: var(--divider-border-width) var(--divider-border-style) #293;
}
.elementor-element-aaf2c28>div>div>div>div {
    background: #293 !important;
}

/* Widgets abajo */
.elementor-element-ad4c976, .elementor-element-61e2e2e, .capafondo {
    display: none;
}

}
