@-moz-document domain("roblox.com") {
  /**
          Raw ROBLOX Style 
          Theme color: Green
          This is my first style, so you should probably try it out!
          More changes will be added, including colors for new chat windows.

      ROBLOX account: Mirzadaswag
  */
  .container-fluid {
      background-color: green !important;
  }
  .chat-windows-header .dialog-header {
      background-color: green !important;
  }
  .chat-main .chat-header {
      background-color: green !important;
  }
  .dialog-container {
      background-color: green !important;
  }
  .notification-red {
      color: white !important;
      background-color: black !important;
  }
  .notification-blue {
      color: white !important;
      background-color: black !important;
  }
  .dialog-message-container .dialog-message {
      background-color: green !important;
  }
  .message-inbound {
      color: white !important;
  }
  .btn-secondary-xs {
      background-color: green !important;
      border-radius: 6px !important;
  }
  .btn-primary-md {
      background-color: green !important;
      border-radius: 6px !important;
  }
  #navigation {
      box-shadow: 0 0 50px green !important;
  }
  .mCSB_dragger_bar {
      background-color: green !important;
  }
  .content {
      background-color: rgb(155,155,155) !important;
  }
  .home-header-content {
      color: white !important;
  }
  body {
  	color: green !important;
  }
}
