/* ==UserStyle==
@name GoLang Dark
@version 1.2.1
@description Dark theme for GoLang and GoDoc sites
@namespace x1unix
@author x1unix
@preprocessor uso
==UserStyle== */
@-moz-document domain("golang.org"), domain("godoc.org")
{
  :root
  {
    /* Main UI Colors */
    --color-primary: #444;
    --color-primary-dark: #333;
    --color-accent: #ccc;
    --color-link: rgb(65, 131, 196);

    /* Editor colors */
    --color-comment: #0085FF;
    --color-line-number: #89989E;
    --color-primitive: #ffc600;
    --color-prop-foreground: #ccccc8;
    --color-prop-background: #0d3a58;
    --cobalt-background: #18354A;
    --cobalt-foreground: #F7F7F7;
  }

  html, body
  {
    background-color: var(--color-primary-dark);
    color: var(--color-accent);
  }

  div#topbar,
  #banner
  {
    background-color: var(--color-primary);
  }

  .top-heading a
  {
    color: var(--color-accent);
  }

  a,
  .exampleHeading .text,
  .expandAll,
  h1, h2, h3, h4,
  .rootHeading
  {
    color: var(--color-link);
  }

  h2, pre
  {
    background: var(--color-primary);
  }

  /** Editor Colors start **/
  div#learn .input,
  div#learn .input textarea,
  div#learn .output,
  div#learn .output pre,
  textarea.code,
  div.play .input,
  div.play .input textarea, 
  div.play .output, 
  div.play .output pre,
  div.container > pre,
  div.decl > pre
  {
    background: var(--cobalt-background) !important;
    background-color: var(--cobalt-background) !important;
    color: var(--cobalt-foreground) !important;
  }

  /** Prop types **/
  pre a {
    background: var(--color-prop-background);
    color: var(--color-prop-foreground);
  }
  pre a[href*="builtin"] {
    background: inherit;
    color: var(--color-primitive);
  }

  /** Prop names **/
  pre span:not(.com):not([id=""]) {
    background: var(--color-prop-background);
    color: var(--color-prop-foreground);
  }

  /** Code view - Comments **/
  span.comment,
  span.com
  {
    color: var(--color-comment);
  }

  /** Highlight method names **/
  [data-kind] {
    font-family: 'Menlo', 'Consolas', 'Source Sans', monospace;
  }

  /** Code View && Editor - Line number color **/
  pre .ln,
  div.lines div {
    background: inherit;
    color: var(--color-line-number);
  }

  /** Code editor - line number ident **/
  div.lines + textarea#code {
    padding-left: 15px;
    box-sizing: border-box;
  }

  /** End **/

  /* Go Playground */
  #about
  {
    background-color: var(--color-primary-dark);
    color: var(--color-accent);
  }

  #wrap, #code
  {
    background: var(--cobalt-background) !important;
    background-color: var(--cobalt-background) !important;
    color: var(--cobalt-foreground) !important;
  }

  #output,
  #output pre
  {
    background: #000;
  }

  #output
  {
    padding: 0;
  }

  #output pre
  {
    padding: 10px;
  }

  #output:before
  {
    content: 'Output';
    display: block;
    padding: 10px;
    text-transform: uppercase;
    background: var(--color-primary-dark);
    font: 10pt sans-serif;
    font-weight: bold;
  }

  /** === GoDoc styles === **/

  /** GoDoc Header **/
  nav.navbar.navbar-default {
    background: var(--color-primary);
    border: none;
    border-radius: 0;
  }

  .navbar-default .navbar-brand {
    color: var(--color-accent);
  }

  .navbar-default .navbar-nav > li > a {
    color: var(--color-link);
  }

  .navbar-default .navbar-nav > li.active > a {
    background: var(--color-primary-dark);
    color: var(--color-accent);
  }

  .navbar-default .navbar-nav > li.active > a:hover,
  .navbar-default .navbar-nav > li > a:hover {
    background: var(--color-link);
    color: var(--color-accent);
  }

  /** search **/
  input#x-search-query {
    background: var(--color-primary-dark);
    color: var(--color-accent);
  }

  h2#pkg-overview {
    background: inherit;
  }

  h2#pkg-overview + p > code {
    color: var(--color-comment);
  }

  /** Prop types **/
  div.decl > pre a {
    background: var(--color-prop-background);
    color: var(--color-prop-foreground);
  }
  div.decl > pre a[href*="builtin"] {
    background: inherit;
    color: var(--color-primitive);
  }

  /** Prop names **/
  div.decl > pre span:not(.com):not([id=""]) {
    background: var(--color-prop-background);
    color: var(--color-prop-foreground);
  }

  /** Editor border fix **/
  div.decl > pre {
    border-color: var(--cobalt-background);
  }

  /** GoDoc footer **/
  div#x-footer {
    background: var(--color-primary);
    border: none;
  }

  /** GoDoc Home **/
  .jumbotron, .well {
    background: var(--color-primary);
    border: none;
  }

  .jumbotron input.form-control,
  .well input.form-control {
    background: var(--color-primary-dark);
    color: var(--color-accent);
  }

  .jumbotron h2 {
    text-align: center;
    margin-bottom: 25px;
    display: block;
    color: var(--color-accent);
  }

  /** Project summary **/
  div#x-projnav {
    background: var(--color-primary);
    border: none;
  }
}
