html,body {
	background: #222 !important;
	font-size:10pt;
	color: #8C4D62;
}
.adminbar {
	text-align: right;
	clear: both;
	float: right;
	background: #222 !important;
}
.postform{
    display:block;
    background:none !important;   
}
.postarea {
    background: none !important;
    border-radius: 10px !important;
}
.postarea table {
    display: table;
    background: none;
    margin: 0px auto;
    text-align: left;
}
.logo {
	clear: both;
	text-align: center;
	font-size: 15pt;
	-webkit-text-stroke: 1px #8C4D62;
    color: #000;
    text-shadow:
       3px 3px 0 #FFF0,
     -1px -1px 0 #000,  
      1px -1px 0 #000,
      -1px 1px 0 #000,
       1px 1px 0 #000;
	width: 100%;
	height: 20px !important;
	background-image: url('https://image.ibb.co/fwGKwL/8bitminicrackies.gif') !important;
	background-repeat: repeat !important;
}
form#delform {
/* HERE is the section where you paste in your crackymons */    
    margin-top: 0em;    
    background-image: url('https://image.ibb.co/nQ2VRV/Mould-Happy-Bday-Olivia-logo.gif') !important;
    background-size: 10% !important;
	background-repeat: no-repeat !important;
	background-position: 100% 100% !important;
	background-attachment: scroll !important;
    margin-bottom: 0px;
       
}
form.postform{
    background: none;
}
.theader, .passvalid {
	background: #222;
	-moz-border-radius: 5px;
}
head {
    border-radius: 10px !important;
	background: none !important;
}
hr, .center {
    border-radius: 10px !important;
	display: none !important;
}
td {
	border-radius: 10px !important;
	border: 0 !important;
	color: #FF7BDC !important;
}
input, textarea {
	border-radius: 10px !important;
	background:#1C1C1C !important;
	color: #848484
}
.extended-small .thread {
    border-radius: 10px !important;
	width: 165px !important;
}
.reply:target, .reply.highlight, .reply.highlight-anti {
	border-radius: 10px !important;
	color: #808080 !important;
	border: 0 !important;
}
.reply.preview, .op.preview {
	border-radius: 10px !important;
	color:#569C12 !important;
	width: auto !important;
}
.pagelist.desktop, .reply {
	border-radius: 10px !important;
	border: 0 !important;
	background: rgba(71,71,71,0.25) !important;
}
.extPanel.reply {
	border-radius: 10px !important;
	width: auto !important;
}
.reply {
	border-radius: 10px !important;
	width: auto !important;
}
.thumbnailmsg {
    background: none;
    font-size: small;
    color: #E28333;
}
.omittedposts {
    background: none;
}
.filetitle {
    background: inherit;
    font-size: 1.2em;
    color: #5697c9;
    font-weight: 800;
}
.unkfunc {
    background: #2b2b2b;
    color: #347330;
}
a {
    color: #3c3c84;
}
.opContainer {
	border-radius: 10px !important;
	display: table !important;
	width: auto !important;
}
td.postblock {
	border-radius: 10px !important;
	color: #8C4D62!important;
	background-color: #222!important;
}
tbody tr td {
	border-radius: 10px !important;
	color: #8D8D8D!important;
}
blockquote {
	border-radius: 10px !important;
}
img.thumb {
	border-radius: 5px !important;
}

}
