css:
@-moz-document domain(swappa.com){
body{
    --accent-color: #009A44; /*Original Swappa color: #009A44*/
    
    --primary-background-color: #333333;
    --light-background-color: #555555;
    --dark-background-color: #222222;
    
    --primary-text-color: #CCCCCC;
    
    --primary-line-color: #777777;
    
    --box-shadow: 0 10px 20px rgba(0,0,0,0.19), 0 6px 6px rgba(0,0,0,0.23);
}


/*Top bar*/
.navbar-default{
    background-color: var(--primary-background-color) !important;
    border: 1px solid var(--primary-line-color) !important;
    box-shadow: var(--box-shadow) !important;
}

/*Search Box*/
.navbar .form-control{
    background-color: var(--light-background-color) !important;
    color: var(--primary-text-color) !important;
}

/*Search box dropdown*/
.dropdown-menu{
    background-color: var(--light-background-color) !important;
    border: 1px solid var(--primary-line-color) !important;
}
.dropdown-menu > li > a{
    color: var(--primary-text-color) !important;
}
.dropdown-menu > .active > a, .dropdown-menu > .active > a:hover, .dropdown-menu > .active > .a:focus, .dropdown-menu > li > a:hover{
    background-color: var(--primary-background-color) !important;
}

/*Buttons*/
.btn-default{
    background: var(--light-background-color) !important;
    color: var(--primary-text-color) !important;
    border: 1px solid var(--primary-line-color) !important;
    box-shadow: var(--box-shadow) !important;
}
.btn-primary, .btn-success{
    background-color: var(--accent-color) !important;
    border-color: var(--accent-color) !important;
}

/*Main page background and text*/
body,  .well{
    background-color: var(--primary-background-color) !important;
    color: var(--primary-text-color) !important;
}
a{
    color: var(--primary-text-color) !important;
}

/*Device pictures*/
.cell_product{
    border: 1px solid var(--primary-line-color) !important;
    box-shadow: var(--box-shadow) !important;
}

/*Links*/
a:hover, a:focus, .listing_row a:hover{
	text-decoration: none !important;
	color: var(--accent-color) !important;
}
.title_link, .row_cell_model .price, .listing_row a{
    color: var(--primary-text-color) !important;
}

/*Other Text*/
.speclabel, .speclabel-platform, .speclabel-small{
    color: var(--primary-text-color)
}

/*Recently sold text and prices*/
.price_marker_swappa{
    color: var(--accent-color) !important;
}

/*Current listing price*/
.listing_price_active{
    color: var(--accent-color) !important;
}

/*Navigation bar*/
.breadcrumb{
    background-color: var(--dark-background-color) !important;
    border: 1px solid var(--primary-line-color) !important;
    box-shadow: var(--box-shadow) !important;
}
.breadcrumb > .active{
    color: var(--primary-text-color)
}

/*Table headings*/
.table th{
    background-color: var(--dark-background-color) !important;
    color: var(--primary-text-color) !important;
}

/*Listings of different models of one device and their prices*/
div.col-sm-6:hover{
    box-shadow: var(--box-shadow) !important;
}

/*Number of comments*/
.badge, .active .badge, a.list-group-item.active > .badge, .nav-pills > .active > a > .badge{
    background-color: var(--accent-color) !important;
}

/*Star icons*/
.text-success{
    color: var(--accent-color) !important;
}

/*Random Lines*/
hr{
    border-top: 1px solid var(--accent-color) !important;
}
.dropdown-menu .divider{
    background-color: var(--primary-line-color) !important;
}
.table-bordered > thead > tr > th, .table-bordered > tbody > tr > th, .table-bordered > tfoot > tr > th, .table-bordered > thead > tr > td, .table-bordered > tbody > tr > td, .table-bordered > tfoot > tr > td{
    border: 1px solid var(--primary-line-color) !important;
}
.nav-tabs{
    border-bottom: 1px solid var(--primary-line-color) !important;
}

/*Login Page*/
.panel{
    background-color: var(--primary-background-color) !important;
    color: var(--primary-text-color) !important;
    border: 1px solid var(--primary-line-color) !important;
    box-shadow: var(--box-shadow) !important;
}
.form-control{
    background-color: var(--dark-background-color) !important;
    color: var(--primary-text-color) !important;
    border: 1px solid var(--primary-line-color) !important;
}
.btn-google, .btn-xda{
    background-color: var(--light-background-color) !important;
    color: var(--primary-text-color) !important;
}
div.text-center:nth-child(3) > span:nth-child(1){
    color: var(--primary-text-color) !important;
}
.btn-google img, .btn-xda img{
    background: none !important;
}

/*My Swappa side navigation*/
.list-group .list-group-item{
    background-color: var(--primary-background-color) !important;
    color: var(--primary-text-color) !important;
    border-color: var(--primary-line-color) !important;
}

/*My Profile Page*/
/*Panel headers and footers*/
.panel-heading, .panel-footer{
    background-color: var(--dark-background-color) !important;
    color: var(--primary-text-color) !important;
    border-color: var(--primary-line-color) !important;
}
#id_email{
    background-color: var(--dark-background-color) !important;
}

/*My Buying Tabs*/
#my-swappa-tabs > li.active > a, .nav-tabs > li.active > a:focus{
    background-color: var(--light-background-color) !important;
    color: var(--primary-text-color) !important;
    border: 1px solid var(--primary-line-color) !important;
}
.nav-tabs > li > a{
    color: var(--primary-text-color)
}
#my-swappa-tabs > li > a:hover, #my-swappa-tabs > li > a:focus{
    background-color: var(--dark-background-color) !important;
    /*border: 1px solid transparent !important;*/
}

/*Hide the site reviews widget at the bottom of the main page*/
.trustpilot-widget{
    display: none !important;
}

/*Swappa Footer*/
footer, footer a{
    background: var(--light-background-color) !important;
    color: var(--primary-text-color) !important;
}

/*Recolor some third party logos*/
/*#footer_logo,*/
#press_row .img-responsive{
    filter: invert(80%) !important;
}
}
