css:
@-moz-document domain("www.se7ensins.com") {
#logo img
  {
    max-width: 400px;
    width: 0;
    height: 90px;
    padding-right: 400px;
    background: rgba(55, 196, 57, 0) url(https://i.imgur.com/F6G3mH4.png) top left no-repeat;
  }

#logoBlock { background: url("https://png.pngtree.com/thumb_back/fw800/back_pic/00/01/56/2356050ba772772.jpg") no-repeat scroll 0% 0% rgb(26, 26, 26) !important;}
.sidebar .section .secondaryContent h3 {
    font-weight: bold;
    background: url("https://png.pngtree.com/thumb_back/fw800/back_pic/00/01/56/2356050ba772772.jpg") repeat-x scroll left top rgb(37, 37, 37);
}

.xmgCarouselContainer .sectionMain .titleStrip h3 a {
    color: rgb(11, 0, 0);
}

.nodeList .categoryStrip .nodeTitle a {
    color: rgb(12, 12, 12);
}

.xmgCarouselContainer .sectionMain .titleStrip {
    font-size: 14px;
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    color: rgb(106, 106, 106);
    padding: 9px 10px;
    margin: 3px auto 0;
    background: #2ce022;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#14d61a), color-stop(100%,#0ce430));
    background: -webkit-linear-gradient(top, #4bef1c 0%,#527c39 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #22f518 0%,#41e816 100%);
    -filter: "progid: DXImageTransform.Microsoft.gradient(enabled=false)";
    margin-bottom: 5px;
    border-radius: 5px;
    min-height: 6px;
    -margin-bottom: 10px;
}

.nodeList .categoryStrip {
    font-size: 14px;
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    color: rgb(255, 255, 255);
    padding: 9px 10px;
    margin: 3px auto 0;
    background: #68f911;
    background: -moz-linear-gradient(top, #62ff04 0%, #2cf337 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#32d11d), color-stop(100%,#0cea13));
    background: -webkit-linear-gradient(top, #67ee16 0%,#33f31a 100%);
    background: -o-linear-gradient(top, #5fea0c 0%,#43d322 100%);
    background: -ms-linear-gradient(top, #2ae41c 0%,#61ee0d 100%);
    background: linear-gradient(to bottom, #13fd0c 0%,#2be836 100%);
    -filter: "progid: DXImageTransform.Microsoft.gradient(enabled=false)";
    margin-bottom: 5px;
    border-radius: 5px;
    min-height: 6px;
}

.node .forumNodeInfo.unread .nodeIcon, .node .categoryForumNodeInfo.unread .nodeIcon {
    background-image: url(https://i.imgur.com/oMFN39o.png);
    background-repeat: no-repeat;
    background-position: -36px 0;
}

.sidebar .section .primaryContent h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock h3, .XenBase .xengallerySideBarContainer .xengallerySideBar .section h3 {
    font-size: 14px;
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    color: rgb(0, 255, 45);
    background-color: rgb(26, 26, 26);
    padding-top: 7px;
    padding-bottom: 7px;
    padding-left: 10px;
    margin: -3px -3px 5px;
    border-bottom: 3px solid rgb(82, 125, 57);
    border-radius: 3px;
}

.primaryContent a {
    color: rgb(13, 247, 22);
}

.pairs dt, .pairsInline dt, .pairsRows dt, .pairsColumns dt, .pairsJustified dt {
    color: rgb(130, 124, 124);
}


body {
    -webkit-text-size-adjust: 100%;
    -moz-text-size-adjust: 100%;
    -ms-text-size-adjust: 100%;
    -text-size-adjust: 100%;
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    color: rgb(179, 55, 55);
    word-wrap: break-word;
    line-height: 1.28;
}

body .muted, body a.muted, body .muted a {
    color: rgb(255, 255, 255);
}

a:link, a:visited {
    color: rgb(50, 185, 26);
    text-decoration: none;
}

.username .style7 {
    color: #00daff;
}

html .navTabs .navTab.selected .navLink {
    position: relative;
    font-weight: bold;
    color: rgb(255, 255, 255);
    border-top-left-radius: 3px;
    border-top-right-radius: 3px;
    background: #1cab42;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#363e44), color-stop(100%,#61b92c));
    background: -webkit-linear-gradient(top, #5eb32b 0%,#5dbb25 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #18f748 0%,#03633b 100%);
   -filter:"progid:DXImageTransform.Microsoft.gradient(enabled=false)";
}

.nodeList .categoryStrip .nodeTitle {
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    color: rgb(0, 0, 0);
}

h1, h2, h3, h4, h5, h6 {
    font-size: 100%;
    font-weight: bold;
}

