css:
@-moz-document regexp("http(s)?://.*\\.animezone\\.pl/(?!(v[0-9].[0-9]/)?plugins/|Fplugins|plugins/like).*") {
body {
    background-color: #1e1e1e ! important;
}

.footer {
    background-color: #1e1e1e ! important;
    color: #505050;
}

.news,
.categories {
    background-color: #2e2e2e ! important;
    color: #DEDEDE;
}

.well {
    background: #525252;
    color: white;
    box-shadow: none;
    border: none;
    border-radius: 0;
}

.label-grey {
    background-color: #3A3A3A;
}

.categories-collection .categories .label-grey a,
.categories-newest .categories .label-grey a,
.user-activity .categories .label-grey a {
    color: #DEDEDE;
}

.site-sidebar .list-group-item {
    background-color: #3A3A3A;
    color: white;
    border-color: #444444;
}

.list-group-striped a:hover,
.list-group-striped li:hover {
    background-color: #292929;
}

.list-group-striped a:nth-child(even),
.list-group-striped li:nth-child(even) {
    background-color: #2D2D2D;
}

.content .panel-default .panel-title,
.content .category-description h3 {
    text-shadow: none;
}

.table-bordered > thead > tr > th,
.table-bordered > tbody > tr > th,
.table-bordered > tfoot > tr > th,
.table-bordered > thead > tr > td,
.table-bordered > tbody > tr > td,
.table-bordered > tfoot > tr > td {
    border: none;
    border-bottom: 1px solid #6A6A6A !important;
    background: #272626;
}

.table-bordered > tbody > tr:last-child > td {
    border-bottom: none !important;
}

.table-striped > tbody > tr:nth-child(odd) > td,
.table-striped > tbody > tr:nth-child(odd) > th {
    background: #232323;
    border-bottom: 1px solid #6A6A6A !important;
}

.site-main .panel-default .panel-heading,
.site-main .category-description h5 {
    background: #070707;
    box-shadow: none;
    color: #FFFFFF;
}

.form-control {
    display: block;
    width: 100%;
    height: 34px;
    padding: 6px 12px;
    font-size: 14px;
    line-height: 1.42857143;
    color: #E1E1E1;
    background-color: #242424;
    background-image: none;
    border: 1px solid #525252;
    border-radius: 4px;
    -webkit-box-shadow: inset 0 1px 1px rgba(0, 0, 0, .075);
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, .075);
    -webkit-transition: border-color ease-in-out .15s, box-shadow ease-in-out .15s;
    transition: border-color ease-in-out .15s, box-shadow ease-in-out .15s;
}

.btn-default {
    background: #404040;
    box-shadow: none;
    text-shadow: none;
    color: #E1E1E1;
    border-color: #676767;
}

.btn-default:hover,
.btn-default:focus {
    background: #353535;
    box-shadow: none;
    text-shadow: none;
    color: #E1E1E1;
    border-color: #585858;
}

.btn-primary {
    background: #2d6ca2;
    box-shadow: none;
    border-color: #2d6ca2;
}

.btn-primary:hover, .btn-primary:focus, .btn-primary:active, .btn-primary.active {
    background-color: #3276b1;
    border-color: #2d6ca2;
}

.btn-primary.active:hover, .btn-primary.active:focus, .btn-primary.active:active{
    opacity: 0.9;
}

.site-main .panel-default,
.site-main .panel-default .panel-heading {
    background: transparent;
    border: none;
    box-shadow: none;
    background-color: #42363663;
}

.panel-footer {
    padding: 10px 15px;
    background-color: #42363663;
    border-top: none;
    border-bottom: 1px solid #352f2f;
    color: white;
}

.comments-list .comment:nth-child(even) .comment-body {
    background-color: #444444;
}

.comments-list .comment .comment-body {
    width: 88%;
    margin-left: 2%;
    border-radius: 1px;
    background-color: #2D2D2D;
    padding: 8px;
    position: relative;
    color: #FFFFFF;
}

.site-sidebar .ratings .panel-footer {
    background-color: #1F1F1F;
    padding-top: 1px;
    border: none !important;
}

.pager li > a,
.pager li > span {
    display: inline-block;
    padding: 5px 14px;
    background-color: #141414;
    border: 1px solid #6F6F6F;
    border-radius: 15px;
}

.panel-body,
td,
.site-sidebar .panel-default,
.site-sidebar .panel-default .panel-heading,
.site-main .category-description h3,
.site-sidebar .ratings .panel-body {
    background: rgba(23, 17, 17, 0.24);
    box-shadow: none;
    border-radius: 0;
    border-bottom: none;
    color: white;
    border-color: #3a3a3a;
}

.site-main .category-description h3 {
    text-transform: uppercase;
    font-weight: 700;
    font-size: 16px;
}

th {
    font-weight: bold;
    background-color: #2F2F2F;
    color: white;
}

.bs-callout-info {
    background-color: #233144;
    border-color: #013e50;
}

.panel {
    margin-bottom: 20px;
    background-color: #2A2A2A;
    border: 1px solid transparent;
    border-radius: 4px;
    -webkit-box-shadow: 0 1px 1px rgba(0, 0, 0, .05);
    box-shadow: 0 1px 1px rgba(0, 0, 0, .05);
}

.bs-callout-danger {
    background-color: #242424;
    border-color: #E7DAD9;
}

.comments-list .comment .comment-body .close,
.comments-list .comment .comment-body .comment-edit,
.comments-list .comment .comment-body .comment-report,
.comments-list .comment .comment-body small {
    color: #F6F6F6;
}

.popover {
    position: absolute;
    top: 0;
    left: 0;
    z-index: 1010;
    display: none;
    max-width: 276px;
    padding: 1px;
    text-align: left;
    background-color: #3E3E3E;
    background-clip: padding-box;
    border: 1px solid #ccc;
    border: 1px solid rgba(0, 0, 0, .2);
    border-radius: 6px;
    -webkit-box-shadow: 0 5px 10px rgba(0, 0, 0, .2);
    box-shadow: 0 5px 10px rgba(0, 0, 0, .2);
    white-space: normal;
}

.comments-list .comment .spoiler {
    margin: 5px 0;
    color: #FFFFFF;
}

.rek > div {
    height: auto !important;
}

.nav-head {
    border-top: 1px solid #1f1f1f;
}

.footer-border {
    border-top: none;
}

.img-thumbnail {
    padding: 0;
    background-color: transparent;
    border: none;
    object-fit: cover;
    border-radius: 5px;
}

.category-description-body .basic-info tr td:nth-child(1) {
    background-color: #1f1f1f;
}

.category-description-body .basic-info,
.category-description-body .image {
    border: none;
    padding: 3px 0;
}

.table-bordered {
    border: none;
}

.table-hover > tbody > tr:hover > td,
.table-hover > tbody > tr:hover > th {
    background: #403a3a;
}

.category-description .bg-success.filler {
    background-color: #26461a !important;
}

.btn-success {
    box-shadow: none;
    background-color: #3e8f3e;
    border: transparent;
    text-shadow: none;
}

.btn-success:active, .btn-success.active {
    background-color: #56b756;
    border: transparent;
}

.btn, .btn-xs, .btn-group-xs>.btn {
    border-radius: 0;
}

.btn._visited:visited {
    background: #f0ad4e;
}

.pagination > li > a,
.pagination > li > span {
    color: #acb0b3;
    background-color: #2f2f2f;
    border: 1px solid #444343;
}

.pagination > li > a:hover,
.pagination > li > span:hover,
.pagination > li > a:focus,
.pagination > li > span:focus {
    color: #2a6496;
    background-color: #eee;
    border-color: #ddd;
}

.pagination > li > a:hover,
.pagination > li > span:hover,
.pagination > li > a:focus,
.pagination > li > span:focus {
    color: #b3b3b3;
    background-color: #484747;
    border-color: #585858;
}

.alert-info {
    background: #002f46;
    text-shadow: none;
    box-shadow: none;
    border-color: #074969;
    color: white;
}

.alert-success {
    background: #1f3514;
    text-shadow: none;
    box-shadow: none;
    border-color: #25580e;
    color: white;
}

.close {
    color: white;
    text-shadow: none;
    opacity: .7;
}

.close:hover,
.close:focus {
    color: white;
}

.bs-callout-warning {
    background-color: #c7aa1e;
    border-color: #c3862f;
    color: black;
}

.login-form .btn-silver {
    display: inline-block !important
}

.watching-status .btn-success.active {
    border-left: 5px solid #41793a;
}

.watching-status .btn-warning.active {
    border-left: 5px solid #d8b410;
}

}
