css:
@-moz-document url-prefix("http://3cx"), url-prefix("https://3cx")
{
html
{
    background-color: #333 !important;
    
}
.bg-light.lter, .bg-light .lter {
	background-color: #222 !important;
}

.panel-default .panel-heading {
	background-color: #444 !important;
}

.panel {
	background-color: #222 !important;

}

.table-striped > tbody > tr:nth-child(2n+1) > td, .table-striped > tbody > tr:nth-child(2n+1) > th, .table-striped > thead > th {
	background-color: #333 !important;
}

.bg-white {
	background-color: #222 !important;
	color: hsl(202, 11%, 50%) !important;
}

.app::before {
	background-color: #222 !important;
}

.list-group-item {
	margin-bottom: -1px;
	background-color: #222 !important;
	border: 1px solid #555 !important;
}

.form-control {
	background-color: #222 !important;
	border: 1px solid #555 !important;
}

.btn-default {
	color: #58666e !important;
	border-color: #555 !important;
	background-color: #222 !important;
}

.panel .list-group-item {
	border-color: #555 !important;
}

.b-a {
	border: 1px solid #555 !important;
}

.b-light {
	border-color: #555 !important;
}

.b-r {
	border-right: 1px solid #555 !important;
}

.b-b {
	border-bottom: 1px solid #555 !important;
}

.panel-default > .panel-footer, .panel-default > .panel-heading {
	border-color: #555 !important;
}

.panel-default {
	border-color: #555 !important;
}

.panel > .panel-body + .table, .panel > .panel-body + .table-responsive, .panel > .table + .panel-body, .panel > .table-responsive + .panel-body {
	border-top: 1px solid #555 !important;
}

.table > tbody > tr > td, .table > tbody > tr > th, .table > tfoot > tr > td, .table > tfoot > tr > th {
	border-top: 1px solid #555 !important;
}

.stickyClass {
	background: #222 !important;
}

.modal-content {
	background-color: #222 !important;
}

.tab-container .tab-content {
	background-color: #222 !important;
    border: 1px solid #555 !important;
    }

.nav-tabs > li.active > a, .nav-tabs > li.active > a:focus, .nav-tabs > li.active > a:hover {
	background-color: #222 !important;
}

.i-checks > i {
	background-color: #333 !important;
}

.table > thead > tr > th {
	border-bottom: 1px solid #999 !important;
}

.table > tbody > tr > td, .table > tbody > tr > th, .table > tfoot > tr > td, .table > tfoot > tr > th, .table > thead > tr > td, .table > thead > tr > th {
	border-top: 1px solid #999 !important;
}

body {
	color: #8398a4 !important;
}
.bg-light {
	background-color: #222 !important;
}
}
