css:
/* i really want this to be global */
  body, html {font-family: Verdana;}
a:link {
	color: #0000ff; 
	text-decoration: none; 
}
a:active {
	color: #0000ff;
	text-decoration: none;
}
a:visited {
	color: green;
	text-decoration: none;
}
a:hover {
	color: red;
	text-decoration: none;
}
body, html {
	font-family: Arial;
	background-color: white;
	margin: 0;
	width: 100%;
}
container {
	max-width: 1600px;
	margin: 0 auto;
	padding: 0 1em 0 1em;
}
/* Navigation Styles */
.navigation ul {
	list-style-type: none;
	max-width: 100%;
	display: block;
	margin: 0;
	padding: 5px;
	border: #d4d4d4 1px solid;
        border-radius:0;
	background-color: #fff;
}
.navigation li {
	display: block;
	margin: 0;
	padding: 0;
}
.navigation a {
	text-decoration: none;
	display: block;
	padding: 5px;
	font-size: 16px;
}
.navigation a:hover {
	color: red;
	text-decoration: none;
	background-color: #fff;
}
masthead {
    border: none;
    border-radius: 0;
    padding: 1em;
    background-color: #1E90FF;
    color: white;
    font-size: x-large;
    margin: 10px auto;
}

.content {

    margin-left: 20px;
    width: 90%;
    font-size: 16px;
    padding: 0 1em;

}
footer {
    text-align: center;
    font-size: 9pt;
    clear: both;
    border: #d4d4d4 1px solid;
    border-radius: 0px;
    padding: 1em;
    background-color: #1E90FF;;
    margin: 100px auto;
}
footer a {
    color: #fff;
    text-decoration: none;
}
