/* ==UserStyle==
@name           SyncroMSP in Blue
@namespace      USO Archive
@author         nullzilla
@description    `Customize the dashboard for SyncroMSP https://syncromsp.com/For all the teal h8erz`
@version        20200227.6.24
@license        CC0-1.0
@preprocessor   uso
==/UserStyle== */
@-moz-document regexp("https:\/\/.*\.syncromsp\.com\/.*") {
    /* Main navigation bar active tab text link */
    .waypoint-syncro.light .syncro-top-nav-container .sub-navbar-container .sub-navbar-links > li.active > a {
        color: #0D48A1 !important;
    }
    /* Main navigation bar active tab underline */
    .waypoint-syncro.light .syncro-top-nav-container .sub-navbar-container .sub-navbar-links > li.active > a::before {
        background-color: #0D48A1 !important;
    }
    /* Main navigation bar active tab icon */
    .waypoint-syncro.light .syncro-top-nav-container .sub-navbar-container .sub-navbar-links > li.active > a .fal {
        color: #0D48A1 !important;
    }
    /* Main navigation bar mouseover text link */
    .waypoint-syncro.light .syncro-top-nav-container .sub-navbar-container .sub-navbar-links > li:hover > a {
        color: #0D48A1 !important;
    }
    body.cn.dark .btn-link, body.cn.dark .text-primary {
        color: #0D48A1 !important;
    }
    /* Main navigation bar mouseover tab icon */
    .waypoint-syncro.light .syncro-top-nav-container .sub-navbar-container .sub-navbar-links > li:hover > a .fal {
        color: #0D48A1 !important;
    }
    /* Top right drop down menu mouseover */
    .waypoint-syncro.light .syncro-top-nav-container .main-navbar-container .main-navbar a:hover {
        color: #0D48A1!important;
    }
    /* 'More' drop down menu mouseover */
    .waypoint-syncro.light .syncro-top-nav-container .sub-navbar-container .sub-navbar-links > li.dropdown .dropdown-menu > li a:hover {
        color: #0D48A1!important;
    }
    /* 'More' drop down menu active */
    body.cn .dropdown-menu > .active > a {
        background-color: #0D48A1 !important;
    }
    /* Main navigation bar new item icon */
    .add-new-dropdown-toggle .fad {
        color: #0D48A1 !important; 
    }
    /* Notification sidebar X close button */
    .waypoint-syncro.light .syncro-notification-slideout .slideout-panel-header .slideout-opener-right i {
        color: #0D48A1!important;
    }
    /* Notification sidebar Dismiss All button */
    .waypoint-syncro.light .syncro-notification-slideout .slideout-panel-header .btn-warning {
        background-color: #0D48A1!important;
    }
    /* Other drop downs*/
    body.cn .dropdown-menu > li > a:hover {
        background-color: #0D48A1 !important; 
    }
    /* Tickets buttons */
    body.cn .btn-default.active {
        background-color: #0D48A1!important;
    }
    /* Page name header text on some pages */
    h1 {
        color: #666666!important;
    }
    /* Page name header text on some pages */
    .customer-title {
        color: #666666!important;
    }
    /* Dashboard header text */
    .cardbox .cardbox-row .dashboard-card .card-title {
        color: #666666!important;
    }
    /* Dashboard chart header text */
    .highcharts-title {
        color: #666666!important;
    }
    /* Dashboard chart axis lines */
    .highcharts-axis-line {
        stroke: #0D48A1;
    }
    /* Buttons */
    body.cn .btn-default {
        color: #0D48A1;
    }
    body.cn .btn-primary, body.cn .btn-teal, body.cn .btn-success {
        background-color: #0D48A1!important;
        border-color: #0D48A1!important;
    }
    /* Background color of widget headers */
    body.cn .widget .widget-header {
        background-image: linear-gradient(-53deg, #0D48A1 3%, #0D48A1 92%);
    }
    /* Assets & RMM tab Overview and Alerts header text */
    .blue-tile-header {
        color: #666666;
    }
    /* Asset sidebar widget text */
    body.cn .widget.borderless .widget-header i, body.cn .widget.borderless .widget-header h3 {
        color: #666666;
    }
    /* Asset sidebar widget dividing line */
    body.cn .widget.borderless .widget-header {
        border-bottom-color: #666666;
    }
    /* Signature pad accept button*/
    body.cn .sigPad button:hover {
        background-color: #0D48A1 !important; 
        border-color: #0D48A1 !important; 
    }
    body.cn .sigPad button {
        background-color: #0D48A1 !important; 
    }
    /* Item Links */
    body.cn .table-entry-head {
        color: #0D48A1;
    }
    /* Bonus: Change Mailer to look like the other menu items */
     #marketr-nav:not(.active) a {
        color: #555555;
    }
}
