css:
@-moz-document domain("outlook.office.com"), domain("outlook.office365.com") {
#owaLoading {
    background-color: #444;
}

/* Skype window */
.conductorContent {
    background-color: #444;
}

/* Increasing folders efficiency hiding useless elements and compressing the others*/

div[aria-labelledby="MailFolderPane.FavoritesHeader"] {
  display: None;
}


._n_T2 ._n_S2 {
    height: 35px;
    line-height: 30px;
    margin-top: -5px;
    padding: 0;
    font-size: 0.8em;
}

._n_T2 ._n_U2, ._n_T2 ._n_V2, ._n_T2._n_W2 ._n_X2 {
    top: 31px;
}

div[role=treeitem]{
    height: 20px;
    line-height: 20px;
    margin: 0;
    padding: 0;
    font-size: 8px
}

div[role=treeitem] .ms-font-m{
    font-size: 12px
}


div[role=treeitem] .ms-bg-color-neutralLighter, .ms-bgc-nlr, .ms-bg-color-neutralLighter-hover, .ms-bgc-nlr-h {
    background-color: #444;
}

.groupsPadding {
    display: none;
}

/* End of compression */

#Item\.MessagePartBody {
    background-color: #fff;
}

/* New message buble */
._rp_X {
    background:rgba(0,0,0,1);
    display: none;
}

/* Menu bar*/
.ms-bg-color-themeLighterAlt, .ms-bgc-tlra, .ms-bg-color-themeLighterAlt-hover:hover, .ms-bg-color-themeLighterAlt-focus:focus, .ms-bg-color-themeLighterAlt-before::before, .ms-bgc-tlra-h:hover, .ms-bgc-tlra-f:focus, .ms-bgc-tlra-b::before {
    background-color: #555;
}

/* Search Button */
.ms-bg-color-themeLighter, .ms-bgc-tlr, .ms-bg-color-themeLighter-hover:hover, .ms-bg-color-themeLighter-focus:focus, .ms-bg-color-themeLighter-before::before, .ms-bgc-tlr-h:hover, .ms-bgc-tlr-f:focus, .ms-bgc-tlr-b::before {
    background-color: #555;
}

/* Bottom menu */
.ms-bg-color-neutralLight, .ms-bgc-nl, .ms-bg-color-neutralLight-hover:hover, .ms-bg-color-neutralLight-focus:focus, .ms-bg-color-neutralLight-before::before, .ms-bgc-nl-h:hover, .ms-bgc-nl-f:focus, .ms-bgc-nl-b::before {      
    background-color: #111;
}

/* Increasing folders efficiency Setting different colors perl foldering level*/

span[title="My Name"] {
	color: #ffffff;
}

span[title="Sent Items"] {
	color: #5555ff;
}

span[title="Inbox"] {
	color: #5555ff;
}

span[title="Deleted Items"] {
	color: #222;
}

span[title="RSS Feeds"] {
	color: #222;
}

span[title="Conversation History"] {
	color: #222;
}

span[title="Junk Email"] {
	color: #222;
}

span[title="Notes"] {
	color: #222;
}

/* End of coloration */

.mainWindow {
	background-color: #444;
    color: #fff;
}

#Conversation\.FossilizedTextBody {
    background-color: #fff;
}

.customScrollBar::-webkit-scrollbar-thumb {
    border: solid 6px #666;
}

.customScrollBar::-webkit-scrollbar-thumb:hover {
    border: solid 6px #666;
}

.customScrollBar::-webkit-scrollbar-button {
	background-color: #333;
}

.customScrollBar::-webkit-scrollbar-track{
	background-color: #333;
}

.ms-font-color-themePrimary, .ms-fcl-tp, .ms-font-color-themePrimary-hover:hover, .ms-fcl-tp-h:hover {
    color: #fff;
}

.ms-font-color-neutralSecondary, .ms-fcl-ns, .ms-font-color-neutralSecondary-hover:hover, .ms-fcl-ns-h:hover {
    color: #aaa;
}

.ms-font-l, .ms-font-xs {
    color: #fff;
}

.ms-font-s {
    color: #aaa;
}

.ms-font-s ._lv_01 {
    color: #aaa;
}

.ms-bg-color-themeSecondary, .ms-bgc-ts, .ms-bg-color-themeSecondary-hover:hover, .ms-bgc-ts-h:hover {
    background-color: #008888;
}

.ms-font-color-black, .ms-fcl-b, .ms-font-color-black-hover:hover, .ms-fcl-b-h:hover {
    color: #fff;
}

.ms-font-color-themeDark, .ms-fcl-td, .ms-font-color-themeDark-hover:hover, .ms-fcl-td-h:hover {
    color: #ddd;
}

.ms-bg-color-themeLight, .ms-bgc-tl, .ms-bg-color-themeLight-hover:hover, .ms-bgc-tl-h:hover {
    background-color: #777;
}

.ms-bg-color-neutralLighter, .ms-bgc-nlr, .ms-bg-color-neutralLighter-hover:hover, .ms-bgc-nlr-h:hover {
    background-color: #777;
}

._mcp_11, ._mcp_01  {
    background-color: #444;
}

._mcp_t1 {
    background-color: #444;
    color: #fff;
}

._zy_k, .rps_f749, ._rp_y3 {
    background-color: #fff;
}

.ms-font-xl {
    color: #fff
}

._zy_l, ._zy_m  {
    background-color: #444;
    color: #fff;
}

.ms-bg-color-white, .ms-bgc-w, .ms-bg-color-white-hover:hover, .ms-bgc-w-h:hover {
    background-color: #444
}

.ms-font-color-neutralPrimary,
.ms-fcl-np,
.ms-font-color-neutralPrimary-hover:hover,
.ms-fcl-np-h:hover {
    color: #fff;
}

._f_w1, ._f_w1 ._f_v1 {
 background: #666!important;   
}

._cx_b1 {
background-color: #444;
}

.detailsPane {
    background-color: #444;
}

.popupPanel {
    background-color: #444;
}

._cu_D1, ._cu_w1 {
    background-color: #444;
}

.listItemDefaultBackground {
    background-color: #555;
}


/* Flagged messages */
.showAsFlaggedMouse {
    background-color: #00866a21;
}

/* Tasks css */
.absoluteBottom div {
    background-color:#333;
}

/* Skype discussion  */
.ms-font-color-black, .ms-fcl-b, .ms-font-color-black-hover:hover, .ms-fcl-b-h:hover{
    background-color:#333;
}
}
