css:
@-moz-document domain("myanimelist.net")
{


/* ----------------- STATUS COMOB LIST THINGIES ------------------*/

  .anime-detail-header-stats .user-status-block
  {
    visibility: hidden;
  }
/* ---------------- SCORE SHIT -------------------*/

  .anime-detail-header-stats .stats-block .score
  {
    visibility: hidden;
    width: 0px;
  }

  .anime-detail-header-stats .stats-block .numbers
  {
    visibility: hidden;
  }

  .anime-detail-header-stats .stats-block .information
  {
    font-size: 25px !important;
  }

  .anime-detail-header-stats .stats-block:before
  {
    visibility: hidden;
  }

  .anime-detail-header-stats .stats-block .information-block
  {
    position: absolute;
    top: 23px;
    left: 10px;
  }

  .page-common .pb16
  {
    padding-bottom: 0px !important;
  }

/* -------- STATISTICS ----------- */

  .page-common .po-r
  {
    visibility: hidden;
  }

  .page-common .po-r.clearfix
  {
    visibility: visible;
  }
/* --------- halloween ------------- */

  .anime-detail-header-stats .stats-block
  {
    margin-top: 25px;
  }

  .anime-detail-header-stats.halloween:after
  {
    top: 25px;
  }

  .page-common .borderDark
  {
    visibility: hidden;
    height: 0px;
  }

  .borderDark.pt8.pb8.pl4.pr4.mb8
  {
    visibility: visible !important;
    height: auto;
  }

  .score
  {
    visibility: hidden !important;
  }

  .horiznav-nav-seasonal.po-r
  {
    visibility: visible !important;
  }

  span[itemprop="description"]
  {
    visibility: visible;
    height: 0px;
  }
}
