css:
@-moz-document domain("ing-diba.de")
{
body{
  background:black;
  color:white;
}
hr.gap-bottom-1{
  background:grey;
}
h1, h2, h3, h4, h5, h6, .heading{
  color:#0040FF;
}
a{
  color:white;
}

a.link-primary, a.footer-group__title{
  color: #0040ff;
}

div.grid-w6{
  color:black;
}

form.header-quicksearch{
  background:black;
}

header.header{
  background: black;
}

.nav.nav--ibbr{
  border-color:grey;
}

nav.nav--ibbr{
  border-color: grey;
  background:#2E2E2E;
}

nav.nav{
  margin-bottom: 5px;
  background:#2e2e2e;
}

.nav__item-heading, .nav__link, .nav__static{
    color:#0040FF;
}

main.main{
  background:#222;
}

input.input-field{
  color:black;
}

textarea.textarea{
  color:black;
}


.diba-keypad__display{
  background: #848484;
}

.function-panel{
  background: #2e2e2e;
}

[data-session-timer]{
  color:black;
}

.link-function-panel{
  color:#0040ff;
}

span.account-row__type{
  color:#0040ff;
}
select.select{
  color:black;
}
span.variante b{
  color:#0040ff;
}

.page-wrap::before {
	background: #333;
}

.page-wrap--g2p {
	background: #222;
    color:#aaa ;
}

body {
	color: #111;
}
.search-g2p {
	background-color: #222;
}

.content-container {
	background-color: #333;
    color: #ccc;
}

.function-panel ul {
	background-color: #333;
}

.function-panel__opener {
	background-color: #333;
	color: #ccc;
}

.quick-panel-wrapper--g2p .function-panel li:last-of-type::after {
	background: #333;
}

.g2p-banking-header {
	background: #333;
}

.quick-link-wrapper {
	background: #333;
    color:#ccc;
}

.quick-link-wrapper [class^="quicklink-"]::before {
	color: #777;
}

.quick-link-wrapper [class^="quicklink-"][class$="eroeffnen"] {
	color: #ccc;
}

.header--g2p.header::before {
	background-color: #333;
}

.page-wrap--g2p .content-container__title .heading--m {
	color: #ccc;
}

.button-tiny--primary, .button-tiny--info, .button-info, .ibbr-table--trademask .action-bar--buttons[data-state="open"] a[role="button"], .button-tiny--up, .button-tiny--down[data-trigger-state="open"], .button-tiny--down, .button-tiny--up[data-trigger-state=""], .button-tiny--up[data-trigger-state="down"], .button-tiny--plus, .button-tiny--trend, .button-tiny--check, .button-tiny--primary:focus, .button-tiny--info:focus, .button-info:focus, .ibbr-table--trademask .action-bar--buttons[data-state="open"] a[role="button"]:focus, .button-tiny--up:focus, .button-tiny--down:focus, .button-tiny--plus:focus, .button-tiny--trend:focus, .button-tiny--check:focus {
	background-color: #333;
}

.quick-panel-wrapper--g2p .function-panel-wrapper {
	background: #222;
}

.g2p-content-card, .page-wrap--g2p .feedbackPanel, .g2p-account__card, .g2p-transaction-overview-export, .g2p-transaction__card, .g2p-categories__card {
	background: #333;
}

.g2p-account__card-title, .g2p-account__row:not(:last-child) {
	color: #888;
	padding-bottom: 16px;
}

.g2p-account__row[href]:hover {
	background-color: #333;
}

.quick-panel-wrapper--g2p .quick-link-wrapper .quicklink-postbox__label {
	color: #ccc;
}

.g2p-extend-toggle::after {
	background-color: #333;
}

.modal__dialog {
	background: #222;
}

.modal--g2p .modal__dialog [class*="heading--"] {
	color: #ccc;
}

.details-default, .details-default--inverted, .elements-inverted .details-default, .details-big, .details-big-secondary, .details-primary, .details-hot {
	color: #aaa;
}
.input-field, .textarea, .select, .postbox-notification__button, .postbox-notification__tiny-button {
	background: #222;
    border: 1px solid #555;;
}

input.input-field {
	color: #ccc;
}

.diba-keypad__keyboard {
	background: #222;
}

.button-default, .button-default--back, .content-header__button, .content-header__button-actions, .button-default--dropdown, .nav-ibbr .refresh, .diba-keypad__keyboard-key, .diba-keypad__keyboard-key--double, .button-default--large {
	background-color: #333;
	border: 1px solid #666;
	color: #888;
}

.diba-keypad__display ul li::after {
	background-color: #333;
}

.diba-keypad__display ul li {
	color: #333;
	border: 2px solid #333;
}

.diba-keypad__display ul li.active, .diba-keypad__display ul li.focus {
	background: #333;
}

.diba-keypad.focus .diba-keypad__display li.focus {
	border: 2px solid #333;
}

[data-device="DESKTOP"] .g2p-account__row[href]:hover::before {
	background: #444;
}

.g2p-account__row[href]:hover {
	color: #999;
}

.quick-link-wrapper [class^="quicklink-"] {
	color: #ccc;
}

.g2p-no-padding .notification {
	background: #333;
}

.g2p-transaction-group__header {
	background-color: #5555;
	color: #aaa;
}

.g2p-transaction__card {
	color: #ccc;
}

.g2p-transaction-summary__amount--positive .g2p-amount {
	background-color: #165c2a;
}

.select:not(:disabled), .postbox-notification__button:not(:disabled), .postbox-notification__tiny-button:not(:disabled) {
	background-color: #555;
}

input[type="checkbox"]:disabled, input[type="date"]:disabled, input[type="month"]:disabled, input[type="email"]:disabled, input[type="number"]:disabled, input[type="tel"]:disabled, input[type="text"]:disabled, textarea:disabled {
	background-color: #555;
}

.g2p-tab-nav::after {
	background-color: #666;
}

.header__login-timer-wrapper [data-session-timer] {
	background: #555;
	color: #ccc;
}

.button-primary, .button-primary--back, .button-primary--cta, .button-primary--refresh, .button-default--refresh, .button-primary--large, .button-primary--search, .button-primary--g2p {
	color: #222;
}

.header__inner {
	background: #333;
}

.nav .nav__level-2-inner::before {
	background: #333;
}

.nav .nav__level-2 .nav__level-2-inner > .nav__item-group > .nav__item > .nav__link {
	color: #ccc;
}

.widget__head {
	background-color: #222;
}

.widget__headline h4 {
	color: #ccc;
}

.widget__icon-headline {
	color: #aaa;
}

.widget-teaser__row {
	background-color: #333;
}

.widget-info__row {
	background-color: #333;
}

.g2p-transaction-summary:hover {
	background-color: #444;
}

.form-group__definition.counter {
	background: #222;
}

.form-group__definition {
	color: #aaa;
	background: #222;
}

.notification strong, .notification span, .notification b {
	color: #aaa;
}
}
