css:
@-moz-document url-prefix("https://movielens.org/") {
.ml4-trailer-carousel.ng-scope {
    position: relative;
}
body, .contentWrapper{
    background-color: #252525;
}
header.base-compact-header .base-dropdown, .ml4-movie-card .movie-card-container, .movie-alt .movie-details-header, header.base-compact-header{
    background-color: #1b1b1b;
    border-color: #333;
}
.ml4-wishlist .in-wishlist, .ml4-movie-card .movie-card-container .movie-card-metadata .movie-card-metadata-title:before{
    background: transparent;
}
header.base-compact-header .base-dropdown ul li>a, #root{
    color: lightgray;
}
.movie-card-metadata-info{
    color: darkgray;
}
.btn-default{
    background-color: black;
    border-color: #333;
    color: lightgray;
}
}
