css:
@-moz-document domain("markos.roanm.net") {
body {
    background: #202225;
    color: white;
    overflow: hidden;
    border: 0px;
}

#taskbar {
    background: #111214;
    color: white;
}
#openButton {
    background: #36393e;
    color: white;
    border: 0px solid black;
    border-top: 0px solid black;
    border-radius: 0px;
    box-shadow: 0px 0px 0px;
}
.desktopItem {
    background: #36393e;
    color: white
}
#startMenu {
    background: #36393e;
    border: 0px;
    border-radius: 0px;
    bottom: 30px;
    height: 300px;
    width: 200px;
    color: white;
}
.menu {
    color: white
}
.window {
    background: #36393e;
    color: white;
    overflow: auto;
    border: 1px solid #202225;
}
.header {
    background: #111214;
    color: white;
    border: 0px;
    box-shadow: 0px 0px 0px;
    width: 100%;
}
.exitButton {
    background: #36393e;
    color: white;
    right: 2px;
    top: 11.333px;
    border-radius: 0px;
}
input {
    background: #484b4f;
    color: white;
}
button {
    background-color: #484b4f;
    color: white;
}
a {
    background: none;
    color: #0096cf;
}
#treenetThing {
    margin-right: -0px;
    overflow-y: auto;
    overflow-x: hidden;
}
.button33k3i {
    -moz-box-shadow: inset 0px 0px 0px 0px black;
    -webkit-box-shadow: inset 0px 0px 0px 0px black;
    box-shadow: inset 0px 0px 0px 0px black;
    background: -ms-linear-gradient(top, #484b4f 5%, #484b4f 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0.05, #484b4f), color-stop(1, #484b4f));
    background: -moz-linear-gradient(top, #484b4f 5%, #484b4f 100%);
    background: -webkit-linear-gradient(top, #484b4f 5%, #484b4f 100%);
    background: -o-linear-gradient(top, #484b4f 5%, #484b4f 100%);
    background: linear-gradient(to bottom, #484b4f 5%, #484b4f 100%);
    background-color: #484b4f;
    -moz-border-radius: 0px;
    -webkit-border-radius: 0px;
    border-radius: 0px;
    border: 0px solid black;
    display: inline-block;
    cursor: pointer;
    color: white;
    font-family: Arial;
    font-size: 15px;
    font-weight: normal;
    padding: 6px 24px;
    text-decoration: none;
    text-shadow: 0px 0px 0px black;
}
.button33k3i:hover {
    -moz-box-shadow: inset 0px 0px 0px 0px black;
    -webkit-box-shadow: inset 0px 0px 0px 0px black;
    box-shadow: inset 0px 0px 0px 0px black;
    background: -ms-linear-gradient(top, #64676b 5%, #64676b 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0.05, #64676b), color-stop(1, #64676b));
    background: -moz-linear-gradient(top, #64676b 5%, #64676b 100%);
    background: -webkit-linear-gradient(top, #64676b 5%, #64676b 100%);
    background: -o-linear-gradient(top, #64676b 5%, #64676b 100%);
    background: linear-gradient(to bottom, #64676b 5%, #64676b 100%);
    background-color: #484b4f;
    -moz-border-radius: 0px;
    -webkit-border-radius: 0px;
    border-radius: 0px;
    border: 0px solid black;
    display: inline-block;
    cursor: pointer;
    color: white;
    font-family: Arial;
    font-size: 15px;
    font-weight: normal;
    padding: 6px 24px;
    text-decoration: none;
    text-shadow: 0px 0px 0px black;
}
#taskbar {
    border: 0px;
    box-shadow: 0px 0px 0px;
    height: 30px;
}
#openButton{
    margin: 5px;
    height: 20px;
    width: 60px;
    font-size: 15px;
}
.taskbarIcon{
    height: 25px;
    display: none
}
#time{
    position: absolute;
    line-height: -10px;
    bottom: -6px;
    background: rgba(0,0,0,0);
    border: 0px black solid;
    box-shadow: 0px 0px 0px;
    text-align: center;
}
#taskbarIcon{
    visibility: hidden;
}
#topStartmenuItem{
    width: 10px;
    height: 0px;
    visibility: hidden;
    z-index: -4000000;
}
#bottomStartmenuItem{
    width: 50px;
    height: 50px;
    left: 10px;
    top: 200px;
    box-shadow: 0px 0px 0px;
    border: 0px;
    background: rgba(0,0,0,0);
}
#format{
    width: 100px;
    left: -10px;
    background: rgba(0,0,0,0);
    font-size: 16px;
    border: 0px;
    color: white;
}
}
