css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("4chan.org"){

/* Position of shit, remove crap. A more compact 4chan. */
#boardNavDesktop{width:50%!important;}
.boardTitle,.boardSubtitle,.globalMessage,.postingMode{width:50%!important;text-align:left!important;}
#postForm{position:absolute!important;float:right!important;width:50%!important;top:5px!important;right:0!important;}
.globalMessage{width:49%!important;text-align:left!important;float:left!important;font-size:8px!important;padding-top:10px!important;}
.board{padding-top:200px!important;}
.thread{padding-top:60px!important;}
textarea[name="com"]{width:90%!important;}
.navLinks,#navtopr,.title,.rules,hr,div.deleteform,div.stylechanger,.absBotText,#navbotr{display:none!important;height:0px!important;visibility:hidden!important;border:0!important;}
.pagelist{position:relative!important;float:right!important;bottom:3px!important;right:5px!important;}
#recaptcha_area{width:358px!important;}
img#recaptcha_logo,img#recaptcha_tagline{display:none!important;}
/* #boardNavDesktopFoot{position:absolute!important;float:right!important;width:55%!important;right:5px!important;} */

/* moot is a faggot */
.capcode:after{content:" disregard this, I suck cocks "!important;color:#f0f!important;}

/* Actual style beyond here. */
*{font-weight:bold!important;}
a{text-decoration:none!important;}
#boardNavDesktop,#boardNavDesktopFoot{color:#d00!important;}
#boardNavDesktop a,#boardNavDesktopFoot a{color:#000!important;}
#boardNavDesktop a:hover,#boardNavDesktopFoot a:hover{color:#fff!important;}
.globalMessage a:hover,.pagelist a:hover{color:#000!important;}
.boardTitle,.boardSubtitle{color:#fff!important;}
.boardTitle a,.boardSubtitle a{color:#000!important;}
.boardSubtitle a:hover{color:#00f!important;}
.globalMessage{color:#f00!important;}
.globalMessage a{color:#b00!important;}
.pagelist{color:#d00!important;background-color:#d00!important;border:none!important;}
.pagelist a{color:#fff!important;}

#recaptcha_area{border:#FFF solid 5px!important;background-color:#000!important;}
table.postForm > tbody > tr > td:first-child{color:#fff!important;border:#FFF solid 5px!important;background-color:#000!important;}
input,textarea{color:#fff!important;background-color:#000!important;}
td.postblock,[name="name"],[name="email"],[name="sub"],[name="pwd"],td.replyhl,td.reply,td textarea{border:#FFF solid 5px!important;}
[type="file"]{outline:5px solid white!important;margin:0 0 0 5px!important;}
.fileInfo,.postInfo,.name,.dateTime,.posteruid span,.postertrip,.subject,.summary{color:#fff!important;text-decoration:none!important;}
.fileInfo a,.postNum a{color:#00f!important;text-decoration:none!important;}
.replyContainer{opacity:0.9!important;}
.op>.postMessage{color:#fff!important;background-color:#000!important;padding:10px!important;border:#fff solid 5px!important;}
.post{color:#fff!important;background-color:#000!important;}
.reply{border:#FfF solid 5px!important;}
.quote{color:#0f0!important;}
.spoiler{color:#f00!important;background-color:#f00!important}
.highlight{border:#fff dashed 5px!important;}
.linkifyplus{color:#00f!important;}
.linkifyplus:hover{color:#0f0!important;}
.spoiler{color:#000!important;}

/* Buttons and background. */
*{-moz-appearance:none!important;}

[value="Submit"],[value="Go"]{width:23px!important;height:23px!important;margin:0!important;padding:0!important;border:0!important;background:transparent url(data:image/jpg;base64,) no-repeat center top!important;text-indent:-1000em!important;cursor:pointer!important;}
[value="Previous"],[value="Delete"]{width:23px!important;height:23px!important;margin:0!important;padding:0!important;border:0!important;background:transparent url(data:image/jpg;base64,) no-repeat center top!important;text-indent:-1000em!important;cursor:pointer!important;}
[value="Next"],[value="Report"]{width:23px!important;height:23px!important;margin:0!important;padding:0!important;border:0!important;background:transparent url(data:image/jpg;base64,) no-repeat center top!important;text-indent:-1000em!important;cursor:pointer!important;}

html,body{background:#d00 url(data:image/jpg;base64,
)center no-repeat fixed!important;}

}
