@-moz-document domain("extranet.cfa.univ-lorraine.fr")
{
body
{
    background-color: #ecf0f1!important;
}

.well
{
    background-color: #bdc3c7!important;
    background-image: none!important;
    background-repeat: no-repeat!important;
    border-radius: 10px;
}

a
{
    color: #2c82c9;
}

.btn-primary,
.btn-warning,
.btn-danger,
.btn-info,
.btn-success,
.thumbnail
{
    border: none;
    border-radius: 8px;
}

.thumbnail > img,
.thumbnail a > img
{
    border: none;
    border-radius: 1000px;
}

.list-group-item
{
    border: none;
    border-radius: 5px;
    background-image: none!important;
    background-repeat: no-repeat!important;
}

.list-group
{
    -webkit-box-shadow: none;
    box-shadow: none;
}

.list-group-item-success
{
    color: #1e824c;
    background-color: #29f1c3;
}

.list-group-item.active,
.list-group-item.active:hover,
.list-group-item.active:focus
{
    color: #e4f1fe!important;
    background-color: #2c3e50!important;
}

.btn-danger
{
    background-image: none!important;
    background-repeat: no-repeat!important;
    background-color: #f22613!important;
    border-radius: 100px;
}

.btn-primary
{
    background-image: none!important;
    background-repeat: no-repeat!important;
    background-color: #5333ed;
}

.btn-warning
{
    background-image: none!important;
    background-repeat: no-repeat!important;
    background-color: #d35400;
}

.btn-info
{
    background-image: none!important;
    background-repeat: no-repeat!important;
    background-color: #59abe3;
}

.btn-success
{
    background-image: none!important;
    background-repeat: no-repeat!important;
    background-color: #26a65b;
}

.nav-pills > li.active > a,
.nav-pills > li.active > a:hover,
.nav-pills > li.active > a:focus
{
    background-color: #446cb3!important;
    border-radius: 20px;
}

.panel-heading,
.nav-tabs > li.active > a
{
    color: #336e7b;
    border-bottom-color: #c5eff7!important;
    border:1px solid #c5eff7!important;
    background-color:#c5eff7!important;
    background-image: none!important;
    background-repeat: no-repeat!important;
}

nav.navbar.navbar-inverse.navbar-fixed-top,
footer.navbar.navbar-inverse.navbar-fixed-bottom.noprint
{
    background-image: none!important;
    background-repeat: no-repeat!important;
    background-color: #2c3e50!important;
}

.panel-info
{
    border-top-color: #c5eff7!important;
}

.panel
{
    background-color: #ecf0f1!important;
    border-top: none!important;
}

.panel-info
{
    border-color: #c5eff7!important;
    border-right-color: #c5eff7!important;
    border-bottom-color: #c5eff7!important;
    border-left-color: #c5eff7!important;
}

.panel-info > .panel-heading
{
    border-color: #c5eff7!important;
}

.alert-danger,
.alert-info,
.panel-warning > .panel-heading
{
    background-image: none!important;
    background-repeat: no-repeat!important;
}

.alert-danger
{
    color: #d91e18;
    background-color: #f1a9a0;
    border-color: #d91e18;
}

.alert-info
{
    color: #34495e;
    background-color: #c5eff7;
    border-color: #34495e;
}
}

