css:
@-moz-document url-prefix("https://2ch.hk"), regexp("https://2ch.*") {
:root {
	--theme_default_border:#ccc;
	--theme_default_text:#333;
	--theme_default_alttext:#333;
	--theme_default_btnborder:#ccc;
	--theme_default_altbtnbg:#fff;
	--theme_default_btnbghover:#ddd;
}

html, body {font-size: 16px; font-family: 'Trebuchet MS', 'Tahoma', sans-serif;}

.boardname a {text-decoration: none;}
a, .a-link-emulator {text-decoration: underline;}
a:hover, .a-link-emulator:hover {color: #06f;}

header {padding: 0 5px;}

.adminbar {font-size: .8em;}
.adminbar a {font-weight: bold;}
.adminbar__menu {border-bottom: none; float: right;}
.adminbar__boards_sticky {display: none;}
.adminbar__boards_hovered {transform: none;}
.adminbar-top .adminbar__boards {text-align: right;}
.adminbar-top a[href$="/m.html"], .adminbar-top a[href$="/contacts.html"], .adminbar-top #nsfw, .adminbar-top a[href$="/catalog.html"], .adminbar-top a[href$="/donate.html"], .adminbar-top a[href$="/market.html"], .adminbar-top a[href$="/makaba/makaba.fcgi?task=images_feed"], .adminbar-top a[href$="/makaba/makaba.fcgi?task=images_adult_feed"], .adminbar-top a[href$="/abu/res/42375.html"], .adminbar-top a[href$="/arch/"] , .adminbar-top a[href$="/boardrequest.html"], .adminbar-top a[href$="/makaba/stickers/"], .adminbar-top a[href$="/price.html"], .adminbar > hr, .logo > br, .logo img, #plashque, .board-banner, #mobile-plashque, .moneymoneymoney, body.replypage .reply .reflink::before, .postpanel, .post__detailpart.desktop, .post__number {display: none;}

hr {border-color: #bbb;}
.norm-reply + hr {display: none;}

.boardname {text-align: left;}
.logo {text-align: left; font-family: 'Trebuchet MS', 'Tahoma', sans-serif;}
#title:before {content: "Два.ч —"!important;}

.nav-arrows, .na, .search {display: none !important;}

.cntnt__left {line-height: 1.5;}
.fm {font-size: 13px;}
.fm__header {background: #ddd; border: 1px solid #ccc; border-radius: 5px; -moz-border-radius: 5px; -webkit-border-radius: 5px; padding: 0 0 0 .5em; margin-bottom: 0;}
.fm__header:hover {background: #ddd;}
.fm__sub {padding-left: 0;}
.fm__sub li {border-left: 1px solid #ccc; padding-left: .5em; margin-left: .5em;}

.alert {border: 1px solid #ccc; border-radius: 5px;}
.oppost-wrapper {overflow: unset;}
.reply, .post_type_reply {border-radius: 5px; -moz-border-radius: 5px; -webkit-border-radius: 5px; margin-left: 0; min-width: 20em;}
.post_type_reply::before {display: none;}
.reply.hiclass, .post_type_highlight {border: 2px dashed #e60;}
.post__details {font-size: 1em;}
.reflink, a.post__reflink {font-size: .8em; font-weight: bold;}
.post .post-title {font-size: 1.2em;}
.post .file-attr, .post__file-attr {font-size: .8em;}
.post .file-attr .filesize, .post__filezise {font-style: italic;}
.post .post-message, .post__message {margin: .5em;}
.post .image, .post__image {margin: 0 0 0 17px;}
.post .images-single + blockquote {margin: 0 10px 10px;}

.new-posts-marker .post {border-bottom: 2px solid #f00 ;}
.reply-posts-marker .post, .post_type_replied {border-left: 2px dashed #f00;}
.watched-posts-marker .post, .post_type_watched {border-left: 2px solid #f00;}

.ABU-refmap, .post__refmap {font-style: italic;}

.qr {border: 1px solid #bbb; border-radius: 5px; padding: 3px;}
.qr .qr-header, .qr__header {background: none; border: none; font-weight: bold;}
.qr-reply .qr-vk-public, .rules-area, .kupi-passcode-suka, .rules {display: none;}
.qr-reply .captcha-image img {image-rendering: unset;}

#postform {display: block !important;}
header .reply-label-wrapper, footer .norm-reply {display: none;}

.postform-hr, .prerekl-hr {border-top: 1px solid #eee;}
#postform .mail input[type="text"] {width: 278px;}
#postform td.label {color: #002244; font-size: 16px;}
#postform td #submit {margin-left: 4px;}
#postform td .captcha-box #captcha-value {margin: 4px 0px;}
#CommentToolbar, #qr-CommentToolbar {display: none;}
.form-files-box .form-files-limits, .filer__limits {font-size: 13px;}

textarea {font-family: monospace;}

.literally .horz-toolbar, .literally .lc-picker {background: none;}

.form-files-box .form-files-input-multi, #formimages {display: unset;}
.form-files-box .form-files-drag-area, .filer__drag-area {display: none;}

.bb {border-radius: 5px 5px 0 0}
.bb.favorites, .bb_type_favorites {right: 10px;}
.bb.boardstats, .bb_type_boardstats {display: none !important;}
.nb, .nb + hr {display: none;}

footer .moneymoneymoney + hr, .footer .board-info, .footer__info {display: none;}

@media only screen and (max-width: 480px) and (orientation:portrait),  only screen and (max-width: 540px) and (orientation:landscape){
body {font-size: .9em;}
header, .cntnt {padding: 0;}
header > div, footer > div {padding: 0 5px;}
.adminbar {margin-bottom: 35px; padding: 0;}
.adminbar__boards {z-index: 970;}
.menu {z-index: 980;}
.rmenu {display: none;}
.post {padding: 0;}
.post-wrapper {padding: 0 5px;}
.oppost-options-mob {float: left; width: 100%;}
.ABU-refmap {padding: 1px 3px 3px;}
#ABU-alertbox {min-width: 160px;}
#postform table {width: 100%; padding: 0 5px;}
}
}
