css:
@-moz-document domain(anitokyo.tv)
{
body {
    font-size: 12px;
    font-family: 'Segoe ui', sans-serif;
    background: #000 url("http://images.vfl.ru/ii/1540492268/dc519f6e/23942852.jpg") no-repeat center top;
    background-size: 50%;
    color: #000;
}
a {
    color: #2fabd9;
}

#wrapper {
    width: 1224px;
    position: relative;
    margin: 0 auto;
    padding-top: 40px;
    background-color: transparent;
}
select,
input[type="text"],
input[type="password"],
input[type="datetime"],
input[type="datetime-local"],
input[type="date"],
input[type="month"],
input[type="time"],
input[type="week"],
input[type="number"],
input[type="email"],
input[type="url"],
input[type="search"],
input[type="tel"],
input[type="color"],
.uneditable-input {
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    box-shadow: none;
    -webkit-border-radius: 0;
    -moz-border-radius: 0;
    -ms-border-radius: 0;
    -o-border-radius: 0;
    border-radius: 0;
    font-size: 12px;
    color: #858585;
    border: 1px solid #D7D7D7;
    display: inline-block;
    background: #FDFDFD;
    height: 26px;
    line-height: 26px;
}
progress {
    background-color: #eae2e2;
    border: 0;
    width: 80%;
    height: 18px;
    border-radius: 9px;
}


/*==============================
           Header
           ==============================*/
           header {
            clear: both;
            height: 203px;
            margin: 0;
            position: relative;
            z-index: 5;
            padding: 230px 0 0 0;
        }
        #header_h {
            width: 1232px;
            margin: 0 0 0 -4px;
            height: 152px;
            z-index: 1;
            position: relative;
            background: url("http://images.vfl.ru/ii/1539536567/c3501736/23795258.png") no-repeat 57px 0;
        }
        header .logo {
            overflow: hidden;
            background: url("http://images.vfl.ru/ii/1540574101/132358ee/23952352.png") no-repeat 75px 97px;
            background-size: 50%;
            width: 327px;
            z-index: 1000;
            height: 300px;
            margin: -148px 0 0 105px;
            text-indent: -9999px;
        }
        header .logo a {
            display: block;
            margin-top: 114px;
            margin-left: 70px;
            width: 177px;
            height: 180px;
        }
        .rss_ico {
            display: none;
        }
        .soc_icons {
            display: none;
        }

        .header_bg_1,
        .header_bg_2 {
            position: absolute;
            z-index: -5;
        }
        .header_bg_1 {
            background: url("http://images.vfl.ru/ii/1539718328/4e6c3dc7/23831729.pvng") no-repeat;
            background-size: 70%;
            width: 446px;
            height: 320px;
            top: 159px;
            right: -95px;
        }
        .header_bg_2 {
            background: url("http://images.vfl.ru/ii/1539544530/7b26e372/23797928.pfng") no-repeat;
            background-size: 100%;
            width: 513px;
            height: 409px;
            top: 20px;
            left: 850px;
        }
        .header_search {
            width: 336px;
            height: 36px;
            overflow: hidden;
            position: absolute;
            left: 600px;
            top: 117px;
        }
        .header_search form {
            display: block;
        }
        .header_search form input[type="text"] {
            font-family: 'Segoe ui', sans-serif;
            font-size: 11px;
            color: #e1eef5;
            text-shadow: 1px 1px 0 rgba(0, 0, 0, 0.6);
            width: 220px;
            padding: 0 0 3px 15px;
            background: none;
            border: none;
            height: 32px;
            display: block;
            margin: 0 0 0 7px;
        }
        .header_search form input[type="submit"] {
            height: 26px;
            width: 79px;
            text-indent: -9999px;
            margin: 5px 9px 0 0;
            cursor: pointer;
        }
        .header_search form input[type="submit"]:hover {
            background-position: 0 -26px;
        }
        #header_b {
            margin: 0 auto;
            background: #15404d;
            width: 960px;
            /* IE10+ */
            background: linear-gradient(to bottom, #151515db 0%, #07070b 100%);

            border-radius: 10px;
        }
        #h_flow {
            list-style: none;
        }
        #h_flow > li {
            height: 54px;
            font-size: 13px;
            line-height: 50px;
            background: none;
            margin-bottom: 10px;
        }
        #h_flow > li:hover {
            background: none;
        }
        #h_flow > li a {
            color: #fff;
            height: 54px;
            display: block;
            text-shadow: 1px 1px 0 rgba(0, 0, 0, 0.57);
            text-transform: uppercase;
        }
        #h_flow > li a span {
            height: 54px;
            display: block;
            padding: 0 19px;
        }
        #h_flow > li > a:hover,
        #h_flow > li.selected > a {
            background: #000;
            background: linear-gradient(to bottom, #191822 0%, #100e26 100%);
            border-radius: 10px;
            box-shadow: none;
            text-decoration: none;
        }
        #h_flow > li.h_this a span {

            padding: 0 38px 0 18px;
        }


        /* SubMenu */
        #h_flow .h_this {
            position: relative;
        }
        #h_flow li.h_this ul {
            width: 163px;
            margin: 0;
            top: 54px;
            left: 0;
            padding: 3px 0 0 0;
            position: absolute;
            z-index: 999;
            display: none;
            background: transparent;
            -webkit-box-shadow: 0px 1px 7px 0px rgba(0, 0, 0, 0.29);
            -moz-box-shadow: 0px 1px 7px 0px rgba(0, 0, 0, 0.29);
            box-shadow: 0px 1px 7px 0px rgba(0, 0, 0, 0.29);
        }
        #h_flow li.h_this ul li a,
        #h_flow li.h_this ul li {
            background: none;
            float: none;
            height: auto;
            display: block;
            text-transform: none;
            text-shadow: none;
            border: none;
        }
        #h_flow li.h_this ul li {
            padding: 0;
            border-bottom: 1px solid #ededed;
            height: 32px;
            font-size: 11px;
            background: #ffffff;
            /* Old browsers */
            background: -moz-linear-gradient(top, #ffffff 0%, #fafafa 100%);
            /* FF3.6+ */
            background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #ffffff), color-stop(100%, #fafafa));
            /* Chrome,Safari4+ */
            background: -webkit-linear-gradient(top, #ffffff 0%, #fafafa 100%);
            /* Chrome10+,Safari5.1+ */
            background: -o-linear-gradient(top, #ffffff 0%, #fafafa 100%);
            /* Opera 11.10+ */
            background: -ms-linear-gradient(top, #ffffff 0%, #fafafa 100%);
            /* IE10+ */
            background: linear-gradient(to bottom, #ffffff 0%, #fafafa 100%);
            /* W3C */
        }
        #h_flow li.h_this ul li:last-child {
            border-bottom: 1px solid #fff;
        }
        #h_flow li.h_this ul li a {
            color: #75828a;
            line-height: 30px;
            padding: 0 5px 0 23px;

        }
        #h_flow li.h_this ul li a:hover {
            color: #3c6675;
            text-decoration: none;

        }
        .menu-2 {
            left: 163px!important;
        }
        .menu-3 {
            left: 326px!important;
        }
        .menu-4 {
            left: 489px!important;
        }


/*==============================
           Relizes
           ==============================*/
           .relizes {

            width: 0;
            height: 0;
        }
        .relizes-a {

            width: 256px;
            height: 33px;
            z-index: -1;
            text-indent: -9999px;
            top: 78px;
            right: 310px;
            position: absolute;
        }
        .relizes-a a {
            display: block;
            width: 256px;
            height: 33px;
        }
        .relizes-menu {
            display: none;
        }
        .relizes-menu::before {
            display: none;
        }
        .relizes_block {
            display: none;
        }
        .relizes_img {
            display: none;
        }
        .relizes-mess {
            display: none;
        }


/*==============================
           Content
           ==============================*/
           #contents {
            min-height: 1260px;
            margin-left: 132px;
            position: relative;
            padding: 0;
            background: none;

            box-shadow: none;
        }
        .content {
            width: 728px;
            overflow: hidden !important;
        }
        aside.lcol {
            /*     display:none; */
            width: 0;
        }

        aside.lcol .block {
            background: none;
        }
        aside.lcol .block:nth-child(1) {
            display: none;
        }

        aside.lcol .block:nth-child(2) {
            position: absolute;
            right: 132px;
            top: 280px;
        }
        aside.lcol .block:nth-child(2) .block_h {
            background: none;
            height: 51px;
            width: 190px;
            margin: 0;
            overflow: hidden;
            line-height: 47px;
            font-weight: bold;
            color: #ffffff;
            padding: 0;
            text-shadow: 1px 1px 0 rgba(0, 0, 0, 0.15);
            background: linear-gradient(to bottom, #151515db 0%, #07070b 100%);
            border-radius: 10px;
        }
        aside.lcol .block:nth-child(2) .block_c {
            border: none;

            outline: none;
        }

        #LightChat {
            width: 190px;
        }
        div.block.lc_hide {
            width: 190px;
        }
        .lc_area {
            margin-top: 5px;
            border: none;
            background: #fff;
            position: relative;
            vertical-align: baseline;
            overflow: hidden;
            border-radius: 10px;
        }
        lc_chat_list_area {
            background: linear-gradient(to bottom, #151515db 0%, #07070b 100%);
        }
        aside.lcol .block:nth-child(3) {
            position: absolute;
            right: 82px;
            top: 800px;
        }

        aside.lcol .block:nth-child(3) .block_h {
            background: none;
            height: 51px;
            width: 190px;
            margin: 0;
            overflow: hidden;
            line-height: 47px;
            font-weight: bold;
            color: #ffffff;
            padding: 0;
            text-shadow: 1px 1px 0 rgba(0, 0, 0, 0.15);
            background: linear-gradient(to bottom, #151515db 0%, #07070b 100%);
            border-radius: 10px;
            margin-bottom: 5px;
        }
        .lastcomm {
            min-height: 35px;
            position: relative;
            padding: 6px 5px 5px 5px;
            margin: 0 0 1px 0;
            background: linear-gradient(to bottom, #151515db 0%, #07070b 100%);
            border-radius: 10px;
            border-bottom: 1px solid #000;
        }
        aside.lcol .block:nth-child(3) .block_c {
            width: 190px;
            margin: 0;
        }

        aside.lcol .block:nth-child(4) {
            display: none;
        }
        aside.rcol {
            width: 265px;
        }
        .login_block {
            padding: 4px 0 4px 0;
            width: 190px;
            position: relative;
            background: linear-gradient(to bottom, #151515db 0%, #07070b 100%);
            border-radius: 10px;
            margin-left: -57px;
        }
        .login_block_c {
            background: none;
            padding: 0 6px 0 0;
            overflow: hidden;
        }

        .login_block::before {
            display: none;
        }
        .login_block_h {
            height: 35px;
            font-weight: bold;
            font-size: 12px;
            color: #4a8ba2;
            text-shadow: 1px 1px 0 rgba(0, 0, 0, 0.37);
            line-height: 31px;
            padding: 0 0 2px 0;
        }
        .login_block_h div {
            height: 39px;
            padding: 0 0 0 29px;
            overflow: hidden;
        }
        .login_block_h div span {
            color: #fff;
        }
        .is_login {
            padding: 4px 5px 0 0;
            overflow: hidden;
        }
        .zakladki {
            width: 66px;
            margin-left: 20px;
            margin-top: 10px;
            margin-bottom: 70px;
        }
        .zakladki span {
            float: left;
            height: 22px;
            display: block;
        }
        .zakladki span a {
            width: 22px;
            height: 22px;
            display: block;
        }
        .zakladki span a:hover {
            opacity: 0.85;
        }
        .zakladki span.favorite a {
        }
        .zakladki span.willlook a {
        }
        .zakladki span.watching a {
        }
        .zakladki span.watched a {
        }
        .zakladki span.postponed a {
        }
        .zakladki span.dropped a {
        }
        .is_login .ava {
            background: #2e3d42;
            padding: 2px;
            -webkit-box-shadow: 0px 1px 3px 0px rgba(0, 0, 0, 0.22);
            -moz-box-shadow: 0px 1px 3px 0px rgba(0, 0, 0, 0.22);
            box-shadow: 0px 1px 3px 0px rgba(0, 0, 0, 0.22);
            position: absolute;
            right: 10px;
            top: 50px;
            border-radius: 3px;
        }
        .is_login .ava img {
            width: 64px;
            border: 1px solid #2e2e2e;
        }
        .is_login ul {
            width: 176px;
        }
        .is_login ul li {

            font-size: 11px;
            height: 27px;
            line-height: 23px;
            padding: 0 0 2px 0;
        }
        .is_login ul li:last-child {
            background: none;
            padding: 0;
        }
        .is_login ul li a {
            color: #e3f3f8;
            display: block;
            text-decoration: none;
            text-shadow: 1px 1px 0 rgba(0, 0, 0, 0.42);
        }
        .is_login ul li a:before {
            color: #E3F3F8;
            /* Цвет маркера */
            content: "▪";
            /* Сам маркер */
            padding-right: 10px;
            /* Расстояние от маркера до текста */
            font-size: 7px;
        }
        .is_login ul li a:hover {
            color: #4aadd0;
        }
        .is_login ul li a span {
            background: #263b41;
            padding: 1px 8px 3px 8px;
            margin: 1px 0 0 2px;
            border-radius: 2px;
        }
        .auth {
            padding: 7px 7px 7px 4px;
            overflow: hidden;
        }
        .auth label {
            display: block;
            background: #141c1f;
            height: 30px;
            margin: 0 0 8px 0;
            -webkit-box-shadow: inset 1px 1px 1px 0px rgba(0, 0, 0, 0.24), 1px 1px 0px 0px rgba(26, 38, 42, 1);
            -moz-box-shadow: inset 1px 1px 1px 0px rgba(0, 0, 0, 0.24), 1px 1px 0px 0px rgba(26, 38, 42, 1);
            box-shadow: inset 1px 1px 1px 0px rgba(0, 0, 0, 0.24), 1px 1px 0px 0px rgba(26, 38, 42, 1);
        }
        .auth label span {
            width: 40px;
            font-size: 10px;
            text-transform: uppercase;
            color: #4aadd0;
            display: block;
            float: left;
            height: 30px;
            line-height: 28px;
            padding: 0 4px 0 9px;
            overflow: hidden;
            text-shadow: 1px 1px 0 rgba(0, 0, 0, 0.4);
        }
        .auth label input {
            width: 130px;
            font-family: 'Segoe ui', sans-serif;
            display: block;
            overflow: hidden !important;
            background: none;
            border: none;
            outline: none;
            height: 29px;
            text-shadow: 1px 1px 0 rgba(0, 0, 0, 0.4);
            color: #fff;
        }
        .auth .lcols {
            overflow: hidden !important;
        }
        .auth button[type="submit"] {
            font-family: 'Segoe ui', sans-serif;
            width: 58px;
            cursor: pointer;
            border: none;
            outline: none;
            color: #fff;
            background: #63bbe2;
            /* Old browsers */
            background: -moz-linear-gradient(top, #63bbe2 0%, #34a0c1 100%);
            /* FF3.6+ */
            background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #63bbe2), color-stop(100%, #34a0c1));
            /* Chrome,Safari4+ */
            background: -webkit-linear-gradient(top, #63bbe2 0%, #34a0c1 100%);
            /* Chrome10+,Safari5.1+ */
            background: -o-linear-gradient(top, #63bbe2 0%, #34a0c1 100%);
            /* Opera 11.10+ */
            background: -ms-linear-gradient(top, #63bbe2 0%, #34a0c1 100%);
            /* IE10+ */
            background: linear-gradient(to bottom, #63bbe2 0%, #34a0c1 100%);
            /* W3C */
            /* IE6-9 */
            margin: 0 0 0 6px;
            display: block;
            -webkit-box-shadow: inset 1px 1px 0px 0px #85d3ec;
            -moz-box-shadow: inset 1px 1px 0px 0px #85d3ec;
            box-shadow: inset 1px 1px 0px 0px #85d3ec;
            height: 30px;
            line-height: 28px;
            font-size: 12px;
            text-shadow: 1px 1px 0 rgba(0, 0, 0, 0.27);
        }
        .auth button[type="submit"]:hover {
            background: #28393f;
            /* Old browsers */
            background: -moz-linear-gradient(top, #28393f 0%, #1f2d31 100%);
            /* FF3.6+ */
            background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #28393f), color-stop(100%, #1f2d31));
            /* Chrome,Safari4+ */
            background: -webkit-linear-gradient(top, #28393f 0%, #1f2d31 100%);
            /* Chrome10+,Safari5.1+ */
            background: -o-linear-gradient(top, #28393f 0%, #1f2d31 100%);
            /* Opera 11.10+ */
            background: -ms-linear-gradient(top, #28393f 0%, #1f2d31 100%);
            /* IE10+ */
            background: linear-gradient(to bottom, #28393f 0%, #1f2d31 100%);
            /* W3C */
            -webkit-box-shadow: inset 1px 1px 0px 0px #2f444a;
            -moz-box-shadow: inset 1px 1px 0px 0px #2f444a;
            box-shadow: inset 1px 1px 0px 0px #2f444a;
        }
        .auth a {
            color: #ffffff;
            font-size: 11px;
            text-shadow: 1px 1px 0 rgba(0, 0, 0, 0.34);
        }
        .auth a:hover {
            color: #4aadd0;
        }

        aside.rcol .block {
            width: 254px;
            padding: 0 0 0 0;
            margin: 0 0 0 1px;
            display: none;
        }
        aside.rcol .block .block_h {
            height: 50px;
            clear: both;
            overflow: hidden;
            color: #4aadd0;
            font-size: 13px;
        }
        aside.rcol .block .block_h span {
            padding: 0 0 0 38px;
            height: 50px;
            display: block;
            line-height: 44px;
        }
        aside.rcol .block .block_h span.ico_4 {
        }
        aside.rcol .block .block_h span.ico_8 {
        }
        aside.rcol .block .block_h span.ico_10 {
        }
        aside.lcol .block .block_h span.ico_9 {
        }
        aside.rcol .block .block_c {
            background: #fff;
            padding: 0;
        }
        aside.rcol .block:first-child {
            margin-top: 0;
        }


        /*--- Main section ---*/
        section.lcol {
            width: auto;
            padding: 1px 5px 0 5px;
            border: 0 solid rgb(0, 0, 0);
            border-radius: 10px;
            /*     -webkit-box-shadow: inset 0px 0px 0px 0px rgba(247, 247, 247, 1); */
            /*     -moz-box-shadow: inset 0px 0px 0px 0px rgba(247, 247, 247, 1); */
            box-shadow: 0px 0px 20px 2px rgb(0, 120, 197);
            background-color: rgba(255, 255, 255, 1);
        }


        /*---speedbar---*/
        .speedbar {
            display: none;
        }



        /*---moder icons---*/
        .moder_ico {}
        .moder_ico li {

            float: left;
            height: 27px;
        }
        .moder_ico li a {
            width: 27px;
            height: 27px;
            display: block;
            margin: 0 0 0 4px;
        }
        .moder_ico li.edit,
        .moder_ico li.warn,
        .moder_ico li.del {
            text-indent: -9999px;
        }
        .moder_ico li.edit a {
        }
        .moder_ico li.edit a:hover {
            background-position: 0 -27px;
        }
        .moder_ico li.warn a {
        }
        .moder_ico li.warn a:hover {
            background-position: -54px -27px;
        }
        .moder_ico li.del a {
        }
        .moder_ico li.del a:hover {
            background-position: -81px -27px;
        }
        .moder_ico li.spam a {
        }
        .moder_ico li.spam a:hover {
            background-position: -108px -27px;
        }
        .moder_ico li.quot a {
        }
        .moder_ico li.quot a:hover {
            background-position: -136px -27px;
        }


        /*---full story---*/
        .story {

            padding: 0 0 3px 0;
        }
        .story h1.story_h {
            margin: 0;
            overflow: hidden;
            display: block;
            font-weight: normal;
            font-size: 14px;
            color: #338fb0;
            padding: 10px 12px 10px 31px;
            line-height: 14px;
        }
        .story h2.story_h {
            margin: 0;
            overflow: hidden;
            display: block;
            font-weight: normal;
            font-size: 14px;
            color: #338fb0;
            padding: 10px 12px 10px 31px;
            line-height: 14px;
        }
        .story h1.story_h a {
            color: #338fb0;
        }
        .story h1.story_h a:hover {
            color: #6c797d;
        }
        .story h1.story_h:hover {
        }
        .story .story_inf {
            position: relative;
            margin: 5px 6px;
            height: 33px;
            background: #ffffff;
            -webkit-border-radius: 4px;
            -moz-border-radius: 4px;
            border-radius: 4px;
            border-top: 1px solid #e5edf4;
            -webkit-box-shadow: inset 0px -1px 1px 0px rgba(250, 250, 250, 1), 0px 1px 3px 0px rgba(206, 224, 232, 1);
            -moz-box-shadow: inset 0px -1px 1px 0px rgba(250, 250, 250, 1), 0px 1px 3px 0px rgba(206, 224, 232, 1);
            box-shadow: inset 0px -1px 1px 0px rgba(250, 250, 250, 1), 0px 1px 3px 0px rgba(206, 224, 232, 1);
        }
        .story.two .story_inf {
            width: 323px;
            float: right;
        }
        .story .story_inf::before {
            position: absolute;
            content: '';
            width: 100%;
            height: 30%;
            top: -3px;
            z-index: -1;
            background: #f4f9fb;
            -webkit-border-radius: 4px;
            -moz-border-radius: 4px;
            border-radius: 4px;
        }
        .story_inf ul.lcol {
            margin: 7px 0 0 0;
        }
        .story_inf ul.lcol li {
            float: left;
            height: 19px;
            padding: 0 11px 0 10px;
            line-height: 15px;
            font-size: 11px;
            color: #6b7982;
            border-right: 1px dotted #D4DFE2;
        }
