        .all-anime-text {
            margin: 10px 5px 15px 5px;
            line-height: 16px;
            font-weight: bold;
            text-indent: 10px;
        }



        /*--------    Таймер обратного отсчета    --------*/
        .timer {
            position: absolute;
            bottom: 0px;
            width: 230px;
            height: 50px;
            background: rgba(0, 0, 0, 0.62);
            color: #ffffff;
            font-size: 22px;
            text-align: center;
            padding: 10px;
            z-index: 10;
        }
        .timer .title {
            font-size: 18px;
            display: block;
        }
        .story .story_c .poster-img:hover .timer,
        .story.two .poster-img:hover .timer {
            background: none;
        }



        /*--------    Лог рейтинга комментария    --------*/
        #CommentsRateLog {
            max-height: 350px;
            overflow: auto;
        }
        #CommentsRateLog table thead td {
            padding-bottom: 5px;
            border-bottom: 1px solid #ddd;
        }
        #CommentsRateLog table tbody td {
            padding: 5px 0;
        }




        /* --- Сортировка статей --- */
        .sort {
            list-style: none;
            padding: 0;
            margin: 0;
        }
        .sort > li,
        .sort {
            display: inline;
        }
        .sort > li {
            margin: 0 4px 0 4px;
        }
        .sort > li a {
            color: #353535;
        }
        .sort > li.asc a,
        .sort > li.desc a {
            color: #4aadd0;
        }
        .sort > li.asc a:after,
        .sort > li.desc a:after {
            content: "";
            background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAwAAAAsBAMAAACj90TiAAAAG1BMVEUAAAAzlOYzlOYzlOYzlOYzlOYzlOYzlOYzlOar6gzlAAAACHRSTlMAjesv90eHRuQt1XUAAABSSURBVBjTzdCxDQAhDENRSyzAEFdQ0zDB9axEgzw2DokEI1A9/S4OyAxADEcpPGlpFKWoSpEKp0Dlb6SvG2jY4Fka4tw4PqbEsDNTcb8gn/d4LPQHQDdjhblbAAAAAElFTkSuQmCC);
            display: inline-block;
            vertical-align: middle;
            margin: -1px 0 0 .4em;
            width: 6px;
            height: 11px;
            -webkit-background-size: 6px auto;
            background-size: 6px auto;
        }
        .sort > li.asc a:after {
            background-position: 0 -11px;
        }




        /*--- Таблица Персональных сообщений и лучших пользователей ---*/
        .userstop td,
        .pm td.pm_list,
        .pm td.pm_head {
            border-bottom: 1px solid #efefef;
            padding: 5px 2px;
        }
        table.pm,
        table.userstop {
            width: 100%;
            margin-bottom: 0;
        }
        table.pm select {
            width: 100px;
        }
        .userstop thead td,
        .pm td.pm_head {
            border-bottom: 1px solid #efefef;
            font-weight: bold;
        }
        table.pm .navigation {
            border-top-width: 0;
            margin: 0;
        }



        /*---Выпадающее меню кнопки редактировать---*/
        #dropmenudiv {
            padding: 10px 0;
            min-width: 140px;
            width: auto !important;
            opacity: 1 !important;
            display: none;
            font-size: .9em;
            box-shadow: 0 8px 40px -10px rgba(0, 0, 0, 0.3);
            border: 1px solid #e6e6e6;
            border-color: rgba(0, 0, 0, 0.1);
            background-clip: padding-box;
        }
        #dropmenudiv {
            background-color: #fff;
        }
        #dropmenudiv a {
            text-decoration: none !important;
            color: inherit;
            display: block;
            padding: 6px 20px;
            border: 0 none;
            white-space: nowrap;
            color: inherit;
        }
        #dropmenudiv a:hover {
            background-color: #eeeeef;
        }



        /*---Быстрый поиск на сайте---*/
        #searchsuggestions {
            z-index: 2200;
            width: 300px;
            border-radius: 2px;
            background: #fff;
            box-shadow: 0 8px 40px -10px rgba(0, 0, 0, 0.3);
            border: 1px solid #ddd;
            border-color: rgba(0, 0, 0, 0.2);
            background-clip: padding-box;
            font-size: .9em;
            -moz-box-sizing: border-box;
            -webkit-box-sizing: border-box;
            box-sizing: border-box;
        }
        #searchsuggestions a,
        #searchsuggestions span.notfound {
            clear: both;
            padding: 15px 25px;
            display: block;
            text-decoration: none;
            border-bottom: 1px solid #e5e5e5;
        }
        #searchsuggestions a {
            color: inherit;
        }
        #searchsuggestions a:hover {
            background-color: #f7f7f7;
        }
        #searchsuggestions a span {
            display: block;
            cursor: pointer;
        }
        #searchsuggestions span.searchheading {
            display: block;
            font-size: 12px;
            font-weight: bold;
            margin-bottom: .2em;
        }
        #searchsuggestions span.seperator {
            display: block;
        }
        #searchsuggestions span.seperator a {
            padding: 10px 0;
            text-align: center;
            border: 0 none;
            background-color: transparent;
            color: #919191;
        }
        #searchsuggestions span.notfound {
            padding: 15px 25px;
            display: block;
        }
        #searchsuggestions .break {
            display: none;
        }



        /*---BB Редактор---*/
        .bb-pane {
            height: 1%;
            overflow: hidden;
            padding-bottom: 5px;
            padding-left: 5px;
            margin: 0;
            height: auto !important;
            text-decoration: none;
            background-image: -webkit-gradient(linear, left 0%, left 100%, from(#FBFBFB), to(#EAEAEA));
            background-image: -webkit-linear-gradient(top, #FBFBFB, 0%, #EAEAEA, 100%);
            background-image: -moz-linear-gradient(top, #FBFBFB 0%, #EAEAEA 100%);
            background-image: linear-gradient(to bottom, #FBFBFB 0%, #EAEAEA 100%);
            background-repeat: repeat-x;
            border-radius: 3px 3px 3px 3px;
            -moz-border-radius-bottomright: 0px;
            -webkit-border-bottom-right-radius: 0px;
            -khtml-border-bottom-right-radius: 0px;
            border-bottom-right-radius: 0px;
            -moz-border-radius-bottomleft: 0px;
            -webkit-border-bottom-left-radius: 0px;
            -khtml-border-bottom-left-radius: 0px;
            border-bottom-left-radius: 0px;
            border-top: 1px solid #d7d7d7;
            border-left: 1px solid #d7d7d7;
            border-right: 1px solid #d7d7d7;
            box-shadow: none !important;
        }

        .bb-pane > b {
            margin-top: 5px;
            margin-left: 0;
            vertical-align: middle;
        }
        .bb-pane .bb-btn + .bb-btn,
        .bb-pane .bb-btn + .bb-pane,
        .bb-pane .bb-pane + .bb-btn,
        .bb-pane .bb-pane + .bb-pane {
            margin-left: -1px;
        }
        .bb-btn {
            display: inline-block;
            overflow: hidden;
            float: left;
            padding: 4px 10px;
            border: 1px solid #d4d4d4;
            -webkit-box-shadow: inset 0 1px 2px white;
            -moz-box-shadow: inset 0 1px 2px white;
            box-shadow: inset 0 1px 2px white;
            background-repeat: repeat-x;
            background-image: -webkit-gradient(linear, left 0%, left 100%, color-stop(0%, #fdfdfd), color-stop(100%, #e9e9e9));
            background-image: -webkit-linear-gradient(top, #fdfdfd, 0%, #e9e9e9, 100%);
            background-image: -moz-linear-gradient(top, #fdfdfd, 0%, #e9e9e9, 100%);
            background-image: linear-gradient(to bottom, #fdfdfd 0%, #e9e9e9 100%);
        }


        .bb-btn:hover {
            background: #e6e6e6;
            background-repeat: repeat-x;
            background-image: -webkit-gradient(linear, 50% 0%, 50% 100%, color-stop(0%, #fdfdfd), color-stop(100%, #e6e6e6));
            background-image: -webkit-linear-gradient(top, #fdfdfd, 0%, #e6e6e6, 100%);
            background-image: -moz-linear-gradient(top, #fdfdfd, 0%, #e6e6e6, 100%);
            background-image: -o-linear-gradient(top, #fdfdfd, 0%, #e6e6e6, 100%);
            background-image: linear-gradient(to bottom, #fdfdfd 0%, #e6e6e6 100%);
            -webkit-transition: box-shadow 0.05s ease-in-out;
            -moz-transition: box-shadow 0.05s ease-in-out;
            -o-transition: box-shadow 0.05s ease-in-out;
            transition: box-shadow 0.05s ease-in-out;
        }

        #vk_groups {
            display: none;
        }
.dcorearts {
display:none;
}
.footer_count {
display:none;
}
