css:
@-moz-document domain(ponyfiction.org) {
@font-face { font-family: "Pangolin script=all rev=2"; src: url("https://fonts.gstatic.com/s/pangolin/v3/cY9GfjGcW0FPpi-tWMfJ79zqiA.woff2") format("woff2"); font-style: normal; font-weight: 400; unicode-range: U+400-45F, U+490-491, U+4B0-4B1, U+2116; }
body {
    font-family: Arial, sans-serif;
    color: #a9d7b3;
    background: #dadfff url('https://i.imgur.com/QxGfBMv.jpg') repeat 0 0;
}

.page-header {
	font-family: "Pangolin script=all rev=2", "Adobe Blank";

}
.container {
    background-color: #505050;
}
a {
    color: #40AE49;
}
a:hover {
    color: #00FFAD;
}
a.authorlink, a.edit-link {
	color: #b4cfac;
}
a.authorlink:hover, a.edit-link:hover {
	color: #00FFAD;
}
.spoiler-gray:hover {
	background-color: transparent;
	color: #b4cfac;
}
#content .story-item:nth-child(2n+1), .comment-item:nth-child(2n+1), .comment-tree-loader:nth-child(2n+1) {
    background-color: #666;
}
.newsitem-listitem:nth-child(2n+1) {
	background-color: #666;
}
.newsitem-date {
	color: #e4e4e4;
}
.stories-carousel {
    background-color: white;
    background-color: rgba(45, 48, 45, 0.8);
}
.story-panel {
	background-color: #686464;
}
.breadcrumb > .active {
	color: #c7cec8;
}
.btn {
	background-image: linear-gradient(to bottom, #ccefc9, #55d286);
}
.btn-primary {
	background-color: #1d451e;
	background-image: linear-gradient(to bottom, #1b4b37, #10653b);
}
.breadcrumb > li {
	text-shadow: 0 1px 0 #5d5d5d;
}
.comment-form-content {
	background-color: #6f7d6f;
}
select, textarea, input[type="text"], input[type="password"], input[type="datetime"], input[type="datetime-local"], input[type="date"], input[type="month"], input[type="time"], input[type="week"], input[type="number"], input[type="email"], input[type="url"], input[type="search"], input[type="tel"], input[type="color"], .uneditable-input {
	color: #182f20;
}
textarea, input[type="text"], input[type="password"], input[type="datetime"], input[type="datetime-local"], input[type="date"], input[type="month"], input[type="time"], input[type="week"], input[type="number"], input[type="email"], input[type="url"], input[type="search"], input[type="tel"], input[type="color"], .uneditable-input {
	background-color: #b0b0b0;
	border: 1px solid #30572c;
}
#comment-add-link + .comment-form {
	background: transparent url('https://derpicdn.net/img/2015/2/7/823659/small.png') no-repeat 85% 10%;

}
.btn-primary:hover, .btn-primary:active, .btn-primary.active, .btn-primary.disabled, .btn-primary[disabled] {
	color: #ffffff;
	background-color: #1d451e;
}
.page-header,.nav-main,.footer {
	background-image: url('https://derpicdn.net/img/view/2018/4/10/1704433.png');
}
.page-header {
    	background-position: center 20% ;
}
@media ( min-width : 768px){
.nav-main  {
	background-position:  center 30%;
}
}
@media ( max-width : 767px) {
.nav-main {
	background-position:  center 24%;
}
}
.footer {
	background-position:  center 63%;
}
.comment-item.comment-new:nth-child(2n+1) {
	background-color: rgba(19, 96, 19, 0.7);
}
.starvote {
	background: transparent url('https://i.imgur.com/uLiDVQD.png') no-repeat;
	width: 24px;
	height: 24px;
	display: inline-block;
	background-position: 0px 0px;
	background-size: 100%;
}
.notification:nth-child(2n+1) {
	background-color: #304235;
}
.notification .comment-item:nth-child(2n+1) {
	background-color: #35353580;
	padding: 0;
}
.notification {
	background-color: #1b4827;
}
      }
