css:
@-moz-document domain("duelingnexus.com") {
html ,#game-rps-container{
	background: url(https://imgur.com/Mp8jg8D.png );
}

.game-field-zone-activate {
	content:url("https://imgur.com/vF2NmIJ.png");
}

* {
	border-radius:0px 10px 10px 10px;
}

.game-field-card{
	border-radius:0;
}

.game-field-zone {
	border-radius:0;
}

#editor-main-deck, #editor-extra-deck, #editor-side-deck {
	border:1px solid #ffffff47;	
}

#navbar-left , #navbar-right {
	position:fixed;
	z-index:5;
}

#navbar-middle {
	position:fixed;
	padding-left:35%;
	top:0%;
}

.game-selection-card {
	margin: 1vh 1vw 1vh 1vw;
	z-index: 7;
}

.game-field-card {
	z-index: 10;
}

.game-field-stats-text {
	color: #fff !important;
	z-index: 3;
}

.game-field-zone-content  {
	color: #fff;
	z-index: 5;
}

.game-card-negated {
	z-index: 5;
}

.game-field-zone-activate {
	z-index: 8;
	position: absolute;
}

#game-announce-window {
	height: 10vw;
	width: 10vw;
	overflow-y: scroll;
	z-index: 6;
}

#game-option-list {
	padding: 0.5vw
}

#game-option-list .engine-button {
	margin: 0.25vw;
	padding: 0;
	position: relative;
	bottom: 0;
}

#game-tooltip    {
	color: white ;
	font-size: 1.05vw;
	background: radial-gradient(#00000070, #0b050578);
	border: 1px dashed #000;
	padding: 1vh;
	z-index: 7;
	position: fixed;
}

#downloadDeck {
	background-color: #19660c
}

#card-description-box {
	background: url("https://imgur.com/XUYfjMk.png")
}

#game-alert-window,
#game-end-window,
#options-window,
#game-results-container,
#game-message-window,
#game-waiting-menu,
#game-selection-window,
#game-yesno-window,
#game-position-window,
#game-option-window,
#game-announce-window,
#game-announce-card-window
{
	background-color: #00000080;
	border-color: #fff;
	border-width: 15px 1px 1px 3px;
	border-radius: 15px;
}

#game-room-container {
	width: 60vw;
	min-width: 10px;
	padding-bottom: 0px;
	padding-top: 0px;
	padding-left: 0px;
	padding-right: 0px;
}

#game-room-players {
	width: 40vw;
	min-width: 28px;
	padding: 2vh 2vw 2vh 1vw;
	margin-top: 20px;
}

#game-room-information {
	width: 16vw;
	min-width: 260px;
	padding: 2vh 2vw 2vh 1vw;
}

@media (min-width: 1280px) {
#game-deck-selection {
	border: solid #fff;
	border-width: 12px 1px 1px 3px;
	border-radius: 11px;
	overflow-y: scroll;
	height: 500px;
	width: 450px;
	min-width: 120px;
	margin-top: 7px;
	padding: 5px;
	z-index: 2;
	position: absolute;
	}}

@media (min-width: 1025px) and (max-width: 1279px) {
#game-deck-selection {
	border: solid #fff;
	border-width: 12px 1px 1px 3px;
	border-radius: 11px;
	overflow-y: scroll;
	height: 300px;
	width: 450px;
	min-width: 120px;
	margin-top: 7px;
	padding: 5px;
	z-index: 2;
	position: absolute;
	}}

#game-selection-window {
	overflow-y: scroll;
	height: 20vw;
	width: 40vw;
	margin-top: 3vw;
	margin-left: 33vw;
	z-index: 6;
}

#options-area {
	position: fixed;
	right: 0.8vw;
	bottom: 25px;
}

#options-window {
	height: 200px;
	width: auto;
	overflow-y: visible;
	z-index: 10;
	right: 4vw;
	bottom: 40px;
}

#game-siding-column{
	height: 37.67vw;
	width: 33.33vw;
}

#siding-deck-main-title{
	margin: 0 0 1vw 0;
}

#siding-deck-extra-title,
#siding-deck-side-title
{
	margin: 0.5vw 0;
}

#game-siding-column-2{
	border: 1px dashed #fff;
	height: 37.67vw;
	width: 37.67vw;
}

#game-siding-done{
	height: 4vw;
	width: 100%;
	position: relative;
	top: 5vw;
}

#game-announce-window{
	overflow-y: scroll;
	height: 20vw;
	width: 20vw;
	z-index: 6;
}

.game-field-zone {
    background: 
	repeating-radial-gradient( #0000007d , #000000a6 );
    border: 3px solid #fff0;
    border-style:outset;
}

#my-profile , #home-area ,
#duel-area , #decks-area
, #gamelist-area {
	background-color:#010101b5;
}

#news-area {
	background:#000000b5;
}

.adsbygoogle {
	opacity:0;
}

body{
font-size: 14px;
}
}

@-moz-document domain("duelingnexus.com") {
/*delete this code section if you dont want a 3D game field */
#game-selection-window {
	transform:translate(-19vw , -11vh)
}

#game-yesno-window{
	transform:translate(-4vw)
}

#game-container {
	perspective:2100px;
}

#game-column{
	transform:rotatex(45deg) translatex(-20px) scale(0.9,1.02);
		
}

#game-action-menu{
	transform:translate(-27.5vw, -3vh)
}

#game-tooltip {
	transform:translatex(-27vw)
}

.game-avatar-area {
	transform: scale(1.15) 
}

#game-rps-container , #game-rps-result{
	perspective:1700px;
}

#game-rps-scissors {
	transform:rotatex(50deg) scaley(1.3);
	padding:5px
}

#game-rps-paper {
	transform:rotatex(50deg) scaley(1.3);
	padding:5px
}

#game-rps-rock{
	transform:rotatex(50deg) scaley(1.3);
	padding:5px
}
  
  #game-end-window {
	height:11vh;
}
}
