css:
/* The layout of this site is atrocious and unreadable.
  This style makes it somewhat more accessible.

  This site is BEST VIEWED WITH ALL SCRIPTING AND FLASH TURNED OFF.

  Most of the links suck but the "mystery meat" pics lead to the real content.
  Or use the "Non Flash Version" found in the "sitemap".
*/
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("superrelativity.org") {
p {
  color: black !important;
  font-size: inherit !important;
}

.nav {
  visibility: visible !important;
}

body {
  background: none !important;
  background-color: #F8F8F8 !important;
  color: black !important;
  font-size: 16px !important;
  line-height: 150% !important;
}

#shell {
  height: 100% !important;
  width: 100% !important;
}

#content {
  font-size: inherit !important;
  height: 100% !important;
  width: 700px !important;
}

.thplace {
  width: 100% !important;
}

h {font-size: 200% !important;}
h2, .title {font-size: 150% !important;}

.bold, .cbold {font-size: inherit !important;}
.btnplace input, .btnplace_g input {font-size: inherit !important;}
.clear {font-size: inherit !important;}
.date {font-size: inherit !important;}
.fieldplace .label {font-size: inherit !important;}
.fieldplace input, .fieldplace textarea {font-size: inherit !important;}
.guest_textarea textarea {font-size: inherit !important;}
.guestplace .label, .guest_textarea .label {font-size: inherit !important;}
.guestplace input {font-size: inherit !important;}
.paging {font-size: inherit !important;}
.sitemap_place a {font-size: inherit !important;}
.style1 {font-size: 16px !important;  color: black !important;}
.verCode input {font-size: inherit !important;}
.verCode span {font-size: inherit !important;}
a.readmore {font-size: inherit !important;}
a.theorylink {font-size: inherit !important;}
p {font-size: inherit !important;}
p a {font-size: inherit !important;}
p.guest {font-size: inherit !important;}
ul.links {font-size: inherit !important;}
ul.links li a {font-size: inherit !important;}
ul.links_sm {font-size: inherit !important;}
ul.links_sm li a {font-size: inherit !important;}


#content {color: black !important;}
.cbold {color: black !important;}
.date {color: black !important;}
.fieldplace .label {color: black !important;}
.fieldplace input, .fieldplace textarea {color: black !important;}
.guest_textarea textarea {color: black !important;}
.guest_textarea textarea, .guestplace input, .fieldplace input, .fieldplace textarea, .verCode input {color: black !important;}
.guestplace .label, .guest_textarea .label {color: black !important;}
.guestplace input {color: black !important;}
.paging {color: black !important;}
.verCode span {color: black !important;}
h1 {color: black !important;}
h2, .title {color: black !important;}
p {color: black !important;}


/* Make freaking links more accessible. */
.paging a {color: blue !important;}
.sitemap_place a {color: blue !important;}
a.readmore {color: blue !important;}
a.style1 {color: blue !important;}
a.style2 {color: blue !important;}
a.theorylink {color: blue !important;}
p a {color: blue !important;}
p.guest a {color: blue !important;}
ul.links li a {color: blue !important;}
ul.links_sm li a {color: blue !important;}
ul.links_sm li ul li a {color: blue !important;}

.paging a:visited {color: purple !important;}
.sitemap_place a:visited {color: purple !important;}
a.readmore:visited {color: purple !important;}
a.style1:visited {color: purple !important;}
a.style2:visited {color: purple !important;}
a.theorylink:visited {color: purple !important;}
p a:visited {color: purple !important;}
p.guest a:visited {color: purple !important;}
ul.links li a:visited {color: purple !important;}
ul.links_sm li a:visited {color: purple !important;}
ul.links_sm li ul li a:visited {color: purple !important;}


.fieldplace input, .fieldplace textarea {
  background: white !important;
}

.guest_textarea textarea, .guestplace input, .fieldplace input, .fieldplace textarea, .verCode input {
  background: white !important;
}

.links_all {
    height: 100% !important;
    width: 100% !important;
    overflow-x: auto !important;
}

.sitemap_place {
    left: 0 !important;
    margin: 2em !important;
    position: relative !important;
    text-align: left !important;
    top: 0 !important;
}

.pplace, .pplace_links {
  width: 95% !important;
}

}
