css:
@-moz-document url-prefix("http://www.jeuxvideo.com/forums/") {
.bloc-contenu {
    font-family: "Palatino", Palatino, sans-serif;
    font-size: 20px;
    font-weight: 500;
    color: #000
}

.liste-default-jv,
.topic-subject,
.topic-count {
    font-family: "Palatino", Palatino, sans-serif;
    font-size: 18px;
    color: #2b2b2b
}

.blockquote-jv {
    font-family: "Verdana"!important;
    font-size: 15px!important;
    font-weight: 600;
    color: #7a7a7a!important;
}

.topic-title,
.lien-jv {

    font-size: 16px;
    font-family: "Trebuchet MS";
    font-weight: 600;

    color: #2e3f55;
}



.img-shack {
    min-width: 100px;
    min-height: 75px;
}

.user-avatar-msg {
    min-width: 45px;
    min-height: 45px;
    padding: 0px;
}

.topic-img {
    width: 22px;
    height: 20px;
    display: block !important;
}

.titre-bloc-forum {
    font-size: 34px;
}

.jv-header-sticky,
.video-footer-container,
.fil-ariane-crumb,
.panel-jv-forum,
.forum-right-col,
.ads-middle {
    display: none!important;
    visibility: hidden!important;
}

.video-footer-container,
.external-links,
.highlight,
.bloc-boutique-droite,
.legal,
.jv-footer,
.jv-header-bottom {
    display: none!important;
    visibility: hidden!important;
}
.btn-actu-new-list-forum,
.btn-ajouter-spotify,
.btn-poster-msg,
.pagi-suivant-actif,
.btn-lancer-rech,
.js-post-topic,
.btn-annuler-modif-msg,
.btn-ajouter-sondage,
.btn-forum-modo,
.btn-select-msg-modo,
.btn-comm-actualiser,
.btn-topic-resolu,
.btn-repondre-msg,
.btn-info:hover {
    border: 0.0625rem solid #000!important;
    background: #ffdba2!important;
    color: #16205c!important;
}
.forum-main-col,
.conteneur-topic-pagi,
.content-forum,
.bloc-formulaire-forum,
.conteneur-message,
.bloc-message-forum,
.titre-head-bloc,
.jv-editor,
.bloc-pagi-default,
.bloc-outils-top,
.form-group,
.bloc-outils-bottom {
    overflow: hidden!important;

    word-break: keep-all !important;
    min-width: 1000px!important;
    width: 1130px!important;
}

.xXx.bloc-pseudo-msg.text-user {
    color: #c83900;
    font-size: 22px;
}

.topic-subject {
    overflow: hidden;
}


.topic-list,
.conteneur-topic-pagi {
    background-color: #0000000d!important;
}



.content-forum,
.forum-main-col {
    background: none!important
}
}
