/*
 * Name:			Ciemny Forbot
 * Author:			Atx
 * Version:			v1.0
 * Latest update:	27.10.2018
 */

@-moz-document domain("forbot.pl") {
.article,
.container,
.list-group-item,
#navbar__top,
.ipsComment,
.ipsDataList > li:nth-child(2n),
#ipsLayout_header,
.ipsNavBar_secondary,
.ipsMenu_item,
.ipsNavBar_primary > ul > li.ipsNavBar_active > a,
.ipsBox:not(.ipsBox_transparent):not(.ipsModerated),
.article .card-advertise,
.dropdown-item:nth-child(2n)
{
	background-color: #2C2C2C !important;
	color: #AAA;
}

.list-group-item:hover,
.ipsQuote,
.ipsAreaBackground,
.ipsDataList > li:nth-child(2n+1),
ul:not(.ipsMenu_keyNav) .ipsMenu_item:not(.ipsMenu_itemClicked):not(.ipsMenu_itemDisabled) a:not(.ipsMenu_itemInline):hover,
.ipsMenu_item[data-selected] > a,
.ipsMenu_item[data-selected] span,
.ipsMenu_item.ipsMenu_hover,
div.ipsType_richText.ipsContained > div,
.dropdown-item:nth-child(2n+1)
{
	color: #AAA;
	background-color: #3C3C3C !important;
}

.article > .post > nav > ol.breadcrumb,
.article > .card-deck > div >.card,
#navbarNav > ul.navbar-nav li.dropdown.show .dropdown-menu
{
	background: #1C1C1C !important;
}

.sticky-top div.card
{
	background: none !important;
}

.crayon-code > .crayon-pre > .crayon-line:nth-child(2n)
{
	background: #0C0C0C !important;
}

.crayon-code > .crayon-pre > .crayon-line:nth-child(2n+1)
{
	background: #1C1C1C !important;
}

.crayon-theme-classic .crayon-toolbar
{
	background: #0A0A0A !important;
	border-bottom-color: #2C2C2C !important;
}

.crayon-nums > .crayon-nums-content > .crayon-num:nth-child(2n),
.dropdown-item:hover:nth-child(2n)
{
	background-color: #4095bf !important;
	color: #0A0A0A !important
}

.crayon-nums > .crayon-nums-content > .crayon-num:nth-child(2n+1),
.dropdown-item:hover:nth-child(2n+1)
{
	background-color: #79b5d2 !important;
	color: #0A0A0A !important
}

.crayon-theme-classic
{
	border-color: #4C4C4C !important;
}

.crayon-theme-classic .crayon-pre .crayon-sy
{
	color: #CCC !important;
}

.crayon-theme-classic .crayon-pre .crayon-c
{
	color: #57a64a !important;
}

.crayon-theme-classic .crayon-pre .crayon-st,
.crayon-theme-classic .crayon-pre .crayon-k
{
	color: #ff8000 !important;
}

.crayon-theme-classic .crayon-pre .crayon-v
{
	color: #0af !important;
}

.crayon-theme-classic .crayon-pre .crayon-p
{
	color: #00979d !important;
}

.crayon-theme-classic .crayon-pre .crayon-t,
.crayon-theme-classic .crayon-pre .crayon-r
{
	color: #AAA !important;
}

.crayon-theme-classic .crayon-pre .crayon-o
{
	color: #bf80ff !important;
}

.crayon-theme-classic .crayon-pre .crayon-s
{
	color: #d6ca6a !important;
}

.crayon-theme-classic .crayon-pre .crayon-e
{
	color: #1363ec !important;
}

.alert-primary,
.alert-info
{
	border: 1px solid #637483 !important;
	background: #21272c !important;
	color: #CCC !important;
}

.ipsPagination:not(.ipsPagination_mini) .ipsPagination_page a,
.ipsPagination:not(.ipsPagination_mini) .ipsPagination_next a,
.ipsPagination:not(.ipsPagination_mini) .ipsPagination_prev a,
.ipsPagination:not(.ipsPagination_mini) .ipsPagination_first a,
.ipsPagination:not(.ipsPagination_mini) .ipsPagination_last a
{
	background-color: #4C4C4C !important;
}

.ipsPagination .ipsPagination_page.ipsPagination_active a
{
	background-color: #3e4148 !important;
}

body, .ipsQuote_citation
{
	background-image: none;
	background-color: #1C1C1C;
}

#ipsLayout_header nav
{
	background: none !important;
}

.post p,
ul.menu-panel li a,
div.sidebar-right-promote p a,
a.main-menu-link,
.article,
.ipsContained,
.ipsType_light span,
nav.ipsBreadcrumb > ul > li,
span.ipsType_normal,
.ipsQuote_citation
{
	color: #AAA !important;
}

a
{
	color: #6084b4 !important;
}

a.btn,
.article > div > div > span.post__category.badge > a,
.article > .card-deck > div > .card > a.card-title,
.flex-column.align-items-stretch > .align-items-center > a.justify-content-center
{
	color: #0A0A0A !important;
}

.ipsType_sectionHead.cAuthorPane_author,
.ipsType_pageTitle,
.ipsType_sectionHead,
body,
.article > p > span,
.article > h2 > span
{
	color: #CCC !important;
}

a.main-menu-link.hot
{
	color: #A51D22 !important;
}

div.box-blue p,
div.box-green p,
div.box-orange p,
div.box-red p
{
	color: #1C1C1C !important;
}

.icon-newspaper
{
	filter: invert(100%) !important;
}

.ipsBox,
.ipsQuote,
.ipsDataList > li,
.ipsMenu
{
	border: 1px solid #4C4C4C !important;
}

.dropdown-divider
{
	border-top: 1px solid #4C4C4C !important;
}

.ipsMenu:not(.ipsMenu_noStem)::before,
.ipsMenu:not(.ipsMenu_noStem)::after
{
	border-color: transparent transparent #3C3C3C transparent !important;
}

.crayon-toolbar > .crayon-tools > .crayon-button
{
	filter: invert(100%) !important;
}
}
