css:
@-moz-document url-prefix("https://www.bilibili.com") {
body {
	background-color:#f9f9f9f9;
}

.nav-menu {
	margin:auto;
}

.nav-menu>div.bili-wrapper {
	width:80%;
	margin:auto;
}

.head-logo {
	margin-left:10%;
}

.blur-bg {
   background-image: none !important; 
	filter:none !important;
	background-color:transparent;
}

#banner_link {
	background-image: none !important; 
	background-color:transparent;
}

.nav-mask {
    background-color:rgba(0,0,0,0.2) !important;
    box-shadow:none !important;
}

a.t {
	color:#fff !important;
}

li[report-id="playpage_download"] {
	display:none;
}

.bili-header-m {
	box-shadow:0 2px 2px 0 rgba(0, 0, 0, 0.14),0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2);
   background-color:#e00;
   background-image: url("https://i0.hdslb.com/bfs/archive/c694a5cc0761ffb2965a0a87954ede38d7e5770c.png");
   background-size:cover;
   background-position:center;
   border-bottom: 2px solid #0cf;
   margin-bottom:30px;
}

.num-wrap {
	display:none;
}

.bili-header-m .bili-wrapper {
	width:100%;
}

.bili-wrapper {
	width:1250px;
}

#primary_menu {
	height:32px !important;
	padding:0 !important;
   border-bottom: none;
   margin:10px 0 0px 0 !important;
   width:100%;
   display:flex;
   justify-content:  space-around;
   background-color:rgba(0,0,0,0.3);
}

#primary_menu>.nav-menu {
	display:flex;
   justify-content:  space-around;
   height:32px;
   width:80%;
}
#primary_menu>.nav-menu li {
   margin:0;
}

.primary-menu .nav-menu li.home>a {
	background-image:none !important;
}

.primary-menu .nav-menu>li>a:not(.side-link) .nav-name {
	    top:0  !important;
    	height:0 !important;
    	padding-top: 0px;
    	line-height: 18px;
    	color:#fff !important;
}

li.side-nav {
	display:none !important;
}

.nav-gif {
    display:none !important;
}

.search {
	margin-right:10%;
    background-color:transparent !important;
}

.searchform {
	background-color: transparent !important;
	border-bottom: 2px solid #0cf;
	margin-left:30px !important;
   background-color:rgba(255,255,255,0.6) !important;
}

.link-ranking {
	background-color:rgba(255,255,255,0.6) !important;
	padding:0 10px 0 10px;
    margin-right:50px !important;
}

.u-link {
    height: 100% !important;
    background-color: transparent !important;
}

.panel,.home-card {
	box-shadow:0 4px 5px 0 rgba(0, 0, 0, 0.14),
                    0 1px 10px 0 rgba(0, 0, 0, 0.12),
                    0 2px 4px -1px rgba(0, 0, 0, 0.4)
}

li {
	overflow:visible !important;
}

.zone-wrap-module {
	background-color:#fff;
	padding:20px;
    border-radius:5px;
    margin-bottom:20px;
    box-shadow:0 4px 5px 0 rgba(0, 0, 0, 0.14),0 1px 10px 0 rgba(0, 0, 0, 0.12), 0 2px 4px -1px rgba(0, 0, 0, 0.4);
}

.zone-wrap-module:hover {
	box-shadow:0 8px 10px 1px rgba(0, 0, 0, 0.14), 0 3px 14px 2px rgba(0, 0, 0, 0.12),0 5px 5px -3px rgba(0, 0, 0, 0.4);
}

.elevator-module {
    margin-left: 0;
    left:calc(80% + 100px);
}

.recommend-module {
	left:7%;
}

* {
	transition:0.25s ease;
}

.app-download {
	display:none;
}

.r-text>a.t {
	color:#000 !important;
}

.bili-footer .footer-wrp {
	box-shadow:0 4px 5px 0 rgba(0, 0, 0, 0.14),0 1px 10px 0 rgba(0, 0, 0, 0.12), 0 2px 4px -1px rgba(0, 0, 0, 0.4);
	background-color:#fff !important;
    margin-bottom:20px;
}

.back-top {
	border-radius:25px !important;
	border:none !important;
	background-color:#fff !important;
	box-shadow:0 4px 5px 0 rgba(0, 0, 0, 0.14),0 1px 10px 0 rgba(0, 0, 0, 0.12), 0 2px 4px -1px rgba(0, 0, 0, 0.4);
	width:48px;
	position:relative;
	left:-8px;
}

.back-top:hover {
	background-color:#0cf !important;
	box-shadow:0 8px 10px 1px rgba(0, 0, 0, 0.14), 0 3px 14px 2px rgba(0, 0, 0, 0.12),0 5px 5px -3px rgba(0, 0, 0, 0.4);
}


.s-line {
	display:none
}

.elevator-module>div.nav-list {
	margin-bottom:10px;	
	overflow:visible;
    border:none;
    background-color:transparent;
}

.elevator-module>div.nav-list>div.item {
	margin-bottom:10px;
	height:32px;
	width:32px;
	border-radius:24px;
	background-color:#0cf;
	color:#fff;
	line-height:32px;
	box-shadow:0 4px 5px 0 rgba(0, 0, 0, 0.14),0 1px 10px 0 rgba(0, 0, 0, 0.12), 0 2px 4px -1px rgba(0, 0, 0, 0.4);
}

.elevator-module>div.nav-list>div.item:hover {
    background-color:#fff !important;
	box-shadow:0 8px 10px 1px rgba(0, 0, 0, 0.14), 0 3px 14px 2px rgba(0, 0, 0, 0.12),0 5px 5px -3px rgba(0, 0, 0, 0.4);
    color:#000;
}

.customize {
	display:none;
}
}
