css:
@-moz-document url-prefix("http://www.jeuxvideo.com/") {
 .conteneur-message {
     font-family: "MERRIWEATHER", MERRIWEATHER, sans-serif;

     font-size: 22px;
     color: #000
 }

 .bloc-pagi-default {
     background-color: #ff5b00;
 }

 .topic-author {
     font-size: 12px;
     font-weight: 600;
     text-align: right;
 }
 .topic-title,
 .lien-jv {

     font-size: 16px;
     font-family: "Trebuchet MS";
     font-weight: 600;
     color: #f00;
 }

 .blockquote-jv {
     color: #494949!important;
     font-size: 15px!important;
 }

 .bloc-header {
     min-width: 30px;
     min-height: 100px;
 }


 .container-content {
     background-color: #004365;

     background-size: cover;
     border: none;
     background-attachment: fixed;
     outline: 5000px solid #004365;
 }

 textarea > Placeholder {
     visibility: hidden!important;
 }

 a,
 li a {
     color: #11356b!important;
 }

 a,
 .lien-jv,
 .liste-default-jv li a {
     color: #000;
 }

 *::-webkit-scrollbar {
     width: 10px ! important;
     height: 10px ! important;
     background: #1e1e1e ! important;
     border: 1px solid #1e1e1e ! important;
     display: none;
 }






 .bloc-message-forum:nth-of-type(odd) {
     background: #dbd8c7!important;
     border: 3px solid #000;
 }

 .bloc-message-forum:nth-of-type(even) {
     background: #d9e6da!important;
     border: 3px solid #000;
 }

 .logo {
     visibility: hidden;
 }

 .logo:before {
     content: "ELITE FORUM";
     font-size: 30px;
     font-family: arial;
     font-weight: 900;
     color: #ff5b00;
     visibility: visible!important;
 }



 .titre-affli-ligne-text,
 .bloc-chev-pix.bloc-chev-pix-link span,
 .lien-chev,
 .bloc-default-profil .foot-link a:before,
 .bloc-default-profil .foot-link a span,
 .bloc-lien-historique-sanction-profil a {
     color: #c8c8c8!important;
 }

 .content-forum-topic-populaire .topic-list .topic-head {
     background-color: #1A1A1A !important;
 }

 .btn-actu-new-list-forum,
 .btn-ajouter-spotify,
 .btn-poster-msg,
 .pagi-suivant-actif,
 .btn-lancer-rech,
 .js-post-topic,
 .btn-annuler-modif-msg,
 .btn-ajouter-sondage,
 .btn-forum-modo,
 .btn-select-msg-modo,
 .btn-comm-actualiser,
 .btn-topic-resolu,
 .btn-repondre-msg,
 .btn-info:hover {
     border: 3px solid #ff5b00!important;
     background: #004365!important;
     color: #fff!important;
     font-size: 13px;
 }


 .form-harassment {
     visibility: hidden;
 }

 .form-harassment:after {
     content: "JV Light, powered by Thanositas";
     color: white;
     visibility: visible;
 }

 .bloc-message-forum {
     background-color: #cce0ec;
 }

 .img-shack {
     min-width: 100px;
     min-height: 75px;
     margin: -3.2px;
     font-size: 0;
     white-space: nowrap;
 }

 .topic-img {
     width: 22px;
     height: 20px;
     display: block !important;
 }

 .titre-bloc-forum,
 .titre-bloc {
     font-size: 34px;
     color: white;
 }

 .jv-header-sticky,
 .video-footer-container,
 .fil-ariane-crumb {

     visibility: hidden!important;
 }
 .panel-jv-forum,
 .forum-right-col {
     position: relative;
     left: 40000px;
 }

 .topic-list li:nth-of-type(even),
 .topic-list .ads-middle ~ li:nth-of-type(odd) {
     background: #e0e0e0;
 }
 .topic-list li:nth-of-type(odd),
 .topic-list .ads-middle ~ li:nth-of-type(even) {
     background: #c4c4c4;
 }
 .topic-list li:hover,
 .topic-list .ads-middle ~ li:nth-of-type(even):hover,
 .topic-list .ads-middle ~ li:nth-of-type(odd):hover {
     background: #ff3d00;
 }

 .nb-connect-fofo {
     position: relative;
     right: 40900px;
     bottom: 95px;
     font-size: 30px!important;
 }


 .bloc-mp-pseudo {
     position: relative;
     left: 750px;
 }
 .xXx.bloc-pseudo-msg.text-user {
     margin: -2px;
     font-size: 30px!important;
     overflow: visible;
     font-variant: small-caps;
 }

 .xXx.bloc-pseudo-msg.text-user:before {
     content: "       ";
 }
 .bloc-date-msg {
     position: relative;
     margin-top: 10px;
 }

 .bloc-date-msg:before {
     content: "              ";
 }

 .video-footer-container,
 .external-links,
 .highlight,
 .bloc-boutique-droite,
 .legal,
 .jv-footer,
 .jv-header-bottom,
 .panel-body {

     visibility: hidden!important;
     display: none!important;
 }

 .header-affix {
     display: none!important;
     visibility: hidden!important;
 }

 .user-avatar-msg {
     min-height: 80px;
     min-width: 80px;
 }


 .page-active {
     font-size: 23px!important;
     font-weight: 600;
 }
 .page-active:before {

     content: 'Page ';
 }

 .forum-main-col,
 .form-group,
 .conteneur-topic-pagi,
 .content-forum,
 .bloc-formulaire-forum,
 .conteneur-message,
 .bloc-message-forum,
 .titre-head-bloc,
 .jv-editor,
 .bloc-pagi-default,
 .bloc-outils-top,
 .bloc-sondage,
 .bloc-outils-bottom {
     overflow: hidden!important;

     word-break: keep-all !important;
     min-width: 1000px!important;
     width: 1130px!important;
 }

 .xXx.bloc-pseudo-msg.text-user {
     color: #ff5b00;
     font-size: 23px;
 }

 .bloc-sondage,
 .intitule-sondage {
     background-color: #1a1a1a;
     color: black;
     font-size: 15px!important;
 }

 .tab-choix {
     color: white!important;
 }


 .topic-subject {
     overflow: hidden;
 }




 .li {
     min-width: 50px;
 }



 .content-forum,
 .forum-main-col {
     background: none!important
 }
}
