css:
@-moz-document domain(mail.tutanota.com) {

	body{
		color: #707070;
		background-color: #232323;
	}

	div#mail-body {
		color: #707070;
	}

	.folder-column {
		background-color: #232323;
		border-right: 1px solid #1e1e1e;
	}

	.header-nav {
		background-color: #232323;
		height: 40px;
		box-shadow: none;
	}

	.main-view {
		top: 40px;
	}
	
	.odd-row {
		background-color: #1e1e1e  !important;
	}
	
	.list-accent-fg {
		color: #840010;
		text-shadow: none;
	}
	
	.list-row {
		background-color: #222222;
	}
	
	.list-border-right {
		border-right: none;
		background-color: #232323;
	}
	
	[class="view-column fill-absolute backface_fix z1"] {
		background-color: #232323;
	}
	
	.content-message-bg {
		background-color: #1e1e1e;
		color: #707070;
	}
	
	.list-message-bg {
		background-color: #1e1e1e;
		color: #707070;
	}
	
	.list {
		color: #707070;
	}
	
	.row-selected {
		border-color: #840010 !important;
		color: #a24343;
		background-color: #342121 !important;
		text-shadow: 0px 0px 4px #84001057;
	}
	
	.hr {
		background-color: #482a2a;
	}
	
	.list-accent-fg {
		color: #867575;
		text-shadow: none;
		font-weight: 600;
	}
	
	.flex-column-reverse {
		color: #a24343;
	}
	
	[class="subject text-break selectable"] {
		color: #bf747d;
	}
	
	blockquote.tutanota_quote {
		border-left: 3px double #6d262e;
	}
	
	
	.red, .blue {
		display: none;
	}
	
	.nav-bar-spacer {
		background-color: #707070;
	}
	
	.bubble {
		border: 7px solid #232323;
		background-color: #707070;
	}
	
	small, .small {
		color: #868686;
		text-shadow: 0px 0px 5px #535353;
	}
	
	
	.list-alternate-background {
		background:none;
	}
	
	.content-bg {
		background-color: #1b1b1b !important;
		color: #707070;
	}
	
	.dialog-buttons > button:not(:first-child) {
		border-left: 1px solid #2b2b2b !important;
	}
	
	.dialog-buttons {
		border-top: 1px solid #2b2b2b !important;
	}
	
	button {
		color: #707070 !important;
	}
	
	
	.inputWrapper > .input {
		color: #707070;
	}
	
	
	.dropdown-panel {
		background-color: #222222;
	}
	
	div.text-ellipsis.b {
		color: #d26262;
	}
	
	
	div[class="wrapper flex items-center"] > [class="pl content-fg"] {
		color: #707070;
	}
	
	[class="checkbox pt click"] > small[class="block content-fg"] {
		color: #707070;
	}
	
	
	/* Scrollbar width */
	::-webkit-scrollbar {
		width: 6px;
	}
	
	/* Scrollbar Track */
	::-webkit-scrollbar-track {
		background: #333; 
	}
	
	/* Scrollbar Handle */
	::-webkit-scrollbar-thumb {
		background: #888; 
	}
	
	/* Scrollbar Handle on hover */
	::-webkit-scrollbar-thumb:hover {
		background: #555; 
	}
}
