css:
@-moz-document url(https://www.wanikani.com/lesson/session) {
    /* Radicals */
    header:not(.quiz) > #main-info.radical > #meaning:not(:hover),
    #supplement-rad-name-mne > .highlight-radical:not(:hover),

    /* Kanji */
    header:not(.quiz) > #main-info.kanji > #meaning:not(:hover),
    #supplement-kan-meaning-mne > .highlight-kanji:not(:hover),
    #supplement-kan-reading-mne > .highlight-reading:not(:hover),
    #supplement-kan-reading-mne > span[lang="ja"]:not(:hover),
    #supplement-kan-meaning > .pure-g-r > .pure-u-1-4 > div:not(:hover),
    #supplement-kan-reading > .pure-g-r > .pure-u-1-4 > div > span:not(:hover),

    /* Vocabulary */
    header:not(.quiz) > #main-info.vocabulary > #meaning:not(:hover),
    #supplement-voc-synonyms:not(:hover),
    .highlight-vocabulary:not(:hover),
    .highlight-reading:not(:hover),
    #supplement-voc-reading > .pure-g-r > .pure-u-1-4 > div[lang="ja"]:not(:hover),
    #supplement-voc-reading-exp > span[lang="ja"]:not(:hover) {
        background-color: #232323;
        color: #232323;
        text-shadow: none;
        
        border-radius: 5px;
    }
}
