css:
@-moz-document domain("d3.ru") {
.react img{
	filter:blur(50px);
}
.b-footer_left,.b-footer_links,.b-footer_right,.b-gertruda IMG,.b-sidebar,.b-user_ad_container{
    display:none
}
BODY .l-wrapper_article{
    background-color:#FFF
}
.post{
    background-color:#FFF;
    border:1px solid #D8D8D8;
    border-bottom-width:2px;
    border-left-width:0;
    border-right-width:0;
    vertical-align:top;
    margin-bottom:10px;
    padding:0!important
}
.b-post_preview_pic{
    width:100%
}
.post .dt IMG{
    max-width:none
}
.post .b-post_cover{
    display:block;
    max-height:none;
    max-width:none;
    overflow:hidden;
    position:relative;
    width:100%
}
.post .dt .post_body{
    display:inline-block
}
.post_body_text{
    padding:10px 15px
}
.b-index_posts_holder{
    padding-right:0
}
.vote{
    height:auto;
    left:auto;
    -moz-user-select:none;
    position:relative;
    top:auto;
    width:auto;
    display:inline-block;
    float:right;
    vertical-align:middle
}
.l-i-content_main{
    background-color:#E5E5E5
}
.post H3{
    display:block;
    font-size:1.5em;
    padding:10px 15px
}
H3 .b-post_snippet_icon{
    margin-right:10px
}
.l-600 .b-post_preview_pic__big,.l-600 .b-post_preview_pic_wrapper,.l-600 .b-post_preview_pic_wrapper .b-video_wrapper,.l-600 .b-video_player{
    height:237px!important;
    width:100%!important
}
.js-post_cut .dd{
    padding:10px 15px;
    font-size:1em;
    border-top:1px solid #F3F3F3
}
.b-post_comments_links{
    display:block;
    padding-top:5px;
    padding-bottom:10px
}
.b-post_my_post_controls{
    float:left
}
.vote_button{
    display:block;
    font-size:1.5em;
    height:20px;
    line-height:17px;
    position:relative;
    width:20px;
    float:left;
    margin:5px
}
.vote_button_plus{
    left:auto;
    top:0;
    float:left
}
.vote_button_minus{
    bottom:0;
    left:auto;
    float:right
}
.vote_result{
    color:#888;
    cursor:hand;
    cursor:pointer;
    display:inline-table;
    font-size:1.2em;
    font-weight:700;
    height:19px;
    -khtml-user-select:none;
    left:0;
    line-height:19px;
    -moz-user-select:none;
    -o-user-select:none;
    position:relative;
    text-align:center;
    top:5px;
    -webkit-user-select:none;
    width:auto;
    float:none;
    margin-left:10px;
    margin-right:10px
}
.b-post_my_post_controls_button .b-button{
    top:0;
    margin:0;
    padding:0
}
.b-post_my_post_controls{
    display:inline-block;
    vertical-align:top
}
.b-post_my_post_controls_button{
    display:inline-block;
    height:25px;
    overflow:hidden;
    vertical-align:middle
}
.b-post_my_post_controls{
    margin-left:0;
    margin-right:6px
}
.b-post_comments_links{
    margin-left:-15px;
    margin-right:-15px;
    padding-left:15px;
    border-bottom:1px solid #F3F3F3;
    margin-bottom:5px
}
.b-post_snippet{
    display:block!important;
    height:auto;
    margin-bottom:-2px;
    margin-left:0;
    opacity:0;
    overflow:hidden;
    position:absolute;
    text-decoration:none;
    visibility:hidden;
    background-color:#ECECEC;
    border-top:1px solid #C2C2C2;
    border-bottom:1px solid #CCC;
    box-shadow:inset 0 0 10px 2px #C7C7C7
}
.b-post_snippet:before{
    border-left:none
}
.dt P{
    margin:0;
    padding:5px 0 10px
}
.b-blog_nav_filter{
    float:none;
    left:auto;
    margin-right:18px;
    top:auto;
    display:inline-block;
    vertical-align:top;
    padding:5px
}
#js-threshold{
    background-color:#FFF;
    display:inline-block;
    width:100%;
    padding:10px
}
.react .b-layout__right {
    display:none;
}
.react .b-layout__left {
    display:none;
}
}
