css:
@-moz-document domain("trello.com") {
/* hide scrollbar */
::-webkit-scrollbar {
	display: none;
}
/*join the cards*/
#header {
	padding: 1px;
}

.list-cards {
	padding: 0 0px;
	margin: 0px 0px;
}

.list-card {
	border-bottom: 1px dashed rgba(0, 0, 0, 0.3);
	margin: 0px 0px;
	border-radius: 0px;
}

.list-card-details {
	padding-top: 0px;
	padding-right: 5px;
	padding-bottom: 1px;
	padding-left: 5px;
}

.list-card-title {
	margin-top: 0px;
	margin-right: 0px;
	margin-bottom: 1px;
	margin-left: 0px;
}

.list-header {
	padding-top: 2px;
	padding-bottom: 2px;
	padding-left: 8px;
}

.board-header {
	padding-top: 2px;
	padding-right: 4px;
	padding-bottom: 2px;
	padding-left: 8px;
}

.open-card-composer {
	padding: 4px;
}

/* show count of cards */
body .list-header-num-cards.hide {
	display: inline-block;
	font-size: 11px;
}

.card-label.mod-card-front {
	max-width: 40px !important;
	margin-bottom: 1px;
}

.card-label {
	border-top-left-radius: 0px;
	border-top-right-radius: 0px;
	border-bottom-right-radius: 3px;
	border-bottom-left-radius: 3px;
}

.card-label.mod-card-front {
	height: 5px;
}

.list-card-title.js-card-name {
	float: left;
}

.list-card-operation {
	padding: 2px !important;
}

.list-card.active-card {
	background-color: #e2e2e2 !important;
}
.card-short-id {
	display: inline !important;
	border: 1px solid #d9d9d9;
	border-bottom-color: #d3d3d3;
	border-radius: 2px;
	color: #737373;
	cursor: default;
	float: left;
	font-size: 11px;
	line-height: 16px;
	height: 16px;
	margin-right: 4px;
	padding: 0 4px;
	position: relative;
	text-decoration: none;
}
}
