css:
@-moz-document domain("docs.sencha.com") {
html, body {
    font-family: 'Open Sans', Helvetica-Neue, Arial, sans-serif;
    color: #e2e2e2;
    background-color: #1e1e1e;
}

.class-body-wrap {
    background-color: #1e1e1e;
}

#tree {
    background: #1e1e1e;
}

.selected-tree-node {
    background-color: #3a3a3a !important;
}

.tree-node {
    color: #e2e2e2;
}

.tree-node a,
.tree-node a:hover {
    color: #e2e2e2;
}

.tree-node:hover {
    background-color: #3a3a3a;
    color: #e2e2e2;
}

.private-class-toggle-ct {
    background: #2d2d2d;
}

.private-class-toggle-ct > label {
    cursor: pointer;
    color: #e2e2e2;
}

#class-tree-ct {
    background: #1e1e1e;
    box-shadow: none;
}

.toggle-all-tree-nodes-btn {
    background-color: #3a3a3a;
}

.sub-tab:hover {
    color: #e2e2e2;
    background-color: #3a3a3a;
}

.search-results-nav-header .active-tab,
.toolbarHeaderButton.active-tab {
    color: #e2e2e2 !important;
    border-bottom: 3px solid #e2e2e2;
    background-color: #1e1e1e;
}

.classMeta {
    color: #e2e2e2;
    background-color: #1e1e1e;
}

#tree-header, .sub-header {
    background-color: #1e1e1e;
}

#toolbar {
    background-color: #1e1e1e;
}

#class-tree-ct, #tree, #rightMembers{
    transition: transform 300ms;
}

.tree-node-indexed .tree-node-icon {
    color: #e2e2e2 !important;
    border: 2px solid transparent;
}

.tree-hidden #tree {
    overflow: hidden;
    right: 0px;
}

.tree-hidden #rightMembers {
    left: 300px;
    transform: translateX(-266px);
    width: 100%;
}

.tree-hidden #class-tree-ct {
    background-color: #1e1e1e;
    left: 0;
    transform: translateX(-266px);
}

.tree-hidden #tree-header {
    background-color: #1e1e1e;
}

.tree-hidden #tree-header i {
    color: #e2e2e2;
}

.search-results-nav-header .active-tab:hover,
.toolbarHeaderButton.active-tab:hover {
    background-color: #1e1e1e;
    border-bottom: 3px solid #e2e2e2;
}

.nav-tab:hover{
    background-color: #1e1e1e;
}

.nav-tab {
    display: inline-block;
    color: #fff;
    font-size: 14px;
    padding: 0 8px 9px;
    margin: 20px 5px 0 0;
}

#rightMembers {
    background-color: #1e1e1e;
    width: calc(100% - 300px);
}

.memberFilter input {
    background-color: transparent;
    border-bottom: 3px solid #3a3a3a !important;
    border-radius: 0;
    box-shadow: none;
    color: #e2e2e2;
}

#member-display {
    padding: 4px 18px;
    background-color: #1e1e1e;
}
input[type="checkbox"] {
    background-color: transparent;
}

code[class*="language-"],
pre[class*="language-"] {
    background: #1a1a1a !important;
    text-shadow: 0 1px #1e1e1e !important;
}
code[class*="language-"],
pre[class*="language-"] {
    color: #e2e2e2;
}
#history-nav {
    background-color: #3a3a3a;
    border-bottom: 1px solid #3a3a3a;
}
.history-title {
    color: #e2e2e2;
}
.history-meta {
    color: #e2e2e2;
}
#member-class-name h1 {
    color: #0a8cc6;
    white-space: nowrap;
    font-weight: 400;
}

.type-sub-category-title {
    color: #e2e2e2;
    background-color: #3a3a3a;
}

.collapse-toggle {
    background-color: #1e1e1e;
    border-right: 1px solid #1e1e1e;
}

.classmembers.member,
.classmembers.member-private,
.classmembers.member-protected {
    border-bottom: 1px solid #1e1e1e;
}

.classmembers.member:after,
.classmembers.member-private:after,
.classmembers.member-protected:after {
    background-color: #1e1e1e;
}

pre.defaults-to-dec {
    border-bottom-style: none;
}

.member-expanded > .collapse-toggle, .member-filter-expanded > .collapse-toggle, .member-temp-expanded > .collapse-toggle {
    background-color: #1e1e1e !important;
}

.member-expanded > .collapse-toggle:hover, .member-filter-expanded > .collapse-toggle:hover, .member-temp-expanded > .collapse-toggle:hover {
    background-color: #1e1e1e !important;
}

.collapse-toggle:hover {
    background-color: #1e1e1e;
}

.members .type {
    border-bottom: 4px solid #1e1e1e;
}
.members {
    border-top: 2px solid #1e1e1e;
}

p code {
    color: #e2e2e2;
    background: #3a3a3a;
    border-color: #3a3a3a;
}

.toolbarButton {
    background-color: #3a3a3a;
    color: #e2e2e2;
}

#memberTypeMenu {
    background-color: #1e1e1e;
    position: fixed;
    border: 2px solid #3a3a3a;
}

.member-menu-col a:hover {
    background-color: #3a3a3a;
}
.da-inline-fiddle-nav {
    background: #3a3a3a;
    border-bottom: 1px solid #3a3a3a;
}

.da-inline-fiddle-nav-code, .da-inline-fiddle-nav-fiddle {
    color: #1e97cc;
    background: #3a3a3a;
    padding: 6px 22px;
    border-style: none;
    box-shadow: 0 0 3px #000;
    margin: 0 0 10px 5px;
    top: -5px;
}

.example-theme-picker {
    background-color: #1e1e1e;
    border-style: none;
    color: #e2e2e2;
}

option:checked{
    background-color: #1e1e1e;
    border-style: none;
    color: #e2e2e2;
}

.accessor-method {
    background-color: #1e1e1e;
    border: 1px solid #1e1e1e;
}

.classmembers .classmembers .collapse-toggle {
    background-color: #1e1e1e;
}

.classmembers .classmembers .collapse-toggle:hover {
    background-color: #1e1e1e;
}

.isGetter::before, .isSetter::before {
    position: absolute;
    color: #e2e2e2;
    background-color: #3a3a3a;
    top: 0;
    left: 28px;
    padding: 1px 9px 2px;
    line-height: 12px;
    font-size: .8em;
    border-radius: 3px;
    border: 2px solid #3a3a3a;
    font-weight: 700;
    letter-spacing: 1px;
}

.member-expanded.isGetter::before, .member-filter-expanded.isGetter::before, .member-filter-expanded.isSetter::before, .member-temp-expanded.isGetter::before, .member-temp-expanded.isSetter::before {
    color: #e2e2e2;
    border-color: #3a3a3a;
}

#classic-search-filter.active, #modern-search-filter.active {
    color: #e2e2e2;
    background-color: #3a3a3a;
}
input#searchtext{
    background-color: #3a3a3a;
    color: #e2e2e2;
}

#search-history-panel {
    background-color: #3a3a3a;
}

.search-history-item {
    padding: 4px 8px;
    cursor: pointer;
    border-bottom: 1px solid #3a3a3a;
}

.search-history-item:hover {
    background-color: #1e1e1e;
}

#search-results-ct > div {
    border: 2px solid #3a3a3a;
    background: #1e1e1e;
}

#search-results-ct {
    border-right: 0px solid #3a3a3a;
}

.search-results-header {
    border-bottom: 1px solid #3a3a3a;
    background-color: #3a3a3a;
    color: #e2e2e2;
}

.guide-search-item:hover, .search-item:hover {
    background-color: #3a3a3a;
    color: #e2e2e2;
}

.guide-search-item:focus, .search-item:focus {
    background-color: #3a3a3a !important;
    outline: 0;
}

.guide-search-item {
    background-color: #1e1e1e;
    border-bottom: 1px solid #1e1e1e;
    color: #e2e2e2;
}
#search-results-ct .search-match, #search-results-ct .search-source {
    color: #e2e2e2;
}
#search-results-ct .search-item {
    border-bottom: 1px solid #1e1e1e;
}
#search-results-ct .search-item-meta-ct.private, #search-results-ct .search-item-meta-ct.protected, #search-results-ct .search-item-meta-ct.public {
    color: #e2e2e2;
    border-right: 0px solid #1e1e1e;
}

#search-results-ct .search-item.classic {
    background-image: none;
}
#search-results-ct .search-item.classic:after {
    content: "C";
    color: #e2e2e2;
    position: absolute;
    right: 8px;
    top: 3px;
}

#search-results-ct .search-item.modern {
    background-image: none;
}

#search-results-ct .search-item.modern:after {
    content: "M";
    color: #e2e2e2;
    position: absolute;
    right: 5px;
    top: 3px;
}

#footer {
    background-color: #3a3a3a;
    border-top: 1px solid #3a3a3a;
}

.guide-body-wrap {
    background-color: #1e1e1e;
}

.toolkit-badge-ct {
    background: #3a3a3a;
}

.is-guide #toggleExamples {
    background: #1e1e1e;
}

.toc {
    background-color: #3a3a3a;
    color: #e2e2e2;
}
.toc a {
    color: #e2e2e2;
}

.since-dec {
    background: #3a3a3a;
    border: 1px solid #3a3a3a;
}

.classmembers.member-expanded .deprecated-dec, .classmembers.member-expanded .preventable-dec, .classmembers.member-expanded .removed-dec, .classmembers.member-expanded .template-dec, .classmembers.member-expanded pre, .classmembers.member-filter-expanded .deprecated-dec, .classmembers.member-filter-expanded .preventable-dec, .classmembers.member-filter-expanded .removed-dec, .classmembers.member-filter-expanded .template-dec, .classmembers.member-filter-expanded pre, .classmembers.member-temp-expanded .deprecated-dec, .classmembers.member-temp-expanded .preventable-dec, .classmembers.member-temp-expanded .removed-dec, .classmembers.member-temp-expanded .template-dec, .classmembers.member-temp-expanded pre {
    background: #3a3a3a;
}

#product-tree-ct {
    background-color: #3a3a3a;
    color: #e2e2e2;
}

.product-name-item {
    color: #e2e2e2;
}

#product-tree-ct .selected-product {
    background-color: #1e1e1e;
}

#product-version-tree-ct {
    background-color: #3a3a3a;
}

.generic-content {
    background-color: #1e1e1e;
}
.homeBubble {
    border: 1px solid #1e1e1e;
}

.home-bubble-badge-ct {
    background: center no-repeat #3a3a3a;
}

#help-wrap {
    background-color: #1e1e1e;
    border: 5px solid #1e1e1e;
}

pre {
    background-color: #3a3a3a !important;
}

.member-highlight-fade{
    background-color: #1e1e1e !important;
}

.member-highlight{
    background-color: #2f2f2f;
    transition: all 400ms;
}

#filters-ct, #related-classes-context-ct {
    background-color: #1e1e1e;
}

.member-types-menu .toolbarButton {
	background: #3a3a3a;
}
.history-type-selector {
    background-color: #3a3a3a;
}
#history-full-list {
    background-color: #3a3a3a;
    border-style: none !important;
}
.history-item {
    display: block;
    border-top: 1px solid #3a3a3a;
}
.members p {
    white-space: normal;
}
}
