css:
@-moz-document domain(spotify.com)
{
@font-face {
  font-family: 'Open Sans';
  font-style: normal;
  font-weight: 400;
  src: local('Open Sans Regular'), local('OpenSans-Regular'), url(https://fonts.gstatic.com/s/opensans/v15/mem8YaGs126MiZpBA-UFVZ0b.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
}

@font-face {
  font-family: 'Fugaz One';
  font-style: normal;
  font-weight: 400;
  src: local('Fugaz One'), local('FugazOne-Regular'), url(https://fonts.gstatic.com/s/fugazone/v7/rax_HiWKp9EAITukFsl8Axhf.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
}

body, input, .navigator-toolbox, .toolbarbutton-text, .sidebar-title{
text-shadow: 0px 0px 1px  !important;
  font-family: 'Open Sans',sans-serif !important;
    

}

/*scrollbar*/

::-webkit-scrollbar {
       transition:0.3s;
    background: #0f0f14; 
		
	 width: 6px;
   
height: 8px;
	
}



::-webkit-scrollbar-track {
       transition:0.3s;
    border-left: 0px solid #0f0f14;


	

}
::-webkit-scrollbar-thumb {

       transition:0.3s;
    background: #b3802b !important;
    -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, 0.27); 
	border-radius: 0px;


	
}
::-webkit-scrollbar-thumb:hover {
        
    background: #b3802b !important;
    filter:brightness(120%);
    -webkit-box-shadow: inset 0 0 0px rgba(0,0,0,0.5); 
	transition: all 0.6s cubic-bezier(0.165, 0.84, 0.44, 1);
    transition:0.1s;
	

}
::-webkit-scrollbar-thumb:window-inactive {
 
       transition:0.3s;
  background: #b3802b !important;
  
    

}


._1YMukI_FrE7QRuUVSvq_Qe, .SearchInputBox{
    
     background: #0f0f14 !important;
    
}

/*searchbar*/

.SearchInputBox__input{
    
    
    color: #b3802b !important;
    
    caret-color: #fff !important;
}

/*play bar*/

.now-playing-bar{
    
    background: #0f0f14 !important;
    
}

.progress-bar__bg{
    
    
    height: 20px !important;
    background: #0b0b0d !important;
    
}

.progress-bar__fg{
 
    background: #b3802b !important;
    border-radius: 0px !important;
    height: 20px !important;
    

}

.connect-bar, .ConnectBar{
      background: #b3802b !important;
    
}

.connect-bar:after, .ConnectBar:after{
    border-bottom: 10px solid #b3802b !important;
    
    
}

.control-button--active, .tracklist-row--active .position, .tracklist-row--active .tracklist-duration, .tracklist-row--active .tracklist-name, .tracklist-row--active .tracklist-play-pause, .navBar-link--active, .navBar-link--active:focus, .navBar-link--active:hover{
    
    color: #b3802b !important;
}

/* main bg*/

#main .root .top-container{
    
    background: #0f0f14 !important;
}

/*nav bar*/
.nav-bar-container{
    
    background: transparent !important;
    
}

.Root__nav-bar{
    
    
    background-color:transparent !important;
    
}

/*.navBar-expand{
    
      font-family: 'Fugaz One',sans-serif !important;
    font-weight: 100 !important;
    
}*/

/*buton*/

.btn.btn-green, .EYVkZXmTImKH4iH48N7Uj._1-aXXBO8bQU1WTFY-7B0m-:before, .control-button--active-dot:after{
    
     background-color: #b3802b !important;
    
}


/*cover*/


.cover-art.shadow{
    
    transform:scale(0.7);
    box-shadow: 0px 0px 100px -30px #b3802b !important;
    border: solid 1px #b3802b !important;
    border-radius: 50%;

    
}

.cover-art .cover-art-image-loaded{
    
    border-radius: 50% !important;
    filter:sepia(50%);
}

.cover-art .cover-art-image-loaded:hover{
     filter:sepia(0%);
 
}

.cover-art .daily-mix-decoration .waves{
      border-radius: 50% !important;
    transform: translateY(-1px) scaleY(1.01);
    background: transparent;
    opacity: 1;
    
}

.daily-mix-decoration{
    filter: sepia(100%) ;
    color:#fff !important;
    
}


.middle-allign, .progress-bar__slider{
    
    border-radius: 0px !important;
    
    background: #2f2f2f !Important;
    height: 20px !Important;
    margin-top: -10px;
     box-shadow: 0px 0px 100px -30px #b3802b !important;
    width: 6px !important;
    margin-right: -10px !important;    

}


}
