css:
@-moz-document domain("expressjs.com") {
body {
	background: hsl(0, 0%, 8%);
	color: hsl(0, 0%, 85%);
}

h1, h2, h3 {
	color: hsl(0, 0%, 100%);
}

header {
	background: hsl(0, 0%, 12%);
	border-bottom-color: hsl(0, 0%, 5%);
}

.scroll header {
	border-bottom-color: hsl(0, 0%, 3%);
	box-shadow: 0 0 4px hsla(0, 0%, 0%, 0.3);
}

#q {
	background: hsl(0, 0%, 5%);
	color: hsl(0, 0%, 100%);
	border: 0;
	margin: 4px 8px;
}

.algolia-autocomplete {
	min-width: initial;
	max-width: initial;
	top: -0.5em;
}

.algolia-autocomplete .ds-dropdown-menu:before,
.algolia-autocomplete .ds-dropdown-menu [class^=ds-dataset-] {
	background: hsl(0, 0%, 10%);
	border-color: hsl(0, 0%, 12%);
}

.algolia-autocomplete .algolia-docsearch-suggestion {
	background: hsl(0, 0%, 10%);
}

.algolia-autocomplete .algolia-docsearch-suggestion--category-header {
	color: hsl(0, 0%, 90%);
	border-color: hsl(0, 0%, 16%);
}

.algolia-autocomplete .algolia-docsearch-suggestion--title {
	color: hsl(0, 0%, 90%);
}

.algolia-autocomplete .algolia-docsearch-suggestion--text {
	color: hsl(0, 0%, 100%);
}

.algolia-autocomplete .algolia-docsearch-suggestion--subcategory-column {
	color: hsl(0, 0%, 85%);
}

.algolia-autocomplete .algolia-docsearch-suggestion--subcategory-column:before,
.algolia-autocomplete .algolia-docsearch-suggestion--content:before {
	background: hsl(0, 0%, 16%);
}

.algolia-autocomplete .algolia-docsearch-suggestion--highlight {
	color: hsl(0, 0%, 100%);
	background: hsl(0, 0%, 25%);
	padding: 0.05em 0.3em;
}

.algolia-autocomplete .algolia-docsearch-suggestion--category-header .algolia-docsearch-suggestion--category-header-lvl0 .algolia-docsearch-suggestion--highlight, .algolia-autocomplete .algolia-docsearch-suggestion--category-header .algolia-docsearch-suggestion--category-header-lvl1 .algolia-docsearch-suggestion--highlight, .algolia-autocomplete .algolia-docsearch-suggestion--text .algolia-docsearch-suggestion--highlight {
	box-shadow: inset 0 -2px 0 0 hsla(212, 80%, 60%, 0.8);
}

strong, th {
	color: hsl(0, 0%, 92%);
}

p code,
td code,
li code {
	color: hsl(207, 60%, 75%);
	background: hsl(0, 0%, 14%);
	border: 1px solid hsl(0, 0%, 20%);
	border-radius: 2px;
	padding: 0 3px 1px;
}

/* li code {
	color: hsl(207, 60%, 75%);
} */

p em {
	color: hsl(0, 0%, 100%);
}

pre {
	border-color: hsl(0, 0%, 22%);
	color: hsl(0, 0%, 80%);
}

:not(pre) > code[class *= language-],
pre[class *= language-],
.doc-box pre[class*=language-] {
	background: hsl(0, 0%, 20%);
}

code[class*=language-],
pre[class*=language-] {
	color: hsl(0, 0%, 90%);
	text-shadow: 0 1px hsl(0, 0%, 0%);
}

#logo .express {
	color: hsl(0, 0%, 100%);
}

#navbar a {
	color: hsl(207, 80%, 75%);
}
#navbar a.active {
	color: hsl(0, 0%, 100%);
}
.current {
	background: hsl(199, 0%, 30%);
}

.menu ul.dropit-submenu {
	background: hsl(0, 0%, 14%);
	border-color: hsl(0, 0%, 5%);
}

#menu ul a {
	color: hsl(0, 0%, 65%);
}

#menu > li > a {
	color: hsl(0, 0%, 80%);
}

#menu em {
	color: hsl(0, 0%, 72%);
}

#description .express a {
	color: hsl(0, 0%, 100%);
}

#install-command {
	background: hsl(0, 0%, 18%);
	border-color: hsl(0, 0%, 4%);
}

#announcements {
	background: hsl(0, 0%, 18%);
	border: 2px solid hsl(42, 68%, 65%);
}

.doctable, section table {
	border-color: hsl(0, 0%, 8%);
}

.doctable tr th,
section table tr th {
	background: hsl(0, 0%, 15%);
}

.doctable td,
.doctable th,
section table td,
section table th {
	border-color: hsl(0, 0%, 20%);
}



.doc-info,
.doc-notice,
.doc-warn {
	background: hsl(0, 0%, 5%);
	border-width: 2px;
}

.doc-info {
	border-color: hsl(0, 0%, 12%);
}

.doc-notice {
	border-color: hsl(60, 30%, 40%);
}

.doc-warn {
	border-color: hsl(30, 30%, 40%);
}



.token.atrule,
.token.attr-value,
.token.keyword {
	color: hsl(198, 80%, 50%);
}

.token.operator,
.token.entity,
.token.url,
.language-css .token.string,
.style .token.string {
	color: hsl(30, 60%, 62%);
	background: none;
}

.token.function {
	color: hsl(348, 80%, 65%);
}

.token.selector,
.token.attr-name,
.token.string,
.token.char,
.token.builtin,
.token.inserted {
	color: hsl(90, 55%, 50%);
}

.token.comment,
.token.prolog,
.token.doctype,
.token.cdata {
	color: hsl(210, 20%, 70%);
}

.token.property,
.token.tag,
.token.boolean,
.token.number,
.token.constant,
.token.symbol,
.token.deleted {
	color: hsl(280, 70%, 70%);
}

.token.punctuation {
	color: hsl(300, 10%, 85%);
}

.token.regex,
.token.important,
.token.variable {
	    color: hsl(39, 68%, 55%);
}



#mw-fig-img,
[src="/images/clustering.png"],
[src^="https://nodei.co/npm"] {
	filter: invert(92%) hue-rotate(180deg);
}


#api-doc > h3 {
	color: hsl(0, 0%, 87%);
}

#api-doc h5 {
	color: hsl(0, 0%, 84%);
}
}

@-moz-document regexp("companies\\-using\\-express.html") {
#page-doc img {
	filter: invert(92%) hue-rotate(180deg);
}
}
