css:
@-moz-document domain('to-do.microsoft.com') {
.sidebar {background: #1d1b1e;}

.o365sx-navbar { 
background-color: #1d1b1e;}
.o365sx-waffle {
background-color: #1d1b1e;
}
.o365sx-button {
background-color: #1d1b1e;}
html[dir=ltr] #sidebar {
border-right: 0px;
background-color: #1d1b1e;}
.listItem .listItem-title {
color: #e3d9e9;}
.todayToolbar-item.active {
background: #041ac4 !important;
}
.active .todayToolbar-title {
font-weight: 600;
color: #efefef;
}
.listItem .listItem-count {
color: #a3a3a6;}
.listItem.active {
background: #f8f8f8 !important;
}
.todayToolbar-title {
color: #e3d9e9;}
#main {background-color: #1d1b1e;
    color: rgb(255, 239, 253);
}
.listTitle {
color: #fff;}
.button{color: #fff;}
.chunkedComponentList .chunkedScrollContainer {background-color: #1d1b1e;}
.taskItem-titleWrapper {
color: #ececec;}
.selected .taskItem-titleWrapper {
color: #393939;}
.taskItem:hover .taskItem-body {
background-color: #000000;
-webkit-box-shadow: inset 0 0 0 1px #f5f5f5;
box-shadow: inset 0 0 0 1px #353535;}
.button:hover {
color: #141414;}
.addTask {background-color: #1d1b1e;}
#main .main-background {background-color: #1d1b1e;}
.backgroundLines {
display: none;}
.listItem:hover .listItem-title {
color: #29272a;
}
.todayToolbar-item.active .todayToolbar-count {
color: rgb(253, 247, 255);}
body::-webkit-scrollbar-track {
    background-color: #000;
}
.taskItem.selected .taskItem-body {
background: #000000;}
.sidebar-content::-webkit-scrollbar-thumb {
    background: #000; 
    overflow: auto;
}
#detail .detail-inner {
position: relative;
background: #1d1b1e;}
#main.is-suggestions {
background-color: #1d1b1e;
}
.taskCard {background-color: #171717;}
}
