css:
@-moz-document domain(10fastfingers.com) {

#inputfield, #words {
    border-radius: 10px !important;
    font-family: monospace !important;
    z-index: 10000000 !important;
    
}

#words {
    border-radius: 10px !important;
}

body {
    font-family: 'Montserrat', sans-serif !important;
    background-color: black !important;
    background: none;
    
}

#speedtest-main #words {
    margin-left: auto !important;
    margin-right: auto !important;
}

.main-layout #main-content, .main-layout .main-content {
    background-color: #515151;
}

@media (min-width: 1200px) {
.col-lg-4 {
    width: 32%;
}
}

body * {
    box-shadow: none;
}

h2 {
    color: orange;
}

span {
    font-family: monospace;
}

#row1:nth-child(2) {
    color: red !important;
}

.panel-success>.panel-heading {
    background-color: #e09100;
}

.panel-body {
    background-color: #ffb939
}

#table-data td {
    background-color: #515151;
}

.highlight {
    border: 2px solid #ffa500 !important;
    border-radius: 7px !important;
    background-color: #f7ead1de !important;
    box-shadow: 3px 3px;
    opacity: 1;
}

a.list-group-item.active {
    background-color: #d98d01;
    border: 1px solid #000;
    border-right: none;
    
    
}
a.list-group-item.active:hover {
    background-color: #be7b00;
    
}

a.list-group-item {
    background-color: black;
    border: 1px solid #272727;
}

#badge-box {
    background-color: #f1c169;
}

#auswertungbox {
    
}

#words {
    background-color: #f7cb7b !important;
    border: 3px solid dimgray !important;
}

#inputfield {
    border: 3px solid darkorange !important;
    box-shadow: none;
    background: #ffe2ae;
}

#input-row {
    background-color: #333 !important;;
    
}

#participation {
    background-color: orange;
}

#words {
    height: 310px !important;
}

#activity-module div.list-group-item {
    background-color: black !important;
    border: 1px solid #272727;
}

.top-first-bg {
    background: none !important;
    background-color: #d37102 !important;
}

#auswertung-result {
    font-family: monospace;
    font-size: 1px !important;
    border: 4px solid black;
    box-shadow: none !important;
    border-radius: 5px !important;
    background-color: #d79620 !important;
    margin-left: auto !important;
    margin-right: auto !important;
    position: relative !important;
    width: min-content !important
    
}

.ads {
    display: none;
}

.value {
    font-size: 13px !important;
    
}

.name {
    background-color: orange;
}

.top-first-bg {
    border-bottom: 10px solid #d98d00 !important;
}

.value {
    background-color: darkorange !important;
}

#table-data {
    background-color: #202020 !important;
    color: orange;
    border: 5px solid black;
    border-collapse: collapse !important;
    
    
}

#content-bg {
    background: none;
    background-color: orange !important;
}

#main-content-trenner {
    background: none;
    background-color: #c88200 !important;
}

@media (min-width: 992px) {
    .col-md-6 {
    width: 49%;
    margin-left: 5px;
    }
}

td {
    background-color: orange;
}

.nav-tabs {
    border-bottom: 1px solid #ff812d;
}

.nav-tabs>li.active>a, .nav-tabs>li.active>a:hover, .nav-tabs>li.active>a:focus {
    background-color: orange;
    border: 1px solid #ffa500;
}

.nav-tabs>li>a {
    background-color: #d97700;
}

table#profile-data-table  {
    background-color: darkorange;
}

table * {
    border-color: black !important;
}

#wpm {
    background-color: #f7cb7b;
}

.table-striped>tbody>tr:nth-child(odd)>td, .table-striped>tbody>tr:nth-child(odd)>th {
    background-color: orange;
}

#speedtest-main #auswertung-box #auswertung-result h3 {
    background-color: #d79620 !important;
    background: none;
}

#speedtest-main #auswertung-box #auswertung-result #result-table td#wpm #top-score div, #speedtest-main #auswertung-box #auswertung-result #result-table td#wpm #top-score {
    display: none;
}

#timer, .glyphicon-refresh:before, #speedtest-main #reload-btn {
    color: orange !important;
    background-color: #272727 !important;
    border: none;
}

.glyphicon-refresh:before {
    background-color: #f000 !important;
}

#speedtest-main #reload-btn {
    background-color: gray !important;
}

#speedtest-main #reload-btn:hover {
    background-color: #5e5e5e !important;
}

}
