css:
@-moz-document url-prefix("https://minehut.com/") {


.toolbar-heading.mat-toolbar.mat-toolbar-single-row {
    height: 6%;
    background: #1A9ADF;
}

div[_ngcontent-c32] {
    background: white;
}

span.mat-content, .mat-expansion-panel-body, .mat-expansion-header, .mat-action-row {
    background: white;
}

body {
    background: #F0F0F1;
}

h4 {
    position: relative;
    top: 7.5%;
    left: 12.5%;
    color: black;
}

p, h1, h2, h3, .pricing-title, .pricing-value, .ng-pristine.ng-valid.ng-touched, .mat-radio-label-content, .mat-input-infix.mat-form-field-infix, .mat-paginator-page-size-label, .mat-paginator-icon, .mat-select-value, .mat-card-title, .mat-toolbar.mat-primary.mat-toolbar-single-row {
    color: black;
}

textarea.ng-pristine.ng-valid.ng-touched {
    color: #F0F0F1;
}

.link.ng-star-inserted, .cls-1 {
    color: white;
}

.mat-toolbar.mat-toolbar-multiple-rows, .pricing-item-selected, .mat-toolbar.mat-primary.mat-toolbar-single-row, mat-dialog-container.ng-tns-c33-168.ng-trigger.ng-trigger-slideDialog.ng-star-inserted {
    background: white;
}

.balance.text-center, .mat-menu-content.ng-trigger.ng-trigger-fadeInItems, .mat-menu-item, .grey-text.mat-button {
    color: black;
    background: #F0F0F1;
}

.pricing-table {
    background: #F0F0F1;
}

.nav-container {
    background: #262729;
    position: fixed;
    height: 100%;
    top: 6%;
    left: 0%;
}

.mat-dialog-container.ng-tns-c25-87.ng-trigger.ng-trigger-slideDialog.ng-star-inserted {
    color: black;
    background: white;
}


.mat-paginator-container {
    color: black;
    background: #F0F0F1;
}

.mat-card {
    border: 0.5px solid gray;
    background: white;
}

.mat-line {
    position: relative;
    left: 1%;
}
}
