css:
@-moz-document domain("bbs.meyet.net") {
	/*列表*/
	.tl table {
		border-collapse: separate;
		background-color: rgba(222, 185, 135, 0.54) !important;
	}
	/*楼层*/
	body,
	input,
	button,
	select,
	textarea {
		font-size: 14px !important;
	}
	#wp {
		background-color: rgba(184, 165, 238, 0.68) !important;
	}
	.box {
		background-color: rgba(194, 170, 210, 0.14) !important;
	}
	/*正文背景图片，背景颜色，字体颜色*/
	.bm {
		background: url(https://pic2.me/images/grey.png);
	}
	.plc {
		background-color: rgba(165, 84, 15, 0.18) !important;
	}
	font {
		color: black !important;
		font-size: 18px !important;
	}
	/*回复字体颜色大小*/
	.bm {
		color: rgba(7, 29, 123, 0.74) !important;
	}
	.xs1 {
		font-size: 15px !important;
	}
	/*左侧背景颜色*/
	.pls {
		background: rgba(225, 210, 174, 0.73) !important;
	}
	/*楼层分界线*/
	.authi {
		border-bottom: 1px, dashed, grey;
		border-top: 1px, solid, white;
	}
	.ad {
		background: #573b3b !important;
		padding: 0;
		overflow: hidden;
	}
}
