css:
@-moz-document domain("inet.se") {
/*header*/
.top-nav-bar {
    background-color: #333;
    border-bottom: 1px solid #666;
}
.form-control {

    background-color: #444;
    background-image: none;
    border: 1px solid #ccc;
    border-radius: 0;
    box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
    height: 29px;
    padding: 6px 12px;
    transition: border-color .15s ease-in-out,box-shadow .15s ease-in-out;
    width: 100%;
}
.search-dropdown .products {
    background: #333;
    list-style: none;
    margin: 0;
    padding: 0;
}
.search-dropdown .products .product:nth-child(2n+1),
.search-dropdown .show-more {
    background: #444;
}
.search-dropdown:not(.mobile) {

    background: #333;
    color: #000;
    z-index: 15;
    border: 1px solid #666;
        border-top-color: #666;
        border-top-style: solid;
        border-top-width: 1px;
    border-top: none;
    box-shadow: 4px 6px 15px rgba(0, 0, 0, .25);
    left: -1px;
    max-height: calc(100vh - 123px);
    overflow-y: hidden;
    padding-bottom: 0;
    position: absolute;
    right: 69px;
    top: 29px;

}


/*body*/
body {
    background-color: #111;
    color: #aaa;
    font-family: Open Sans,Helvetica Neue,Helvetica,Arial,sans-serif;
    font-size: 11px;
}
.btn-default {
    background-color: #555;
    border-color: #000;
    color: #000;
}
.btn.disabled, [disabled].btn, fieldset [disabled].btn {
    background-color: #222;
    border-color: #f00;
    color: #f00;
}
.dropdown-menu {
    color: #aaa;
    background-color: #333;
    border: 1px solid #666;
    border-radius: 0;
    box-shadow: 0 6px 12px rgba(0,0,0,.175);
}
.dropdown-menu > li > a {
    color: #aaa;
}


/*clearfix tree-wrapper*/
a,
a > span,
h1,
h2,
h3,
.btn-link
.product-reviews .panel-body > footer,
.search-dropdown .products .product .price,
.price,
.qty-string,
.product-list > ul > li > div,
.product-page .product-description,
.product-page .long-description,
.breadcrumb>li+li:before {
    color: #aaa;
}
.inet-navbar,
.tree-wrapper {
    background-color: #333;
}
.well {
    background-color: #333;
    border: 1px solid #666;
}
.breadcrumb {
    background-color: #333;
    border: 1px solid #666;
}


/*main colum*/
.product-gallery .carousel-gallery {
    border-color: #666;
    background-color: #fff;
    opacity: 0.8;
}
.front-page-carousel .carousel-labels > li.active {
    background-color: hsla(0, 0%, 0%, .8);
    color: #aaa;
}
.front-page-carousel .carousel-labels > li {
    background-color: hsla(0, 0%, 25%, .5);
    border-right: 1px solid #666;
    color: #aaa;
}
.nav.nav-tabs+.tab-content,
.specifications li:nth-child(odd),
.product-list>ul>li:nth-child(odd),
.inet-striped-list .list-row:nth-child(odd),
.inet-striped-table tbody tr:nth-child(2n),
.specifications h2 {
    background-color: #333;
}
.specifications li:nth-child(even),
.product-list>ul>li:nth-child(even) {
    background-color: #444;
}
.filters {
    color: #aaa;
    background: #333;
    border: 1px solid #666;
}
.select .dropdown,
.select .dropdown ul {
    border: #666;
    background: #333;
    color: #aaa;
}
.select header {
    color: #aaa;
    background: #333;
    border: 1px solid #666;
}
.select .dropdown li {

    background: #666;
}
.nav-tabs>li.active>a,
.nav-tabs>li.active>a:focus,
.nav-tabs>li.active>a:hover {
    color: #aaa;
    background-color: #333;
}
.nav-tabs>li>a,
.nav-tabs>li>a:focus,
.nav-tabs>li>a:hover,
.nav-tabs>li.disabled>a,
.nav-tabs>li.disabled>a:focus,
.nav-tabs>li.disabled>a:hover {
    color: #aaa;
    background-color: #444;
}
.nav-tabs > li.active > a,
.nav-tabs > li.active > a:focus,
.nav-tabs > li.active > a:hover,
.nav-tabs > li > a,
.nav-tabs > li > a:focus,
.nav-tabs > li > a:hover {
    color: #aaa;
    border: solid #666;
    border-bottom-color: #666;
}


/*right side*/
.input-group .form-control,
select,
::placeholder,
.product-page .stock-box,
.category-top-list .review .body > span,
.category-top-list .review h2,
.product-page .purchase {
    color: #aaa !important;
    border-color: #666 !important;
    background-color: #333 !important;
}
.category-top-list > .nav-tabs > li.active > a {
    background: #d10000;
    border: 1px solid #d10000;
    color: #000;
}
.category-top-list > .nav-tabs > li > a {
    background: #333;
    border: 1px solid #666;
        border-top-color: #666;
        border-right-color: #d10000;
        border-bottom-color: #666;
        border-left-color: #d10000;
    border-color: #666 #666 #d10000;
}
.nav + .tab-content {
    border: 1px solid #666;
    position: relative;
}
.category-top-list .review .body .show-more {
    background: #555;
    border-color: #000;
    color: #000;
}


/*Reviews*/
.panel {
    background-color: #333;
    border: 1px solid #666;
    border-radius: 0;
    margin-bottom: 15px;
}
.modal-content {
    background-color: #333;
    border: 1px solid #666;
    border-radius: 0;
    box-shadow: 0 3px 9px rgba(0,0,0,.5);
}
.review-modal > article {
    border-bottom: 1px solid #666;
    padding: 15px;
}
.review-modal > article.focused {
    border: 1px solid #d10000;
}
.product-reviews .review .body .show-more {
    background: #555;
    border-color: #000;
    color: #000;
}
.panel-default > .panel-heading {
    background-color: #333;
    border-color: #666;
    color: #aaa;
}
.btn-primary {
    background: #555;
    border-color: #000;
    color: #000;
}


/*left side*/
#category-tree ol.lvl1 {
    border-color: #666;
}
#category-tree ol>li.open>.tree-item>button,
.contact-container a {
    color: #aaa;
}
#category-tree ol.lvl1>li>.tree-item {
    border-color: #666;
    background-color: #333;
}
#category-tree ol.lvl2>li>.tree-item {
    border-color: #666;
    background-color: #555;
}
#category-tree ol.lvl3,
#category-tree ol {
    border-color: #666;
    background-color: #666;
}
.contact-container {
    -webkit-align-items: center;
    -webkit-flex-direction: column;
    align-items: center;
    background-color: #333;
    border: 1px solid #666;
    display: -webkit-flex;
    display: flex;
    flex-direction: column;
    font-size: 1.3em;
    padding: 20px 25px;
    position: relative;
}

/*Min Sida*/
.my-pages ul.nav > li.active a {
    background-color: #555;
    border-bottom: none;
}
.my-pages ul.nav > li a {
    background-color: #444;
    border-bottom: 0px solid #cdcdcd;
    border-right: 1px solid #666;
    padding: 10px 0;
}
.order-list > li.header {
    background: #444;
    font-weight: 700;
}
.order-list > li {
    border: 1px solid #666;
}
.order-list > li .products {
    background: #444;
    border-top: 0px solid #eaeaea;
}
.inet-striped-list .list-header {
    background: #444;
    border-bottom: 0px solid #cdcdcd;
}
.inet-striped-list {
    border: 1px solid #666;
    list-style: none;
    margin: 0;
    padding: 0;
}
.inet-striped-table thead tr th {
    background: #444;
    border-bottom: 1px solid #666;
}
.inet-striped-table {
    border: 1px solid #666;
}


/*Kassa*/
.panel-danger > .panel-heading {
    background-color: #222;
    border-color: #666;
    color: #d10000;
}
.bg-danger {
    background-color: #444;
}
.checkout .shipping-methods > div.active {
    background-color: #555;
    border: 1px solid #d10000;
    color: #aaa;
    font-weight: 700;
}
.checkout .shipping-methods > div.disabled {
    -webkit-filter: grayscale(1);
    background-color: #333;
    border: 0px solid #666;
    color: #999;
    filter: grayscale(1);
}
.checkout .shipping-methods select > option {
    color: #aaa;
}
.checkout .nav > li.active {
    border: 0px solid #666;
    border-bottom: none;
}
.checkout .nav > li.active > a {
    background-color: #555;
    border: none;
}
.checkout .nav > li {
    border-bottom: 0px solid #666;
}
.checkout .nav > li > a {
    background-color: #444;
    border-bottom: 0px solid #666;
    border-right: 1px solid #666;
    border-top: 0px solid #666;
    padding: 10px 0;
}
.checkout .nav + .tab-content {

    border: 0px solid #666;
    border-top: none;
    padding: 12px;
}


/*footer*/
.inet-footer {
    background-color: #333;
    border-top: 1px solid #666;
}

}
