css:
@-moz-document domain("4chan.org"), domain("4channel.org") {
/* (stylish)[applies to 4chan.org && 4channel.org] original-4chan-halloween-css <v11-nov24-2018> by twily-aka-analie */
html, body { background: none !important; background-color: #140814 !important; color: #96b4aa !important;  }
.quoteLink, .deadlink, a, a.visited, blockquote > .quotelink, blockquote > .deadlink, span > a, blockquote > span > .quotelink, span > a.visited, div > a, div > a.visited, .button, .dd-menu li, .backlink span a.quotelink, #arc-list .quotelink, s .sjis, a.replylink:not(:hover), span a.replylink:not(:hover), div#absbot a:not(:hover) { color: #d98518 !important; text-shadow: 0 0 8px #d98518 !important; }
div.postContainer div.post div.postInfo span.postNum a:hover,.posteruid .hand:hover, .backlink span a.quotelink:hover, a:hover ,.button:hover,.dd-menu li:hover, #arc-list .quotelink:hover,s .sjis:hover, blockquote > .quotelink:hover, span > a:hover, span > a.visited:hover, div > a:hover, div > a.visited:hover { color: #ff9d1c !important; transition-duration: 1s !important; transition-property: color !important; }
.globalMessage { color: red !important; }
.ws div.boardBanner { color: #5995b3 !important; text-shadow: 0 0 5px rgba(89,149,179,.75) !important; }
hr { color: transparent !important; background: transparent !important; border: 0 !important; height: 10px !important; }
select { background-color: #241023 !important; border: 1px solid rgba(150,180,170,.3) !important; color: #96b4aa !important; }
#postForm tr > td > .desktop, #ctrl, .navLinks, .postNum.desktop > span { color: #140814; }
#boardNavDesktop, #boardNavDesktopFoot, #togglePostFormLink { color: #140814 !important; }
.navLinks.desktop > *, .postblock, td > .rules { color: #96b4aa; }
div.post div.postInfo span.subject { color: #5995b3 !important; }
div.post div.postInfo span.nameBlock span.postertrip, div.post div.postInfo span.nameBlock span.name, #qrHeader { color: #d98518 !important; }
.panel, div.reply, table .highlightPost:not(.op) { background: #241023 !important; border: 0 !important; }
div .reply:target, div .reply.highlight { background-color: #331433 !important; border: 0 !important; }
blockquote > span.quote { color: #789922 !important; }
.panel, .extPanel, #disclaimer-dialog { box-shadow: 0 0 6px #000 !important; }
#disclaimer-dialog, #fp-menu, #entries, #form { background: #241023 !important; color: #96b4aa !important; }
#fp-menu ul li:hover { background: #d98518; color: #000 !important; }
#quickReply input[type="text"]:focus, #quickReply textarea:focus, input[type="text"]:focus, input[type="password"]:focus, input:not([type]):focus, textarea:focus, .extPanel #qrForm textarea:focus { border: 1px solid #96b4aa !important; }
#quickReply textarea, .ctrl-wrap, div#absbot, .pln { color: #96b4aa !important; }
#quickReply input[type="text"], #quickReply textarea, #quickReply #recaptcha_response_field, #quote-preview, .postblock, #qrForm textarea, .extPanel input[type="text"], .extPanel textarea { background-color: #241023 !important; border: 1px solid rgba(150,180,170,.3) !important; }
.prettyprint { background: #140814 !important; border: 1px solid #333 !important; }
.str, .atv { color: #4E9A06 !important; }
.kwd, .tag { color: #3465A4 !important; }
.com, .fun { color: #CC0000 !important; }
.typ, .atn, .dec, .var { color: #75507B !important; }
.lit { color: #06989A !important; }
.pun,.opn,.clo { color: #C4A000 !important; }
.dd-menu ul { background-color: #241023 !important; border: 1px solid #333 !important; }
.dd-menu li { border: 0 !important; }
.dd-menu li:hover, header { background: none !important; }
header, #content article.post, #content article.post .post-meta, #sidebar { background: none !important; background-color: #140814 !important; }
.boxcontent dd { border: 0 !important; }
dt { color: #060 !important; }
div.sideArrows, #postForm tr > td > .desktop, #ctrl-top, .navLinks.desktop, .postNum.desktop > span, .open-qr-wrap { color: #140814 !important; }
.middlead a img, #bannerCnt { opacity: .5 !important; }
input[type="checkbox"] { opacity: .25 !important; }
table.flashListing tr:nth-of-type(2n+1) { background: transparent !important; }
input[type="text"], input[type="password"], table.postForm > tbody textarea, textarea, #recaptcha_response_field { background-color: #241023 !important; color: #96b4aa !important; }
div.pagelist { background: #140814 !important; color: #140814 !important; border: 0 !important; }
.top-box, #announce, #ft li, #ft ul, .left-box, .right-box, .boxcontent, .content, footer li, footer { background: transparent !important; color: #96b4aa !important; border: 0 !important; }
.boxbar, .boxbar *, #entries th, #form h2 { background: #241023 !important; color: red !important; text-shadow: 0 0 0 #f00 !important; }
.boxbar:hover, .boxbar *:hover, #sidebar h1, #sidebar h2, #sidebar h3, #sidebar h4, #sidebar h5, #sidebar h6 { color: red !important; }
.adg-rects, .adg-rects.desktop, .GS { display: none !important; }
#qrCaptchaContainer { opacity: .75 !important; }
body::after { content: ''; width: 500px; height: 500px; background: url('https://twily.info/randomskeleton.php') transparent no-repeat bottom right; position: fixed; bottom: 0; right: 0; pointer-events: none; }
}
