css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("friendfeed.com") {

/*
By Aliha
http://Aliha.ir/
friendfeed.com/aliha
*/

body, input, textarea, td, select, option {
font-family: Tahoma !important;
font-size: 9pt !important;
}

#main {
background-color: #fdfdfd !important;
}

.feed .cluster .icon {
float: right !important;
margin-top: 6px !important;
margin-right: 1px !important;
}

.feed .hiddenentries, .feed .cluster .body, .feed .togglerelated, .feed .related {
margin-right: 23px !important;
margin-top: 10px !important;
padding: 5px !important;
background-color: #f9f9f9 !important;
border: 1px solid #f2f2f2 !important;
-moz-border-radius: 5px !important;
-khtml-border-radius: 5px !important;
-webkit-border-radius: 5px !important;
border-radius: 5px !important;
}

#mainmainsizer {
width: 70%;
}

.cluster {
padding: 10px 0 20px 0 !important;
-moz-border-radius: 5px !important;
-khtml-border-radius: 5px !important;
-webkit-border-radius: 5px !important;
border-radius: 5px !important;
}

#main .body {
direction: rtl !important;
}

.togglerelated {
direction: ltr !important;
}

.feed .entry .title a, .title {
text-decoration: none !important;
font: 10pt Tahoma !important;
}

.expandcomment {
direction: ltr !important;
text-align: right !important;
margin-right: 25px !important;
}

.comment {
margin-right: 18px !important;
line-height: 145%;
padding: 5px; !important;
}

.l_editcomment:before {
content: 'ÙˆÛŒØ±Ø§ÛŒØ´ '
}

.l_deletecomment:before {
content: 'Ù¾Ø§Ú© Ú©Ø±Ø¯Ù† '
}

.info {
direction: ltr !important;
margin-left: 15px !important;
}

.feed .entry .likes {
padding-bottom: 5px !important;
direction: ltr !important;
}

.quote {
left: 98.5% !important;
}

.servicefilter {
direction: ltr !important;
}

.graybox .t {
direction: ltr !important;
}

.sharebox textarea.sharetitle {
direction: rtl !important;
}

.sharebox input.comment {
direction: rtl !important;
}

.feedprofile td.picture, .feedprofile td.content {
direction: ltr !important;
}

.feedprofile h1 a:link, .feedprofile h1 a:visited, .feedprofile h1 a:hover, .feedprofile h1 a:active {
color: #2f72c4 !important;
text-decoration: none !important;
}

.feedprofile h1 {
direction: rtl !important;
text-align: left !important;
}

.subscribebuttons .button {
padding-left: 8px !important;
direction: ltr !important;
}

.profilebox .discussion {
margin-bottom: 5pt !important;
direction: ltr !important;
}

.profilebox .discussion .synopsis {
font-size: 8pt !important;
margin-left: 18px !important;
direction: ltr !important;
}

#popup .c {
direction: ltr !important;
text-align: left !important;
}

.popup .headline, .popup .services {
direction: ltr !important;
text-align: left !important;
}

}
