css:
@-moz-document domain("tvprofil.com") {
/* v1.1 by jamshy */

/*remove elements */
div[class="slider slider-promo container"]{ display:none  }

div[class="col-12 col-sm-6 col-lg-3 tvbox no-padding-xs ad_placeholder"]{ display:none  }

div[class="row light"]{ display:none  }

.time {
    border-right: 4px solid #E9E9E9;
}


.tvbox>div.channel-shows>div {
    min-height: 25px;
    font-size: 0;
    position: relative;
}

.tvbox {
    min-height: 430px;
    line-height: 1rem;
    overflow: hidden;
    box-shadow: 0 -3px 3px #FFF;
}

.light {
    color: #17181E;
    background-color: #1D1E29;
}


.col-sm-6, .col-lg-3, .tvbox {
    position: relative;
    width: 100%;
    padding-right: 5px;
    padding-left: 5px;
}

.tvbox>div.channel-shows {
    width: calc(100% - 10px);
    background-color: #FFF;
    position: absolute;
    top: 32px;
    left: 5px;
}
}

@-moz-document url-prefix("https://tvprofil.com/tvprogram") {
/*remove elements */
div[class="col-lg-2 col-md-3 col-sm-4 banner-container"]{ display:none  }


#schedule-table .time {
    display: inherit;
    margin-right: 10px;
}

.tip-1, .tip-2, .tip-3 {
    padding: 4px 40px 2px 8px;
    margin-left: 8px;
}

body {

    line-height: 1;

}

#program-list {

    min-width: 830px;
}

.schedule-day {
    position: fixed;
    width: 830.5px;
    z-index: 51;
    transition: all 0.3s;
    top: 50px;
}
}

@-moz-document url-prefix("https://tvprofil.com/sport/") {
div[class="row no-gutters"]{ display:none  }

div[class="col-md-3"]{ display:none  }


.col-md-9 {
    flex: 0 0 100%;
    max-width: 100%;
}

#schedule-table .row>div {
    padding: 4px 6px;
    line-height: 1.2rem;
    display: inherit;
}

#schedule-table .time {
    max-width: 5rem;
    white-space: nowrap;
    text-align: center;
}

#schedule-table .kanal-date {
    max-width: 7rem;
    text-align: center;
}

.ai a, .ai a:visited, .ai .fka {
    margin-right: 8px;
}
}
