// ==UserScript==
// @name Facebook pink
// @namespace http://userstyles.org
// @description Hello Kitty version of facebook
// @author max berggren
// @homepage http://userstyles.org/styles/3582
// @include http://facebook.com/*
// @include https://facebook.com/*
// @include http://*.facebook.com/*
// @include https://*.facebook.com/*
// ==/UserScript==
(function() {
var css = "/* ta bort reklam och fixa sÃ¥ att applications alltid syns */ #ad_0, #ad_1, #ad_2, #ad_3, #ad_4, #ad_5, #ad_6, #ad_7, #ad_8, #ad_9, #ssponsor, .advert, .ad_body, .ad_capsule, .ad_media, .comments_ad_image, .sponsors, .sponsor_absolute { display:none !important; } /* Remove the unnecessary New Stuff and Next Step sidebar items from the Facebook Home page. */ #new_stuff_content, .nextstep { display:none !important; } #more_section { display:none !important; } #expandable_more { display:block !important; } #more_link { display:none !important; } div#ssponsor { display:none !important; } /* ####################################################### */ /* gÃ¶ra toppsakerna svarta */ #sidebar a.go_home { background:#3B5998 url(http://img530.imageshack.us/img530/7225/facebohellokittyco0.gif) no-repeat scroll left top !important; } #navigator { background:transparent url(http://img530.imageshack.us/img530/4642/facebookvakgrundio2ju6.gif) repeat scroll 0% !important; } #navigator .main_set li a.edit_link { color:#FFFFFF !important; } #navigator .main_set li a.global_menu_arrow, #navigator .main_set li a.global_menu_arrow_active { background:transparent url(http://img172.imageshack.us/img172/70/globalmenuarrowjb3dn2.gif) no-repeat scroll 0px center !important; } #navigator .secondary_set li a { color:#FFFFFF !important; } /* #navigator .main_set li a.active, #navigator .main_set li a:hover { background:#4a4a4a none repeat scroll 0% !important; } */ #navigator .main_set li a.active, #navigator .main_set li a:hover { background:#e97db0 none repeat scroll 0% !important; } h2 a { color: #6d2746 !important; } #sidebar .divider_bar { display: none !important; } #app_non_nav_list { margin-top: -10px !important; } a { color: #87375a !important;} #navigator a { color:white !important; text-decoration:none; }";
if (typeof GM_addStyle != "undefined") {
GM_addStyle(css);
} else if (typeof addStyle != "undefined") {
addStyle(css);
} else {
var heads = document.getElementsByTagName("head");
if (heads.length > 0) {
var node = document.createElement("style");
node.type = "text/css";
node.appendChild(document.createTextNode(css));
heads[0].appendChild(node);
}
}
})();

css:
@-moz-document domain("facebook.com") {





/* ta bort reklam och fixa sÃ¥ att applications alltid syns */



#ad_0, #ad_1, #ad_2, #ad_3, #ad_4, 

#ad_5, #ad_6, #ad_7, #ad_8, #ad_9,

#ssponsor,

.advert,

.ad_body,

.ad_capsule,

.ad_media,

.comments_ad_image,

.sponsors,

.sponsor_absolute { display:none !important; }



/* Remove the unnecessary New Stuff and Next Step

   sidebar items from the Facebook Home page. */

#new_stuff_content,

.nextstep { display:none !important; }

#more_section { display:none !important; }

#expandable_more { display:block !important; }

#more_link { display:none !important; }

div#ssponsor { display:none !important; }



/* ####################################################### */ 















/* gÃ¶ra toppsakerna svarta */



#sidebar a.go_home {





background:#3B5998 url(http://img530.imageshack.us/img530/7225/facebohellokittyco0.gif) no-repeat scroll left top !important;







}













#navigator {

background:transparent url(http://img530.imageshack.us/img530/4642/facebookvakgrundio2ju6.gif) repeat scroll 0% !important;



}





#navigator .main_set li a.edit_link {

color:#FFFFFF !important;



}









#navigator .main_set li a.global_menu_arrow, #navigator .main_set li a.global_menu_arrow_active {



background:transparent url(http://img172.imageshack.us/img172/70/globalmenuarrowjb3dn2.gif) no-repeat scroll 0px center !important;



}





#navigator .secondary_set li a {

color:#FFFFFF !important;

}



/*

#navigator .main_set li a.active, #navigator .main_set li a:hover {

background:#4a4a4a none repeat scroll 0% !important;

}

*/





#navigator .main_set li a.active, #navigator .main_set li a:hover {

background:#e97db0 none repeat scroll 0% !important;

}







h2 a {



color: #6d2746 !important;

}



























#sidebar .divider_bar {

display: none !important;

}



#app_non_nav_list { margin-top: -10px !important; }













a { color: #87375a !important;}



#navigator a {

color:white !important;

text-decoration:none;

}







}
