css:
@-moz-document domain("dota2.net") {
    body {
    background: #282b2f;
    color: #E0E0E0;
}

.sortby {
    background: #373b3e;
}
.sortby span {
    color: #E0E0E0;
}
.sortby > .right {
    background-color: #373b3e;
}
.market-left .hint {
    color: #534c4c;
}
.menu-left {
    background: #373b3e;
}
.chat {
    background: #282b2f;
    color: #E0E0E0;
}
.chat-form__textarea textarea {
    background-color: #373b3e;
    color: #E0E0E0;
}
.chosen-container-active .chosen-choices {
    border-color: #5181b8;
    background: #626466;
    color: #f7f5f5;
}
.chosen-container-multi .chosen-choices {
    background: #626466;
    border: 2px solid #5181b8;
    color: #f7f5f5;
    box-shadow: inset 0 1px 5px rgba(0, 0, 0, 0.5);
}
.chosen-container .chosen-drop {
    background: #626466;
    border: 2px solid #5181b8;
    color: #f7f5f5;
    text-shadow: 1px 1px 2px black;
}
.chosen-container-single .chosen-single {
    background: #626466;
    border: 2px solid #5181b8;
    color: #f7f5f5;
    box-shadow: inset 0 1px 5px rgba(0, 0, 0, 0.5);
}
.chosen-container .chosen-results {
    color: #f7f5f5;
}
.chosen-container-active.chosen-with-drop .chosen-single {
    background: #626466;
    border: 2px solid #5181b8;
    box-shadow: inset 0 1px 5px rgba(0, 0, 0, 0.5);
}
.search-field {
    color: #f7f5f5;
}
input[type="text"],
input[type="password"],
input[type="phone"],
textarea,
.inputlike {
    background: #626466;
    border: 2px solid #5181b8;
    box-shadow: inset 0 1px 5px rgba(0, 0, 0, 0.5);
    color: #f7f5f5;
}
.fixheightblock.toggler .toggleblock {
    border-color: #79a3d3;
    background: #414447;
    color: #f7f5f5;
    box-shadow: inset 0 1px 5px rgba(0, 0, 0, 0.5)
}
.fixheightblock.toggler header {
    background: #373b3e;
    color: black;
    border: 2px solid #3b5e86;
}
.fixheightblock.toggler span {
    color: #E0E0E0;
}
.fixheightblock.toggler b {
    color: #E0E0E0;
}
[class^="notice-"] {
    color: #c03030;
}
[class^="notice-"].notice-4days {
    color: #E0E0E0;
}
.onoffswitcher-label {
    background: #7696b7;
}
.warning-message {
    background: #9e9e9e54;
}
.marketuseful,
.blueicon {
    background: #373b3e;
    color: #E0E0E0;
}
.market-important .usm {
    background: #373b3e;
    color: #E0E0E0;
}
.headline > .right {
    background: #373b3e;
    color: #E0E0E0;
}
.headline.backpack-items {
    background: #373b3e;
    color: #E0E0E0;
}
.marketsup {
    background: #373b3e;
    color: #E0E0E0;
    border: 2px solid #3b5e86;
}

.marketsup-head {
    color: #E0E0E0;
}
.headline.history-items {
    background: #373b3e;
    color: #E0E0E0;
}
.chosen-container-multi .chosen-choices li.search-field input[type="text"] {
    color: #f7f5f5;
}
.market-items .item .imageblock {
    border: 1px solid #3c8eec;
}
.headline {
    background: #373b3e;
    color: #E0E0E0;
}
.howitworks-term {
    background: #373b3e;
    color: #E0E0E0;
}
.howitworks-term:nth-child(2n) {
    background: #5b5c5c;
    color: #E0E0E0;
}
.how-steps {
    background: #5b5c5c;
    border: 2px solid #3b5e86;
}
#new-ticket-container {
    background: #5b5c5c;
    border: 2px solid #3b5e86;
}
#new-ticket-wrapper {
    color: black;
    border: 2px solid #3b5e86;
}
#new-ticket-wrapper textarea{
    color: white;
}
.ticket-head-main {
    background: #5b5c5c;
    border: 2px solid #3b5e86;
}

.ticket-head-last {
    background: #5b5c5c;
    border: 2px solid #3b5e86;
}
header.menu {
    border-bottom: 2px solid #5181b8;
}
.outsumm {
    background: #5b5c5c;
    border: 2px solid #3b5e86;
}
.outsumm-result {
    background: #717575;
    border: 2px solid #fdfdfd;
}
.confirmation-captcha,
.confirmation-argee,
.confirmation-password {
    background: #5b5c5c;
    border: 2px solid #3b5e86;
}
input[type="password"] {
    background: #fdfdfd;
    color: black;
}
.table-list tr:nth-child(even) td {
    background: #6c7073;
    border: 2px solid #000;
}
.table-list td {
    background: #50739c;
    border: 2px solid #000;
}
.cm-link.cm-link-checkin,
.cm-link.cm-link-checkout,
.cm-link.cm-link-checklog {
    background: #5b5c5c;
    border: 2px solid #3b5e86;
    color: #fdfdfd
}
.commonmenu {
    background: #838484;
}
.table-list th {
    background: #6c7073;
    color: black;
    text-align: center;
    border: 2px solid #000;
}
.ui-widget.ui-widget-content {
    background: #5b5c5c;
    border: 2px solid #3b5e86;
}
.myitem.myinventory {
    border: 2px solid #0b5d8b;
}
.myitem.myinventory .itemname {
    background: #0b5d8b;
    color: white;
}
.ui-draggable .ui-dialog-titlebar {
    background: #8f9699;
}
.market-items .item .name {
    background: #0b5d8b;
    color: white;
}
.pagination.pagination-top {
    border-bottom: 4px solid #0b5d8b;
}
.commonlink {
    color: #7dc9f5;
}
.market-items .item.hot .name {
    color: black;
}
.grid-inner {
    border: 4px solid #3c8eec;
}
img {
    border-radius: 2%;
    border: 1px solid #3c8eec;
    box-shadow: 0 0 2px 1px;
}
}
