css:
/* i really want this to be global */
@namespace url(http://www.w3.org/1999/xhtml);

.docs-title-save-label {
    color: #afafaf;
}

.docs-material #docs-header .docs-titlebar-buttons {
    align-items: center;
    background-color: #202020;
}

.docs-vertical-ruler .docs-ruler-face {
    width: 15px;
    background: #0d0d0d;
}
div#kix-horizontal-ruler {
    background: #0d0d0d;
    border-color: black;
}

.docs-horizontal-ruler .docs-ruler-face {
    width: 15px;
    background: #0d0d0d;
}
.docs-material .goog-toolbar-button,
.docs-material .goog-toolbar-menu-button {
    color: rgba(0, 0, 0, 0.89);
    text-rendering: optimizeLegibility;
    font-weight: 600;
    font-size: 13px;
}
.docs-material .goog-toolbar-combo-button-input {
    color: rgb(0, 0, 0);
}

#kix-vertical-ruler {
    background: #202020;
    border-color: black;
}
.docs-material #docs-docos-commentsbutton .docs-icon {
    filter: invert(1)brightness(10);
}

.docs-material .goog-toolbar-button,
.docs-material .goog-toolbar-combo-button,
.docs-material .goog-toolbar-menu-button {
    background-color: #e0e0e0;
}
input.docs-omnibox-input.jfk-textinput {
    border: 1px solid #313131;
    background: #171717;
    border-top: 1px solid #313131;
}
#docs-chrome {
    background: #333 none repeat scroll 0% 0% !important;
    color: #DDD !important;
}

#gb#gb a.gb_b,
/* Account email in top right corner */
.docs-presence-plus-widget-status/* x total viewers text */
{
    color: #DDD !important;
}

/* Document title box */
.docs-title-input {
    background-color: #2B2B2C !important;
    border-radius: 2px !important;
    border: 1px solid #171717 !important;
    border-top-color: #0f0f0f !important;
    box-shadow: 0 1px rgba(255, 255, 255, 0.15),
    inset 0 1px rgba(0, 0, 0, 0.1) !important;
    font-style: normal !important;
}
.docs-title-input:focus {
    color: white !important;
    background-color: #2B2B2C !important;
    border: 1px solid #171717 !important;
    border-top-color: #0f0f0f !important;
}
.docs-title-untitled {
    display: none !important;
}

/* Fix for menu bar text when hover */
.goog-control-hover {
    color: black !important;
}

/* Main toolbar */
#docs-toolbar-wrapper {
    border-top: 1px solid #151516 !important;
    border-bottom: 1px solid #040404 !important;
    background-image: linear-gradient(#404143, #333436) !important;
    box-shadow: inset 0 1px rgba(255, 255, 255, 0.15) !important;
}

/* Toolbar's vertical dividers */
.goog-toolbar-separator.goog-inline-block {
    border-left: 1px solid #222 !important;
}

/* A very simple/lazy fix to get toolbar item colors to look good on a dark background */
.goog-toolbar-button,
.goog-toolbar-toggle-button,
.goog-toolbar-menu-button:not([id="textColorButton"]),
.goog-toolbar-combo-button,
.docs-icon-folder-solid {
    -webkit-filter: invert(1) !important;
    filter: invert(1) !important;
}

/* Fixes for suggestion mode when editing own doc, viewing mode when editing others' docs */
#docs-toolbar-mode-switcher,
#docs-access-level-indicator {
    background-color: transparent !important;
    color: black !important;
}
.docs-icon-mode-review-white,
.docs-icon-mode-view-white {
    filter: invert(1) !important;
}

/* Background behind the document */
#docs-editor {
    background: #333 none repeat scroll 0% 0% !important;
}

/* Document drop-shadow removal */
.kix-page-paginated {
    box-shadow: none !important;
}
#docs-chrome {
    background: #202020 none repeat scroll 0% 0% !important;
}
