css:
@-moz-document url-prefix("https://web.skype.com/en/") {
/* Hide footer */
.swx .shell.footer {
    display: none !important;
}

:root .swx .swxMe {
    background: #265491ed !important;
}


/* TWEAKS TO THE SIDEBAR AREA */
.search .input {
    background: #2a2a2a;
}

:root .swx .search .input.active input {
    color: #f9f9f9ed;
}


/* Background color of text entered in search box */
:root .swx .search .input input {
    background-color: transparent;
    border-style: solid;
    border-color: transparent;
    border-width: 2px 0px;
    color: #f6f6f7;
}

:root .swx .search .splitter {
    display: block;
    background-color: #50eceb;
    height: 1px;
    margin: .25em 1em .25em 1em;
}


/* Background color of Chat Container */
.shell.main .chatContainer {
    background: #272726ed;
}

.side .timeline {
    background: #272726ed;
}

.swx .search.beforeMenuItems {
    background: #272726ed;
}

.Me-sky {
    box-shadow: 0 0 0 7px #2a2a2a, 0 0 0 14px #2a2a2a;
    background-color: #2a2a2a;
}

.Me-sky {
    -webkit-border-radius: 50%;
    border-radius: 50%;
    background-color: #fff;
    box-shadow: 0 0 0 7px #2a2a2a, 0 0 0 14px #2a2a2a;
    height: 65px;
    left: 8px;
    width: 65px;
    position: absolute;
    top: 22px;
}

.swx .Me-skyContainer {
    background-color: #2a2a2a;
}

.Me-displayNameContainer {
    background-color: #2a2a2a;
}


/* Background color of Input box */
.Me-info {
    color: #eee;
}

.Avatar {
    background-color: #2a2a2a;
}

.swx .ContactsPage {
    background-color: #272726ed;
}
:root .swx .ContactsPage-header {
    background-color: #272726ed;
}

:root .swx .ContactsPage-heading {
    background-color: #272726ed;
}

:root .swx .ContactsPage-categories {
    background-color: #272726ed;
}

:root .swx .ContactsPage-hint {
    color: #ffffff;
    background-color: #272726c2;
}

:root .swx .ContactsPage-body .peoplePicker {
    color: #e4ec01;
    background-color: #272726c2;
}
:root .swx .ContactsPage-categoryItem .MenuItem-link {
    color: #eee;
}



/* Color of Mood (Online, Away, etc.) */
.Me-moodMessage,
:root .swx.desktop.dark .Me-moodMessage-moodMessage,
:root .swx.desktop.dark .Me-displayBalance {
    color: #70f5ec;
}

/*
.swx .side .searchItem .message {
       color: #eee;
}
*/

/* Text color of recent topics */
.swx .side .recent .topic {
    color: #eee;
}


/* Text color of status of recent topics with contacts */
.swx .side .timeline .history swx-recent-item .text .message {
    color: #e4ec01;
    font-style: italic;
}

/*TWEAKS TO THE CHAT AREA */

/* Allow message bubbles to be full size */

/* Background color of YOUR chat responses */
.chat .conversation .message.me .bubble {
    width: 100%;
    color: #ffffff;
    background: #0fa3f3f5 !important;
}

.chat .conversation .message.me .bubble .content {
    /*width: 100%;*/
    background: #0fa3f3f5 !important;
}

:root .swx .chat .conversation .message.me .bubble .content p {
    /*width: 100%;*/
    background: #0fa3f3f5 !important;
}
:root .swx .message.me .DeliveryStatus-status {
    display: inline-block;
    position: relative;
    color: #e5f90a;
    margin-right: .15em;
}

.message.me .DeliveryStatus-status {
    display: inline-block;
    position: relative;
    color: #e5f90a;
    margin-right: .15em;
}

:root .swx .chat .conversation .message .bubble .timestamp {
    color: #e5f90a;
}



/* Background color of THEIR chat responses */
.chat .conversation .message.their .bubble {
    width: 100%;
    background: #26915aed !important;
}

.chat .conversation .message.their .bubble .content {
    /*width: 100%;*/
    background: #26915aed !important;
    color: #ffffff;
}

:root .swx.desktop.dark .chat .conversation .message.showTimestamp .timestamp p {
    color: #e5f90a;
}


/* Background color of Conversation Header */
.swx .chat .conversationHeader {
    background: #272726ed !important;
}


/* Background color of Input box */
.swx .chat .input {
    background: #2a2a2a !important;
}


/* Changes color of the presence marker; the circle next to the avatar
.swx .Me-presenceStatus {
    background: #26915aed !important;
}
*/

/*Background color of chat window */
.conversationHistory {
    background-color: #2a2a2a;
}


/*Background color of menu icons */
.side .navigation.afterSearch .navigationMenuIcons {
    background-color: #2a2a2a;
}


.side .navigation.afterSearch {
    background-color: #2a2a2a;
}

.hyperscrollV {
    background-color: #2a2a2a;
}
}
